import React, { useState, useEffect } from 'react';
import { createTraining, getCoachTrainings, getTrainingParticipants, updateTraining } from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/CoachDashboard.css';

// Визначення категорій спорту та відповідних секцій
const sportCategories = {
  "Ігри з м'ячем": ["Футбол", "Баскетбол", "Бадмінтон", "Волейбол", "Бейсбол", "Регбі", "Хокей"],
  "Бойові мистецтва": ["Карате", "Бокс", "Ушу", "Джиу-джитсу", "ММА"],
  "Легка атлетика": ["Біг", "Стрибки", "Метання"],
  "Водні види спорту": ["Плавання", "Вітрильний спорт", "Каноїзм"],
  "Екстремальні види спорту": ["Скейбординг", "Сноубординг", "Альпінізм"],
  "Фітнес та тренажерний зал": ["Фітнес", "Силові тренування", "Йога"]
};

const CoachDashboard = () => {
  const [trainingData, setTrainingData] = useState({
    date: '',
    start_time: '',
    end_time: '',
    price: '',
    spots: '',
    currency: 'UAH',
    category: '',
    section: ''
  });
  const [errors, setErrors] = useState({});
  const [modalMessage, setModalMessage] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [trainings, setTrainings] = useState([]);

  // Стан для модального вікна деталей тренування
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);

  // Стан для редагування тренування
  const [editMode, setEditMode] = useState(false);
  const [editData, setEditData] = useState({
    date: '',
    start_time: '',
    end_time: '',
    price: '',
    spots: '',
    currency: '',
    category: '',
    section: ''
  });

  const today = new Date().toISOString().split('T')[0];

  const getMinTime = () => {
    if (trainingData.date === today) {
      const now = new Date();
      let hours = now.getHours();
      let minutes = now.getMinutes();
      hours = hours < 10 ? `0${hours}` : hours;
      minutes = minutes < 10 ? `0${minutes}` : minutes;
      return `${hours}:${minutes}`;
    }
    return "00:00";
  };

  const formatTime = (timeString) => {
    const date = new Date(timeString);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const groupTrainingsByDate = (trainings) => {
    const groups = {};
    trainings.forEach((training) => {
      const dateObj = new Date(training.start_time);
      const formattedDate = dateObj.toLocaleDateString('uk-UA', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      });
      if (!groups[formattedDate]) {
        groups[formattedDate] = [];
      }
      groups[formattedDate].push(training);
    });
    for (const date in groups) {
      groups[date].sort((a, b) => new Date(a.start_time) - new Date(b.start_time));
    }
    return groups;
  };

  const handleChange = (e) => {
    setTrainingData({ ...trainingData, [e.target.name]: e.target.value });
  };

  // Функція для зміни категорії: при виборі категорії оновлюється список секцій
  const handleCategoryChange = (e) => {
    const newCategory = e.target.value;
    setTrainingData({
      ...trainingData,
      category: newCategory,
      section: '' // скидаємо попередній вибір секції
    });
  };

  const fetchTrainings = async () => {
    const coachId = localStorage.getItem('userId');
    if (coachId) {
      try {
        const data = await getCoachTrainings(coachId);
        setTrainings(data.trainings);
      } catch (error) {
        console.error("Помилка отримання тренувань:", error.message);
      }
    }
  };

  useEffect(() => {
    fetchTrainings();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});
    const coachId = localStorage.getItem('userId');
    if (!coachId) {
      setErrors({ general: 'Не знайдено даних про тренера. Будь ласка, увійдіть повторно.' });
      return;
    }
    if (!trainingData.date || !trainingData.start_time || !trainingData.end_time) {
      setErrors({ general: 'Будь ласка, оберіть дату та час проведення тренування.' });
      return;
    }
    if (!trainingData.category || !trainingData.section) {
      setErrors({ general: 'Будь ласка, оберіть категорію та секцію тренування.' });
      return;
    }
    if (trainingData.start_time >= trainingData.end_time) {
      setErrors({ end_time: "Час завершення не може бути раніше часу початку" });
      return;
    }
    const startDateTime = trainingData.date + "T" + trainingData.start_time;
    const endDateTime = trainingData.date + "T" + trainingData.end_time;
    const startDT = new Date(startDateTime);
    const now = new Date();
    if (startDT < now) {
      setErrors({ general: "Неможливо створити тренування для минулої дати або часу" });
      return;
    }
    const payload = {
      coach_id: coachId,
      start_time: startDateTime,
      end_time: endDateTime,
      price: parseFloat(trainingData.price),
      spots: parseInt(trainingData.spots, 10),
      currency: trainingData.currency,
      category: trainingData.category,
      section: trainingData.section
    };
    try {
      await createTraining(payload);
      setModalMessage('Тренування успішно створено!');
      setShowModal(true);
      setTrainingData({
        date: '',
        start_time: '',
        end_time: '',
        price: '',
        spots: '',
        currency: 'UAH',
        category: '',
        section: ''
      });
      fetchTrainings();
    } catch (err) {
      setErrors({ general: err.message || "Сталася помилка" });
    }
  };

  const handleModalClose = () => setShowModal(false);

  // Перегляд учасників тренування (відкриття модального вікна деталей)
  const handleViewParticipants = async (training) => {
    setSelectedTraining(training);
    try {
      const data = await getTrainingParticipants(training._id);
      setParticipants(data.participants);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch (error) {
      console.error("Помилка отримання учасників:", error.message);
    }
  };

  const closeParticipantsModal = () => {
    setShowParticipantsModal(false);
    setParticipants([]);
    setSelectedTraining(null);
    setEditMode(false);
  };

  // Режим редагування – заповнюємо форму редагування значеннями тренування
  const handleEdit = () => {
    if (selectedTraining) {
      const start = new Date(selectedTraining.start_time);
      const end = new Date(selectedTraining.end_time);
      const formattedDate = start.toISOString().split('T')[0];
      const formattedStartTime = start.toTimeString().split(' ')[0].slice(0, 5);
      const formattedEndTime = end.toTimeString().split(' ')[0].slice(0, 5);
      const signedCount = selectedTraining.participants ? selectedTraining.participants.length : 0;
      const currentTotal = parseInt(selectedTraining.spots) + signedCount;
      setEditData({
        date: formattedDate,
        start_time: formattedStartTime,
        end_time: formattedEndTime,
        price: selectedTraining.price,
        spots: currentTotal,
        currency: selectedTraining.currency,
        category: selectedTraining.category,
        section: selectedTraining.section
      });
      setEditMode(true);
    }
  };

  const handleEditChange = (e) => {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  };

  // Підтвердження редагування тренування
  const handleEditSubmit = async (e) => {
    e.preventDefault();
    if (!editData.date || !editData.start_time || !editData.end_time) {
      setErrors({ edit: 'Будь ласка, оберіть дату та час проведення тренування.' });
      return;
    }
    if (editData.start_time >= editData.end_time) {
      setErrors({ edit: "Час завершення не може бути раніше часу початку" });
      return;
    }
    const newStart = editData.date + "T" + editData.start_time;
    const newEnd = editData.date + "T" + editData.end_time;
    const startDT = new Date(newStart);
    const now = new Date();
    if (startDT < now) {
      setErrors({ edit: "Неможливо встановити тренування в минулому" });
      return;
    }
    const signedCount = selectedTraining.participants ? selectedTraining.participants.length : 0;
    const currentTotal = parseInt(selectedTraining.spots) + signedCount;
    const newTotal = parseInt(editData.spots);
    const updatePayload = {
      start_time: newStart,
      end_time: newEnd,
      price: parseFloat(editData.price),
      currency: editData.currency,
      category: editData.category,
      section: editData.section
    };
    if (newTotal !== currentTotal) {
      if (newTotal < signedCount) {
        setErrors({ edit: "Нова загальна місткість не може бути меншою за кількість уже записаних учасників" });
        return;
      }
      updatePayload.spots = newTotal - signedCount;
    }
    try {
      await updateTraining(selectedTraining._id, updatePayload);
      setModalMessage('Тренування успішно оновлено!');
      setShowModal(true);
      setEditMode(false);
      fetchTrainings();
      closeParticipantsModal();
    } catch (err) {
      setErrors({ edit: err.message || "Сталася помилка при оновленні" });
    }
  };

  const cancelEdit = () => {
    setEditMode(false);
  };

  const groupedTrainings = groupTrainingsByDate(trainings);
  const sortedDates = Object.keys(groupedTrainings).sort((a, b) => {
    const [dayA, monthA, yearA] = a.split('.');
    const [dayB, monthB, yearB] = b.split('.');
    return new Date(yearA, monthA - 1, dayA) - new Date(yearB, monthB - 1, dayB);
  });

  return (
    <div className="dashboard-container">
      <h1>Кабінет Тренера</h1>

      <div className="form-wrapper">
        <h2>Створення тренування</h2>
        <p>Заповніть форму нижче, щоб створити нове тренування.</p>
        {errors.general && <p className="error-message">{errors.general}</p>}
        <form onSubmit={handleSubmit} className="create-training-form">
          <div className="form-row">
            <label htmlFor="date">Дата проведення:</label>
            <input
              type="date"
              name="date"
              id="date"
              value={trainingData.date}
              onChange={handleChange}
              min={today}
              required
            />
          </div>
          <div className="form-row time-group">
            <div className="time-field">
              <label htmlFor="start_time">Початок:</label>
              <input
                type="time"
                name="start_time"
                id="start_time"
                value={trainingData.start_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
            </div>
            <div className="time-field">
              <label htmlFor="end_time">Завершення:</label>
              <input
                type="time"
                name="end_time"
                id="end_time"
                value={trainingData.end_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
              {errors.end_time && <p className="error-message">{errors.end_time}</p>}
            </div>
          </div>
          {/* Новий блок вибору категорії */}
          <div className="form-row">
            <label htmlFor="category">Категорія:</label>
            <select
              name="category"
              id="category"
              value={trainingData.category}
              onChange={handleCategoryChange}
              required
            >
              <option value="">-- Оберіть категорію --</option>
              {Object.keys(sportCategories).map((cat) => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          {/* Блок вибору секції залежно від обраної категорії */}
          <div className="form-row">
            <label htmlFor="section">Секція:</label>
            <select
              name="section"
              id="section"
              value={trainingData.section}
              onChange={handleChange}
              required
              disabled={!trainingData.category}
            >
              <option value="">-- Оберіть секцію --</option>
              {trainingData.category &&
                sportCategories[trainingData.category].map((sect) => (
                  <option key={sect} value={sect}>{sect}</option>
                ))
              }
            </select>
          </div>
          <div className="form-row price-currency-group">
            <div className="price-group">
              <label htmlFor="price">Ціна:</label>
              <input
                type="number"
                step="0.01"
                min="0"
                name="price"
                id="price"
                placeholder="Введіть ціну"
                value={trainingData.price}
                onChange={handleChange}
                required
              />
              {errors.price && <p className="error-message">{errors.price}</p>}
            </div>
            <div className="currency-group">
              <label htmlFor="currency">Валюта:</label>
              <select
                name="currency"
                id="currency"
                value={trainingData.currency}
                onChange={handleChange}
                required
              >
                <option value="UAH">UAH</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="spots">Доступні місця:</label>
            <input
              type="number"
              min="0"
              name="spots"
              id="spots"
              placeholder="Введіть кількість місць"
              value={trainingData.spots}
              onChange={handleChange}
              required
            />
            {errors.spots && <p className="error-message">{errors.spots}</p>}
          </div>
          <button type="submit" className="submit-btn">Створити тренування</button>
        </form>
      </div>

      {/* Секція "Ваші тренування" */}
      <div className="trainings-wrapper">
        <h2>Ваші тренування</h2>
        {sortedDates.length === 0 ? (
          <p>Немає створених тренувань.</p>
        ) : (
          sortedDates.map((date) => (
            <div key={date} className="training-group">
              <h3 className="training-date-header">{date}</h3>
              <ul className="trainings-list">
                {groupedTrainings[date].map((t) => {
                  const startTime = formatTime(t.start_time);
                  const endTime = formatTime(t.end_time);
                  const signedCount = t.participants ? t.participants.length : 0;
                  const totalCapacity = signedCount + t.spots;
                  return (
                    <li key={t._id} className="training-item">
                      {startTime} - {endTime}, записані учасники: {signedCount}\{totalCapacity}
                      <button className="view-btn" onClick={() => handleViewParticipants(t)}>
                        Переглянути
                      </button>
                    </li>
                  );
                })}
              </ul>
            </div>
          ))
        )}
      </div>

      {/* Модальне вікно для перегляду/редагування тренування */}
      {showParticipantsModal && selectedTraining && (
        <Modal
          modalType="info"
          onClose={closeParticipantsModal}
          message={
            <>
              {!editMode ? (
                <>
                  <h2>Детальна інформація про тренування</h2>
                  <p><strong>Секція:</strong> {selectedTraining.section}</p>
                  <p>
                    <strong>Дата:</strong> {new Date(selectedTraining.start_time).toLocaleDateString('uk-UA')}
                  </p>
                  <p>
                    <strong>Час:</strong> {formatTime(selectedTraining.start_time)} - {formatTime(selectedTraining.end_time)}
                  </p>
                  <p>
                    <strong>Записані учасники:</strong> {selectedTraining.participants ? selectedTraining.participants.length : 0}\{ parseInt(selectedTraining.spots) + (selectedTraining.participants ? selectedTraining.participants.length : 0)}
                  </p>
                  <p>
                    <strong>Зароблена сума:</strong> { (selectedTraining.participants ? selectedTraining.participants.length : 0) * selectedTraining.price} {selectedTraining.currency}
                  </p>
                  <hr />
                  <h3>Список учасників:</h3>
                  {participants.length === 0 ? (
                    <p>Ніхто ще не записався.</p>
                  ) : (
                    <ul>
                      {participants.map((p, index) => (
                        <li key={index}>{p.name} (вік: {p.age})</li>
                      ))}
                    </ul>
                  )}
                  <button className="view-btn" onClick={handleEdit}>
                    Редагувати
                  </button>
                </>
              ) : (
                <>
                  <h2>Редагування тренування</h2>
                  <form onSubmit={handleEditSubmit} className="edit-training-form">
                    <div className="form-row">
                      <label htmlFor="editDate">Дата:</label>
                      <input
                        type="date"
                        name="date"
                        id="editDate"
                        value={editData.date}
                        onChange={handleEditChange}
                        required
                      />
                    </div>
                    <div className="form-row time-group">
                      <div className="time-field">
                        <label htmlFor="editStart">Початок:</label>
                        <input
                          type="time"
                          name="start_time"
                          id="editStart"
                          value={editData.start_time}
                          onChange={handleEditChange}
                          required
                        />
                      </div>
                      <div className="time-field">
                        <label htmlFor="editEnd">Завершення:</label>
                        <input
                          type="time"
                          name="end_time"
                          id="editEnd"
                          value={editData.end_time}
                          onChange={handleEditChange}
                          required
                        />
                      </div>
                    </div>
                    <div className="form-row">
                      <label htmlFor="editPrice">Ціна:</label>
                      <input
                        type="number"
                        step="0.01"
                        min="0"
                        name="price"
                        id="editPrice"
                        value={editData.price}
                        onChange={handleEditChange}
                        required
                      />
                    </div>
                    <div className="form-row">
                      <label htmlFor="editSpots">Загальна місткість:</label>
                      <input
                        type="number"
                        min="0"
                        name="spots"
                        id="editSpots"
                        value={editData.spots}
                        onChange={handleEditChange}
                        required
                      />
                    </div>
                    <div className="form-row">
                      <label htmlFor="editCurrency">Валюта:</label>
                      <select
                        name="currency"
                        id="editCurrency"
                        value={editData.currency}
                        onChange={handleEditChange}
                        required
                      >
                        <option value="UAH">UAH</option>
                        <option value="EUR">EUR</option>
                        <option value="USD">USD</option>
                      </select>
                    </div>
                    <div className="form-row">
                      <label htmlFor="editCategory">Категорія:</label>
                      <select
                        name="category"
                        id="editCategory"
                        value={editData.category}
                        onChange={handleEditChange}
                        required
                      >
                        <option value="">-- Оберіть категорію --</option>
                        {Object.keys(sportCategories).map((cat) => (
                          <option key={cat} value={cat}>{cat}</option>
                        ))}
                      </select>
                    </div>
                    <div className="form-row">
                      <label htmlFor="editSection">Секція:</label>
                      <select
                        name="section"
                        id="editSection"
                        value={editData.section}
                        onChange={handleEditChange}
                        required
                        disabled={!editData.category}
                      >
                        <option value="">-- Оберіть секцію --</option>
                        {editData.category &&
                          sportCategories[editData.category].map((sect) => (
                            <option key={sect} value={sect}>{sect}</option>
                          ))
                        }
                      </select>
                    </div>
                    {errors.edit && <p className="error-message">{errors.edit}</p>}
                    <button type="button" className="submit-btn cancel-btn" onClick={cancelEdit}>
                      Відхилити
                    </button>
                    <button type="submit" className="submit-btn save-btn">
                      ОК
                    </button>
                  </form>
                </>
              )}
            </>
          }
        />
      )}

      {showModal && <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />}
    </div>
  );
};

export default CoachDashboard;
