const API_BASE_URL = 'http://localhost:8000';

export const registerUser = async (payload) => {
  const res = await fetch(`${API_BASE_URL}/register`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  });
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || 'Помилка при реєстрації');
  }
  return res.json();
};

export const loginUser = async (credentials) => {
  const res = await fetch(`${API_BASE_URL}/login`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(credentials)
  });
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || 'Помилка авторизації');
  }
  return res.json();
};

export const createTraining = async (trainingData) => {
  const res = await fetch(`${API_BASE_URL}/create-training`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(trainingData)
  });
  if (!res.ok) {
    const errorData = await res.json();
    let errorMsg = '';
    if (typeof errorData.detail === 'string') {
      errorMsg = errorData.detail;
    } else {
      errorMsg = 'Помилка створення тренування';
    }
    throw new Error(errorMsg);
  }
  return res.json();
};

export const getTrainings = async (coachEmail) => {
  const res = await fetch(`${API_BASE_URL}/coach/trainings?email=${encodeURIComponent(coachEmail)}`);
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || 'Помилка отримання тренувань');
  }
  return res.json(); // повертає { trainings: [...] }
};
