import React, { useState, useEffect } from 'react';
import { getTrainings, getAthleteTrainings, signupForTraining } from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/AthleteDashboard.css';

// Категорії спорту та секції
const sportCategories = {
  "Ігри з м'ячем": ["Футбол", "Баскетбол", "Бадмінтон", "Волейбол", "Бейсбол", "Регбі", "Хокей"],
  "Бойові мистецтва": ["Карате", "Бокс", "Ушу", "Джиу-джитсу", "ММА"],
  "Легка атлетика": ["Біг", "Стрибки", "Метання"],
  "Водні види спорту": ["Плавання", "Вітрильний спорт", "Каноїзм"],
  "Екстремальні види спорту": ["Скейбординг", "Сноубординг", "Альпінізм"],
  "Фітнес та тренажерний зал": ["Фітнес", "Силові тренування", "Йога"]
};

const AthleteDashboard = () => {
  const [allTrainings, setAllTrainings] = useState([]);
  const [registeredTrainings, setRegisteredTrainings] = useState([]);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  // Стан фільтрації та сортування
  const [filterCategory, setFilterCategory] = useState('');
  const [filterSection, setFilterSection] = useState('');
  const [sortOption, setSortOption] = useState("date_asc");

  const fetchAllTrainings = async () => {
    try {
      const data = await getTrainings();
      setAllTrainings(data.trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchRegisteredTrainings = async () => {
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен");
      return;
    }
    try {
      const data = await getAthleteTrainings(athleteId);
      setRegisteredTrainings(data.trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchAllTrainings();
    fetchRegisteredTrainings();
  }, []);

  const formatDate = (timeString) => {
    const d = new Date(timeString);
    return d.toLocaleDateString('uk-UA', { day: '2-digit', month: '2-digit', year: 'numeric' });
  };

  const formatTime = (timeString) => {
    const d = new Date(timeString);
    return d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Фільтруємо доступні тренування – виключаємо ті, на які спортсмен уже записаний
  const registeredIds = new Set(registeredTrainings.map(t => t._id));
  let availableTrainings = allTrainings.filter(t => !registeredIds.has(t._id));

  if (filterCategory) {
    availableTrainings = availableTrainings.filter(t => t.category === filterCategory);
  }
  if (filterSection) {
    availableTrainings = availableTrainings.filter(t => t.section === filterSection);
  }

  // Сортування доступних тренувань
  availableTrainings.sort((a, b) => {
    switch (sortOption) {
      case "date_asc":
        return new Date(a.start_time) - new Date(b.start_time);
      case "date_desc":
        return new Date(b.start_time) - new Date(a.start_time);
      case "price_asc":
        return a.price - b.price;
      case "price_desc":
        return b.price - a.price;
      default:
        return 0;
    }
  });

  const handleSignup = async (trainingId) => {
    setError('');
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен, щоб записатись на тренування.");
      return;
    }
    try {
      const res = await signupForTraining(trainingId, athleteId);
      setModalMessage(res.message);
      setShowModal(true);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    setModalMessage('');
  };

  // Групування тренувань, на які спортсмен записаний (залишається для секції "Ваші тренування")
  const groupTrainingsByDate = (trainings) => {
    const groups = {};
    trainings.forEach(training => {
      const d = new Date(training.start_time);
      const dateStr = d.toLocaleDateString('uk-UA', { day: '2-digit', month: '2-digit', year: 'numeric' });
      if (!groups[dateStr]) groups[dateStr] = [];
      groups[dateStr].push(training);
    });
    Object.keys(groups).forEach(date => {
      groups[date].sort((a, b) => new Date(a.start_time) - new Date(b.start_time));
    });
    return groups;
  };

  const groupedRegistered = groupTrainingsByDate(registeredTrainings);
  const sortedRegisteredDates = Object.keys(groupedRegistered).sort((a, b) => {
    const [dA, mA, yA] = a.split('.');
    const [dB, mB, yB] = b.split('.');
    return new Date(yA, mA - 1, dA) - new Date(yB, mB - 1, dB);
  });

  return (
    <div className="dashboard-container">
      <h1>Кабінет Спортсмена</h1>
      {error && <p className="error-message">{error}</p>}

      {/* Блок доступних тренувань */}
      <section className="available-trainings-section">
        <div className="filters">
          <div className="filter">
            <label htmlFor="filterCategory">Категорія:</label>
            <select
              id="filterCategory"
              value={filterCategory}
              onChange={(e) => {
                setFilterCategory(e.target.value);
                setFilterSection('');
              }}
            >
              <option value="">-- Всі категорії --</option>
              {Object.keys(sportCategories).map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <div className="filter">
            <label htmlFor="filterSection">Секція:</label>
            <select
              id="filterSection"
              value={filterSection}
              onChange={(e) => setFilterSection(e.target.value)}
              disabled={!filterCategory}
            >
              <option value="">-- Всі секції --</option>
              {filterCategory && sportCategories[filterCategory].map(sect => (
                <option key={sect} value={sect}>{sect}</option>
              ))}
            </select>
          </div>
          <div className="filter">
            <label htmlFor="sortOption">Сортувати за:</label>
            <select
              id="sortOption"
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
            >
              <option value="date_asc">За датою (зростання)</option>
              <option value="date_desc">За датою (спадання)</option>
              <option value="price_asc">За ціною (зростання)</option>
              <option value="price_desc">За ціною (спадання)</option>
            </select>
          </div>
          <button
            className="reset-filter-btn"
            onClick={() => {
              setFilterCategory('');
              setFilterSection('');
              setSortOption("date_asc");
            }}
          >
            Скинути фільтр
          </button>
        </div>

        <h2>Доступні тренування</h2>
        {availableTrainings.length === 0 ? (
          <p>Немає доступних тренувань.</p>
        ) : (
          <table className="trainings-table">
            <thead>
              <tr>
                <th>Дата</th>
                <th>Час</th>
                <th>Секція</th>
                <th>Вільних місць</th>
                <th>Тренер</th>
                <th>Вартість</th>
                <th>Дія</th>
              </tr>
            </thead>
            <tbody>
              {availableTrainings.map(t => {
                const dateStr = formatDate(t.start_time);
                const timeStr = `${formatTime(t.start_time)} - ${formatTime(t.end_time)}`;
                return (
                  <tr key={t._id}>
                    <td>{dateStr}</td>
                    <td>{timeStr}</td>
                    <td>{t.section}</td>
                    <td>{t.spots}</td>
                    <td>{t.coach_name}</td>
                    <td>{t.price} {t.currency}</td>
                    <td>
                      <button
                        className="signup-btn"
                        onClick={() => handleSignup(t._id)}
                        disabled={t.spots === 0}
                      >
                        Записатись
                      </button>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        )}
      </section>

      {/* Блок "Ваші тренування" */}
      <section className="registered-trainings-section">
        <h2>Ваші тренування</h2>
        {Object.keys(groupTrainingsByDate(registeredTrainings)).length === 0 ? (
          <p>Ви ще не записалися на тренування.</p>
        ) : (
          Object.keys(groupTrainingsByDate(registeredTrainings))
            .sort((a, b) => {
              const [dA, mA, yA] = a.split('.');
              const [dB, mB, yB] = b.split('.');
              return new Date(yA, mA - 1, dA) - new Date(yB, mB - 1, dB);
            })
            .map(date => (
              <div key={date} className="registered-group">
                <h3 className="training-date-header">{date}</h3>
                <ul className="training-list">
                  {groupTrainingsByDate(registeredTrainings)[date].map(t => {
                    const timeStr = `${formatTime(t.start_time)} - ${formatTime(t.end_time)}`;
                    return (
                      <li key={t._id} className="training-item">
                        <div className="training-details">
                          <span className="training-time">{timeStr}</span>
                          <span className="training-section">{t.section}</span>
                        </div>
                      </li>
                    );
                  })}
                </ul>
              </div>
            ))
        )}
      </section>

      {showModal && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}
    </div>
  );
};

export default AthleteDashboard;
