import React, { useState } from 'react';
import { registerUser } from '../../services/api';
import { useHistory } from 'react-router-dom';
import Modal from '../Common/Modal';

const Register = () => {
  const [userType, setUserType] = useState('athlete'); // 'athlete' або 'coach'
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    email: '',
    password: '',
    section: ''
  });
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const history = useHistory();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (formData.password.length < 6) {
      setError('Пароль має містити не менше 6 символів.');
      return;
    }

    const payload = {
      type: userType,
      data: {
        name: formData.name,
        age: parseInt(formData.age),
        email: formData.email,
        password: formData.password,
        ...(userType === 'coach' && { section: formData.section })
      }
    };

    try {
      await registerUser(payload);
      setModalMessage(
        `Акаунт типу ${userType === 'athlete' ? 'Спортсмен' : 'Тренер'} успішно зареєстрований!`
      );
      setShowModal(true);
    } catch (error) {
      setError(error.message);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    // Після закриття модального вікна перенаправляємо до сторінки авторизації
    history.push('/login');
  };

  return (
    <div className="auth-container">
      <h2>Реєстрація</h2>
      <div className="user-type-switch">
        <button
          onClick={() => setUserType('athlete')}
          className={userType === 'athlete' ? 'selected' : ''}
        >
          Спортсмен
        </button>
        <button
          onClick={() => setUserType('coach')}
          className={userType === 'coach' ? 'selected' : ''}
        >
          Тренер
        </button>
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Ім'я"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="age"
          placeholder="Вік"
          value={formData.age}
          onChange={handleChange}
          required
        />
        {userType === 'coach' && (
          <input
            type="text"
            name="section"
            placeholder="Секція"
            value={formData.section}
            onChange={handleChange}
            required
          />
        )}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Пароль"
          value={formData.password}
          onChange={handleChange}
          required
        />
        {error && <p className="error-message">{error}</p>}
        <button type="submit">Зареєструватися</button>
      </form>
      <p>
        Вже маєте аккаунт? <a href="/login">Увійдіть</a>
      </p>
      {showModal && <Modal message={modalMessage} onClose={handleModalClose} />}
    </div>
  );
};

export default Register;
