// src/components/AthleteDashboard/AvailableTrainingsSection.js
import React from 'react';
import Modal from '../../components/Common/Modal';
import {
  format as formatDateFns,
  parseISO
} from 'date-fns';
import './AthleteDashboard.css';

const DEFAULT_AVATAR = '.../avatars/default.png'; // або ваш API_BASE_URL/avatars/default.png

export default function AvailableTrainingsSection({
  allTrainings,
  registeredTrainings,
  recommendations,
  filterCategory,
  filterSection,
  sortOption,
  onFilterCategory,
  onFilterSection,
  onSortOption,
  sportCategories,
  sportLogos,
  onSignup,
  onViewCoach,
  onRefreshRecs
}) {
  // Фільтрація та сортування доступних (як у вас)
  const regIds = new Set(registeredTrainings.map(t=>t._id));
  let available = allTrainings.filter(t=>!regIds.has(t._id));
  if (filterCategory) available = available.filter(t=>t.category===filterCategory);
  if (filterSection)   available = available.filter(t=>t.section===filterSection);
  available.sort((a,b)=>{/* ваш sort logic */});

  const fmtDate = iso => new Date(iso).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const fmtTime = iso => new Date(iso).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});

  return (
    <div>
      {/* Фільтри */}
      <div className="filter-form">
        {/* ...як було */}
      </div>

      <h2>Доступні тренування</h2>
      {available.length===0
        ? <p>Немає доступних тренувань.</p>
        : <div className="cards-grid">
          {available.map(t=>(
            <div key={t._id} className="training-card">
              <div className="card-header">
                <span className="card-date">{fmtDate(t.start_time)}</span>
                <span className="card-time">{`${fmtTime(t.start_time)} – ${fmtTime(t.end_time)}`}</span>
              </div>
              <div className="card-body">
                {sportLogos[t.section] && (
                  <img src={sportLogos[t.section]} alt={t.section} className="card-logo"/>
                )}
                <p><strong>Секція:</strong> {t.section}</p>
                <p className="trainer-line">
                  <strong>Тренер:</strong>{' '}
                  <img
                    src={t.coach_avatar_url||DEFAULT_AVATAR}
                    className="coach-avatar-small"
                    onError={e=>e.currentTarget.src=DEFAULT_AVATAR}
                  />{' '}
                  <span
                    className="coach-name-link"
                    onClick={()=>onViewCoach(t.coach_id)}
                  >{t.coach_name}</span>
                </p>
                <p><strong>Ціна:</strong> {t.price} {t.currency}</p>
                <p><strong>Місць:</strong> {t.spots}</p>
              </div>
              <div className="card-footer">
                <button
                  className="signup-btn"
                  disabled={t.spots===0}
                  onClick={()=>onSignup(t._id)}
                >Записатись</button>
              </div>
            </div>
          ))}
        </div>
      }

      {/* Рекомендації */}
      <h2>Рекомендовані тренування</h2>
      <button className="recommend-btn" onClick={onRefreshRecs}>Оновити рекомендації</button>
      {recommendations.length===0
        ? <p className="no-recommendations">Немає рекомендацій.</p>
        : <div className="cards-grid">
            {recommendations.map(t=>(
              <div key={t._id} className="training-card">
                {/* повторіть ті самі елементи, що й зверху */}
                <div className="card-header">
                  <span className="card-date">{fmtDate(t.start_time)}</span>
                  <span className="card-time">{`${fmtTime(t.start_time)} – ${fmtTime(t.end_time)}`}</span>
                </div>
                <div className="card-body">
                  {sportLogos[t.section] && <img src={sportLogos[t.section]} className="card-logo" />}
                  <p><strong>Секція:</strong> {t.section}</p>
                  <p className="trainer-line">
                    <strong>Тренер:</strong>{' '}
                    <img
                      src={t.coach_avatar_url||DEFAULT_AVATAR}
                      className="coach-avatar-small"
                      onError={e=>e.currentTarget.src=DEFAULT_AVATAR}
                    />{' '}
                    <span className="coach-name-link"
                      onClick={()=>onViewCoach(t.coach_id)}
                    >{t.coach_name}</span>
                  </p>
                  <p><strong>Ціна:</strong> {t.price} {t.currency}</p>
                  <p><strong>Місць:</strong> {t.spots}</p>
                </div>
                <div className="card-footer">
                  <button
                    className="signup-btn"
                    disabled={t.spots===0}
                    onClick={()=>onSignup(t._id)}
                  >Записатись</button>
                </div>
              </div>
            ))}
          </div>
      }
    </div>
  );
}
