import React from 'react';
import { format } from 'date-fns';
import './CoachDashboards.css';

export default function MyTrainingSection({
  trainings,
  onView,
  onDelete,
  sportLogos     // ← додаємо тут
}) {
  if (trainings.length === 0) {
    return <p>Немає створених тренувань.</p>;
  }
  return (
    <div className="cards-grid">
      {trainings.map(training => {
        const date = format(new Date(training.start_time), 'dd.MM.yyyy');
        const time = `${format(new Date(training.start_time), 'HH:mm')} – ${format(new Date(training.end_time), 'HH:mm')}`;
        const signed = training.participants?.length || 0;
        const total  = signed + parseInt(training.spots, 10);
        const logo = sportLogos[training.section];  // ← беремо лого по секції

        return (
          <div key={training._id} className="training-card">
            <div className="card-header">
              <span className="card-date">{date}</span>
              <span className="card-time">{time}</span>
            </div>
            <div className="card-body">
              {logo && <img src={logo} alt={training.section} className="card-logo" />} {/* ← відображаємо */}
              <p className="section-name">{training.section}</p>
              <p className="participants-count">
                Учасників: {signed} із {total}
              </p>
            </div>
            <div className="card-footer">
              <button className="view-btn" onClick={() => onView(training)}>
                Переглянути
              </button>
              <button className="delete-btn" onClick={() => onDelete(training)}>
                Видалити
              </button>
            </div>
          </div>
        );
      })}
    </div>
  );
}
