// src/components/AthleteDashboard/YourTrainingsSection.js
import React from 'react';
import './AthleteDashboard.css';

const DEFAULT_AVATAR = '.../avatars/default.png';

export default function YourTrainingsSection({
  registeredTrainings,
  sportLogos,
  onViewDetails,
  onCancel,
  onViewCoach
}) {
  const fmtDate = t => new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const fmtTime = t => new Date(t).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});

  const sorted = [...registeredTrainings].sort((a,b)=>new Date(a.start_time)-new Date(b.start_time));

  return (
    <div>
      <h2>Ваші тренування</h2>
      {sorted.length===0
        ? <p>Немає записів.</p>
        : <div className="registered-cards-grid">
            {sorted.map(t=> {
              const avatar = t.coach_avatar_url||DEFAULT_AVATAR;
              return (
                <div key={t._id} className="training-card registered-card new-design">
                  <div className="card-header">
                    <span className="card-date">{fmtDate(t.start_time)}</span>
                    <span className="card-time">{`${fmtTime(t.start_time)} – ${fmtTime(t.end_time)}`}</span>
                  </div>
                  <div className="card-body">
                    {sportLogos[t.section] && <img src={sportLogos[t.section]} className="registered-card-logo" />}
                    <p className="registered-section-name">{t.section}</p>
                    <p className="registered-trainer-line">
                      із <img src={avatar} className="coach-avatar-small" onError={e=>e.currentTarget.src=DEFAULT_AVATAR} />{' '}
                      <span className="coach-name-link" onClick={()=>onViewCoach(t.coach_id)}>{t.coach_name}</span>
                    </p>
                  </div>
                  <div className="card-footer">
                    <button className="action-btn view-btn" onClick={()=>onViewDetails(t)}>
                      Переглянути
                    </button>
                    <button className="action-btn cancel-btn" onClick={()=>onCancel(t)}>
                      Відмінити запис
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
      }
    </div>
  );
}
