import React, { useState } from 'react';
import { loginUser } from '../../services/api';
import { useHistory } from 'react-router-dom';
import Modal from '../Common/Modal';

const Login = () => {
  const [credentials, setCredentials] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [role, setRole] = useState('');
  const history = useHistory();

  const handleChange = (e) => {
    setCredentials({ ...credentials, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await loginUser(credentials);
      setModalMessage(
        `Ваш акаунт успішно авторизовано! Ласкавимо просимо до порталу, ${res.name}!`
      );
      setRole(res.role); // Запам'ятовуємо роль користувача
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    // Перенаправлення залежно від ролі
    if (role === 'athlete') {
      history.push('/athlete-dashboard');
    } else if (role === 'coach') {
      history.push('/coach-dashboard');
    } else {
      history.push('/');
    }
  };

  return (
    <div className="auth-container">
      <h2>Авторизація</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={credentials.email}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Пароль"
          value={credentials.password}
          onChange={handleChange}
          required
        />
        {error && <p className="error-message">{error}</p>}
        <button type="submit">Увійти</button>
      </form>
      <p>
        Ще не маєте аккаунт? <a href="/">Реєстрація</a>
      </p>
      {showModal && <Modal message={modalMessage} onClose={handleModalClose} />}
    </div>
  );
};

export default Login;
