const API_BASE_URL = 'http://localhost:8000'; // При необхідності скоригуйте адресу

export const registerUser = async (payload) => {
  const res = await fetch(`${API_BASE_URL}/register`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(payload)
  });
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || 'Помилка при реєстрації');
  }
  return res.json();
};

export const loginUser = async (credentials) => {
  const res = await fetch(`${API_BASE_URL}/login`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(credentials)
  });
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || 'Помилка авторизації');
  }
  return res.json();
};

export const createTraining = async (trainingData) => {
  const res = await fetch(`${API_BASE_URL}/create-training`, {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json'
    },
    body: JSON.stringify(trainingData)
  });
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || 'Помилка створення тренування');
  }
  return res.json();
};
