import React, { useState, useEffect } from 'react';
import {
  getTrainings,
  getAthleteTrainings,
  signupForTraining,
  cancelEnrollment
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/AthleteDashboard.css';

// Відношення для логотипів (шляхи до зображень логотипів)
const sportLogos = {
  "Футбол": "/assets/logos/football.jpg",
  "Баскетбол": "/assets/logos/basketball.jpg",
  "Бадмінтон": "/assets/logos/badminton.jpg",
  "Волейбол": "/assets/logos/volleyball.jpg",
  "Бейсбол": "/assets/logos/baseball.jpg",
  "Регбі": "/assets/logos/rugby.jpg",
  "Хокей": "/assets/logos/hockey.jpg",
  "Карате": "/assets/logos/karate.jpg",
  "Бокс": "/assets/logos/boxing.jpg",
  "Ушу": "/assets/logos/wushu.jpg",
  "Джиу-джитсу": "/assets/logos/jiu_jitsu.jpg",
  "ММА": "/assets/logos/mma.jpg",
  "Біг": "/assets/logos/running.jpg",
  "Стрибки": "/assets/logos/jumping.jpg",
  "Метання": "/assets/logos/throwing.jpg",
  "Плавання": "/assets/logos/swimming.jpg",
  "Вітрильний спорт": "/assets/logos/sailing.jpg",
  "Каноїзм": "/assets/logos/canoeing.jpg",
  "Скейбординг": "/assets/logos/skateboarding.jpg",
  "Сноубординг": "/assets/logos/snowboarding.jpg",
  "Альпінізм": "/assets/logos/climbing.jpg",
  "Фітнес": "/assets/logos/fitness.jpg",
  "Силові тренування": "/assets/logos/strength_training.jpg",
  "Йога": "/assets/logos/yoga.jpg"
};

const AthleteDashboard = () => {
  const [allTrainings, setAllTrainings] = useState([]);
  const [registeredTrainings, setRegisteredTrainings] = useState([]);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  // Стан для модального вікна підтвердження скасування запису
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [selectedCancelTraining, setSelectedCancelTraining] = useState(null);

  // Стан для модального вікна з деталями тренування
  const [selectedTraining, setSelectedTraining] = useState(null);

  // Стани для фільтрації та сортування
  const [filterCategory, setFilterCategory] = useState('');
  const [filterSection, setFilterSection] = useState('');
  const [sortOption, setSortOption] = useState("date_asc");

  const fetchAllTrainings = async () => {
    try {
      const data = await getTrainings();
      setAllTrainings(data.trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchRegisteredTrainings = async () => {
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен");
      return;
    }
    try {
      const data = await getAthleteTrainings(athleteId);
      setRegisteredTrainings(data.trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchAllTrainings();
    fetchRegisteredTrainings();
  }, []);

  const formatDate = (timeString) => {
    const d = new Date(timeString);
    return d.toLocaleDateString('uk-UA', { day: '2-digit', month: '2-digit', year: 'numeric' });
  };

  const formatTime = (timeString) => {
    const d = new Date(timeString);
    return d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Фільтрація: виключаємо тренування, на які спортсмен вже записаний
  const registeredIds = new Set(registeredTrainings.map(t => t._id));
  let availableTrainings = allTrainings.filter(t => !registeredIds.has(t._id));

  if (filterCategory) {
    availableTrainings = availableTrainings.filter(t => t.category === filterCategory);
  }
  if (filterSection) {
    availableTrainings = availableTrainings.filter(t => t.section === filterSection);
  }

  availableTrainings.sort((a, b) => {
    switch (sortOption) {
      case "date_asc":
        return new Date(a.start_time) - new Date(b.start_time);
      case "date_desc":
        return new Date(b.start_time) - new Date(a.start_time);
      case "price_asc":
        return a.price - b.price;
      case "price_desc":
        return b.price - a.price;
      default:
        return 0;
    }
  });

  const handleSignup = async (trainingId) => {
    setError('');
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен, щоб записатись на тренування.");
      return;
    }
    try {
      const res = await signupForTraining(trainingId, athleteId);
      setModalMessage(res.message);
      setShowModal(true);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
    }
  };

  // Функції для роботи з модальним вікном скасування запису
  const openCancelModal = (training) => {
    setSelectedCancelTraining(training);
    setShowCancelModal(true);
  };

  const handleCancelEnrollment = async () => {
    if (!selectedCancelTraining) return;
    const athleteId = localStorage.getItem('userId');
    try {
      await cancelEnrollment(selectedCancelTraining._id, athleteId);
      setModalMessage("Запис успішно скасовано.");
      setShowModal(true);
      setShowCancelModal(false);
      setSelectedCancelTraining(null);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
      setShowCancelModal(false);
    }
  };

  const handleCancelModalClose = () => {
    setShowCancelModal(false);
    setSelectedCancelTraining(null);
  };

  const handleModalClose = () => {
    setShowModal(false);
    setModalMessage('');
    setSelectedTraining(null);
  };

  // Сортуємо тренування спортсмена за часом – без групування
  const sortedRegistered = [...registeredTrainings].sort(
    (a, b) => new Date(a.start_time) - new Date(b.start_time)
  );

  // Функція відкриття модального вікна з деталями тренування
  const handleViewDetails = (training) => {
    setSelectedTraining(training);
    setShowModal(true);
  };

  return (
    <div className="dashboard-container">
      <h1>Кабінет Спортсмена</h1>
      {error && <p className="error-message">{error}</p>}

      {/* Секція "Доступні тренування" – карточки з повною інформацією */}
      <section className="available-trainings-section">
        <div className="filter-form">
          <div className="filter-row">
            <label htmlFor="filterCategory">Категорія:</label>
            <select
              id="filterCategory"
              value={filterCategory}
              onChange={(e) => {
                setFilterCategory(e.target.value);
                setFilterSection('');
              }}
            >
              <option value="">-- Всі категорії --</option>
              {Object.keys(sportLogos).map((sect) => (
                <option key={sect} value={sect}>
                  {sect}
                </option>
              ))}
            </select>
          </div>
          <div className="filter-row">
            <label htmlFor="filterSection">Секція:</label>
            <select
              id="filterSection"
              value={filterSection}
              onChange={(e) => setFilterSection(e.target.value)}
              disabled={!filterCategory}
            >
              <option value="">-- Всі секції --</option>
              {filterCategory &&
                sportCategories[filterCategory] &&
                sportCategories[filterCategory].map((sect) => (
                  <option key={sect} value={sect}>
                    {sect}
                  </option>
                ))}
            </select>
          </div>
          <div className="filter-row">
            <label htmlFor="sortOption">Сортувати за:</label>
            <select
              id="sortOption"
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
            >
              <option value="date_asc">За датою (зростання)</option>
              <option value="date_desc">За датою (спадання)</option>
              <option value="price_asc">За ціною (зростання)</option>
              <option value="price_desc">За ціною (спадання)</option>
            </select>
          </div>
          <button
            className="reset-filter-btn"
            onClick={() => {
              setFilterCategory('');
              setFilterSection('');
              setSortOption("date_asc");
            }}
          >
            Скинути фільтр
          </button>
        </div>

        <h2>Доступні тренування</h2>
        {availableTrainings.length === 0 ? (
          <p>Немає доступних тренувань.</p>
        ) : (
          <div className="cards-grid">
            {availableTrainings.map((t) => {
              const dateStr = formatDate(t.start_time);
              const timeStr = `${formatTime(t.start_time)} - ${formatTime(t.end_time)}`;
              // Отримуємо шлях до логотипу за секцією, якщо є
              const logoSrc = sportLogos[t.section] || '';
              return (
                <div key={t._id} className="training-card">
                  <div className="card-header">
                    {logoSrc && (
                      <img src={logoSrc} alt={t.section} className="card-logo" />
                    )}
                    <span className="card-date">{dateStr}</span>
                    <span className="card-time">{timeStr}</span>
                  </div>
                  <div className="card-body">
                    <p className="card-section">
                      <strong>Секція:</strong> {t.section}
                    </p>
                    <p className="card-coach">
                      <strong>Тренер:</strong> {t.coach_name}
                    </p>
                    <p className="card-price">
                      <strong>Вартість:</strong> {t.price} {t.currency}
                    </p>
                    <p className="card-spots">
                      <strong>Вільних місць:</strong> {t.spots}
                    </p>
                  </div>
                  <div className="card-footer">
                    <button
                      className="signup-btn"
                      onClick={() => handleSignup(t._id)}
                      disabled={t.spots === 0}
                    >
                      Записатись
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Секція "Ваші тренування" – карточки з мінімальною інформацією, новий дизайн */}
      <section className="registered-trainings-section">
        <h2>Ваші тренування</h2>
        {sortedRegistered.length === 0 ? (
          <p>Ви ще не записалися на тренування.</p>
        ) : (
          <div className="registered-cards-grid">
            {sortedRegistered.map((t) => {
              const dateStr = formatDate(t.start_time);
              const timeStr = `${formatTime(t.start_time)} - ${formatTime(t.end_time)}`;
              return (
                <div key={t._id} className="training-card registered-card new-design">
                  <div className="card-header header-compact">
                    <span className="card-date">{dateStr}</span>
                    <span className="card-time">{timeStr}</span>
                  </div>
                  <div className="card-body body-compact">
                    <p className="card-section">{t.section}</p>
                  </div>
                  <div className="card-footer footer-compact">
                    <button
                      className="action-btn view-btn"
                      onClick={() => handleViewDetails(t)}
                    >
                      Переглянути
                    </button>
                    <button
                      className="action-btn cancel-btn"
                      onClick={() => openCancelModal(t)}
                    >
                      Відмінити запис
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Модальне вікно підтвердження скасування запису */}
      {showCancelModal && (
        <Modal
          modalType="confirm"
          onClose={handleCancelModalClose}
          message={
            <div className="cancel-modal">
              <p>Ви впевнені, що хочете відмінити запис на це тренування?</p>
              <div className="modal-buttons">
                <button className="confirm-btn" onClick={handleCancelEnrollment}>
                  Так
                </button>
                <button className="cancel-btn" onClick={handleCancelModalClose}>
                  Ні
                </button>
              </div>
            </div>
          }
        />
      )}

      {/* Модальне вікно для перегляду деталей тренування */}
      {showModal && selectedTraining && (
        <Modal
          modalType="info"
          onClose={handleModalClose}
          message={
            <div className="details-modal">
              <h3>Детальна інформація</h3>
              <p><strong>Дата:</strong> {formatDate(selectedTraining.start_time)}</p>
              <p>
                <strong>Час:</strong> {`${formatTime(selectedTraining.start_time)} - ${formatTime(selectedTraining.end_time)}`}
              </p>
              <p><strong>Секція:</strong> {selectedTraining.section}</p>
              <p><strong>Вартість:</strong> {selectedTraining.price} {selectedTraining.currency}</p>
              <p><strong>Записано учасників:</strong> {selectedTraining.participants ? selectedTraining.participants.length : 0}</p>
              <p><strong>Тренер:</strong> {selectedTraining.coach_name}</p>
              <button onClick={handleModalClose}>Ок</button>
            </div>
          }
        />
      )}

      {/* Модальне вікно для інших повідомлень */}
      {showModal && !selectedTraining && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}
    </div>
  );
};

export default AthleteDashboard;
