// src/components/Dashboards/CoachDashboards/NavigationPanel.jsx
import React from 'react';
import './NavigationPanel.css'; // <-- підключаємо саме цей файл зі стилями

/**
 * Компонент навігаційної панелі
 *
 * @param {Object} props
 * @param {'my-trainings'|'create-training'|'profile'} props.activeTab — поточна активна вкладка
 * @param {(tab: string) => void} props.onTabChange — колбек по кліку на таб
 */
export default function NavigationPanel({ activeTab, onTabChange }) {
  return (
    <nav className="navigation-panel">
      <button
        className={`tab ${activeTab === 'my-trainings' ? 'active' : ''}`}
        onClick={() => onTabChange('my-trainings')}
      >
        Ваші тренування
      </button>

      <button
        className={`tab ${activeTab === 'create-training' ? 'active' : ''}`}
        onClick={() => onTabChange('create-training')}
      >
        Створити тренування
      </button>

      <button
        className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
        onClick={() => onTabChange('profile')}
      >
        Ваш профіль
      </button>
    </nav>
  );
}
