import React, { useState } from 'react';
import { registerUser, loginUser } from '../../services/api';
import Modal from '../../components/Common/Modal';
import { useHistory } from 'react-router-dom';

const AuthPage = () => {
  // "mode" дозволяє перемикатися між реєстрацією (register) та авторизацією (login)
  const [mode, setMode] = useState('register');
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    email: '',
    password: '',
    section: ''
  });
  const [userType, setUserType] = useState('athlete'); // 'athlete' або 'coach'
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [role, setRole] = useState('');
  const history = useHistory();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (formData.password.length < 6) {
      setError('Пароль має містити не менше 6 символів.');
      return;
    }
    const payload = {
      type: userType,
      data: {
        name: formData.name,
        age: parseInt(formData.age),
        email: formData.email,
        password: formData.password,
        ...(userType === 'coach' && { section: formData.section })
      }
    };
    try {
      await registerUser(payload);
      setModalMessage(
        `Акаунт типу ${userType === 'athlete' ? 'Спортсмен' : 'Тренер'} успішно зареєстрований!`
      );
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await loginUser({
        email: formData.email,
        password: formData.password
      });
      setModalMessage(
        `Ваш акаунт успішно авторизовано! Ласкаво просимо до порталу, ${res.name}!`
      );
      setRole(res.role);
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    if (mode === 'register') {
      // Після успішної реєстрації – перемикаємо режим на авторизацію та очищуємо форму
      setMode('login');
      setError('');
      setFormData({
        name: '',
        age: '',
        email: '',
        password: '',
        section: ''
      });
    } else if (mode === 'login') {
      if (role === 'athlete') {
        history.push('/athlete-dashboard');
      } else if (role === 'coach') {
        history.push('/coach-dashboard');
      }
    }
  };

  return (
    <div className="landing-container">
      <div className="welcome-text">
        <h1>Ласкаво просимо до Порталу Тренувань!</h1>
        <p>Найкращий керівник твоєго спортивного життя</p>
      </div>
      <div className="form-container">
        {mode === 'register' ? (
          <>
            <h2>Реєстрація</h2>
            <div className="user-type-group">
              <small className="user-type-label">
                Оберіть бажаний тип користувача
              </small>
              <div className="user-type-switch">
                <button
                  onClick={() => setUserType('athlete')}
                  className={userType === 'athlete' ? 'selected' : ''}
                >
                  Спортсмен
                </button>
                <button
                  onClick={() => setUserType('coach')}
                  className={userType === 'coach' ? 'selected' : ''}
                >
                  Тренер
                </button>
              </div>
            </div>
            <form onSubmit={handleRegister}>
              <input
                type="text"
                name="name"
                placeholder="Ім'я"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <input
                type="number"
                name="age"
                placeholder="Вік"
                value={formData.age}
                onChange={handleChange}
                required
              />
              {userType === 'coach' && (
                <input
                  type="text"
                  name="section"
                  placeholder="Секція"
                  value={formData.section}
                  onChange={handleChange}
                  required
                />
              )}
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Пароль"
                value={formData.password}
                onChange={handleChange}
                required
              />
              {error && <p className="error-message">{error}</p>}
              <button type="submit">Зареєструватися</button>
            </form>
            <p>
              Вже маєте акаунт?{' '}
              <span
                className="toggle-link"
                onClick={() => {
                  setMode('login');
                  setError('');
                  setFormData({
                    name: '',
                    age: '',
                    email: '',
                    password: '',
                    section: ''
                  });
                }}
              >
                Увійдіть
              </span>
            </p>
          </>
        ) : (
          <>
            <h2>Авторизація</h2>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Пароль"
                value={formData.password}
                onChange={handleChange}
                required
              />
              {error && <p className="error-message">{error}</p>}
              <button type="submit">Увійти</button>
            </form>
            <p>
              Ще не маєте акаунт?{' '}
              <span
                className="toggle-link"
                onClick={() => {
                  setMode('register');
                  setError('');
                  setFormData({
                    name: '',
                    age: '',
                    email: '',
                    password: '',
                    section: ''
                  });
                }}
              >
                Реєстрація
              </span>
            </p>
          </>
        )}
        {showModal && <Modal message={modalMessage} onClose={handleModalClose} />}
      </div>
      <footer className="footer">
        <p>
          Технічна підтримка: +380 (XX) XXX-XX-XX | Email: support@company.ua | Адреса: вул. Хрещатик, 1, Київ
        </p>
      </footer>
    </div>
  );
};

export default AuthPage;
