import React, { useState, useEffect, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import NavigationPanel from './NavigationPanel';
import MyTrainingSection from './MyTrainingSection';
import CreateTrainingSection from './CreateTrainingSection';
import ProfileSection from './ProfileSection';
import ReviewsSection from './ReviewsSection';
import Modal from '../../Common/Modal';
import {
  createTraining,
  getCoachTrainings,
  getTrainingParticipants,
  updateTraining,
  deleteTraining,
  getCoachProfile,
  getAthleteProfile,
  updateCoachProfile,
  changeCoachPassword,
  uploadCoachAvatar,
  getCoachReviews,
  API_BASE_URL
} from '../../../services/api';
import './CoachDashboards.css';

// Мапа секція → логотип
const sportLogos = {
  "Футбол":        require('../../../assets/logos/football.jpg'),
  "Баскетбол":     require('../../../assets/logos/basketball.jpg'),
  "Бадмінтон":     require('../../../assets/logos/badminton.jpg'),
  "Волейбол":      require('../../../assets/logos/volleyball.jpg'),
  "Бейсбол":       require('../../../assets/logos/baseball.jpg'),
  "Регбі":         require('../../../assets/logos/rugby.jpg'),
  "Хокей":         require('../../../assets/logos/hockey.jpg'),
  "Карате":        require('../../../assets/logos/karate.jpg'),
  "Бокс":          require('../../../assets/logos/boxing.jpg'),
  "Ушу":           require('../../../assets/logos/wushu.jpg'),
  "Джиу-джитсу":   require('../../../assets/logos/jiu_jitsu.jpg'),
  "ММА":           require('../../../assets/logos/mma.jpg'),
  "Біг":           require('../../../assets/logos/running.jpg'),
  "Стрибки":       require('../../../assets/logos/jumping.jpg'),
  "Метання":       require('../../../assets/logos/throwing.jpg'),
  "Плавання":      require('../../../assets/logos/swimming.jpg'),
  "Вітрильний спорт":  require('../../../assets/logos/sailing.jpg'),
  "Каноїзм":           require('../../../assets/logos/canoeing.jpg'),
  "Скейбординг":       require('../../../assets/logos/skateboarding.jpg'),
  "Сноубординг":       require('../../../assets/logos/snowboarding.jpg'),
  "Альпінізм":         require('../../../assets/logos/climbing.jpg'),
  "Фітнес":            require('../../../assets/logos/fitness.jpg'),
  "Силові тренування": require('../../../assets/logos/strength_training.jpg'),
  "Йога":              require('../../../assets/logos/yoga.jpg')
};

// Категорії та секції
const sportCategories = {
  "Ігри з м'ячем":           ["Футбол","Баскетбол","Бадмінтон","Волейбол","Бейсбол","Регбі","Хокей"],
  "Бойові мистецтва":        ["Карате","Бокс","Ушу","Джиу-джитсу","ММА"],
  "Легка атлетика":          ["Біг","Стрибки","Метання"],
  "Водні види спорту":       ["Плавання","Вітрильний спорт","Каноїзм"],
  "Екстремальні види спорту":["Скейбординг","Сноубординг","Альпінізм"],
  "Фітнес та тренажерний зал":["Фітнес","Силові тренування","Йога"]
};

const defaultAvatar = `${API_BASE_URL}/avatars/default.png`;

export default function CoachDashboard() {
  // ─── Навігація ───
  const navigate = useNavigate();

  const handleLogout = () => {
  // 1) забираємо з localStorage ідентифікатор користувача
  localStorage.removeItem('userId');
  // 2) перенаправляємо на сторінку логіну
  navigate('/login');
};

  const [activeTab, setActiveTab]         = useState('my-trainings');
  // ─── показати/сховати форму створення ───
  const [showCreateForm, setShowCreateForm] = useState(false);

  // ─── Створення тренувань ───
  const [trainingData, setTrainingData]   = useState({
    date: '', start_time: '', end_time: '',
    price: '', spots: '', currency: 'UAH',
    category: '', section: ''
  });
  const [errors, setErrors]               = useState({});
  const [modalMessage, setModalMessage]   = useState('');
  const [showModal, setShowModal]         = useState(false);

  // ─── Список тренувань ───
  const [trainings, setTrainings]         = useState([]);
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants]   = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);

  // ─── Режим редагування ───
  const [editMode, setEditMode]           = useState(false);
  const [editData, setEditData]           = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'',
    category:'', section:''
  });

  // ─── Профіль тренера ───
  const [profile, setProfile]             = useState(null);
  const [editProfileMode, setEditProfileMode] = useState(false);
  const [nameInput, setNameInput]         = useState('');
  const [ageInput, setAgeInput]           = useState('');
  const [phoneInput, setPhoneInput]       = useState('');
  const [description, setDescription]     = useState('');
  const [favoriteSports, setFavoriteSports] = useState([]);
  const [profileError, setProfileError]   = useState('');
  const [showProfileSuccess, setShowProfileSuccess] = useState(false);

  // ─── Аватар ───
  const [avatarFile, setAvatarFile]       = useState(null);
  const [avatarPreview, setAvatarPreview] = useState('');
  const [showAvatarSuccess, setShowAvatarSuccess] = useState(false);

  // ─── Зміна пароля ───
  const [showPassMode, setShowPassMode]   = useState(false);
  const [oldPass, setOldPass]             = useState('');
  const [newPass, setNewPass]             = useState('');
  const [confirmPass, setConfirmPass]     = useState('');
  const [passError, setPassError]         = useState('');
  const [passSuccess, setPassSuccess]     = useState(false);

  // ─── Видалення ───
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [trainingToDelete, setTrainingToDelete]   = useState(null);

  // ─── Відгуки ───
  const [reviews, setReviews]               = useState([]);
  const [avgRating, setAvgRating]           = useState(null);
  const [reviewCount, setReviewCount]       = useState(0);
  const reviewSectionRef = useRef(null);

  // ─── Інші константи ───
  const today = new Date().toISOString().split('T')[0];
  const allSections = Object.values(sportCategories).flat();

  // ─── Завантаження даних ───
  useEffect(() => {
    fetchTrainings();
    fetchProfileAndReviews();
  }, []);

  async function fetchTrainings() {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { trainings } = await getCoachTrainings(coachId);
      const parsed = trainings.map(t => ({
        ...t,
        spots: typeof t.spots === 'string' ? parseInt(t.spots, 10) : t.spots
      }));
      setTrainings(parsed);
    } catch (err) {
      console.error('Помилка завантаження тренувань:', err);
    }
  }

  async function fetchProfileAndReviews() {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      // 1) Профіль
      const { profile } = await getCoachProfile(coachId);
      setProfile(profile);
      setNameInput(profile.name);
      setAgeInput(profile.age);
      setPhoneInput(profile.phone || '');
      setDescription(profile.description || '');
      setFavoriteSports(profile.favorite_sports || []);
      setAvatarPreview(
        profile.avatar_url
          ? `${profile.avatar_url}?t=${Date.now()}`
          : defaultAvatar
      );

      // 2) Відгуки
      const { reviews, average_rating, count } = await getCoachReviews(coachId);
      const withAvatars = await Promise.all(
        reviews.map(async r => {
          if (!r.athlete_id) return { ...r, athlete_avatar_url: null };
          try {
            const { profile: athlete } = await getAthleteProfile(r.athlete_id);
            return { ...r, athlete_avatar_url: athlete.avatar_url || null };
          } catch {
            return { ...r, athlete_avatar_url: null };
          }
        })
      );
      setReviews(withAvatars);
      setAvgRating(average_rating);
      setReviewCount(count);
    } catch (err) {
      console.error('Помилка завантаження профілю/відгуків:', err);
    }
  }

  // ─── Формування мінімального часу ───
  function getMinTime() {
    return trainingData.date === today
      ? new Date().toTimeString().slice(0, 5)
      : '00:00';
  }

  // ─── Обробка зміни полів форми створення ───
  function handleChange(e) {
    setTrainingData({ ...trainingData, [e.target.name]: e.target.value });
  }

  function handleCategoryChange(e) {
    setTrainingData({
      ...trainingData,
      category: e.target.value,
      section: ''
    });
  }

  // ─── Надсилання створення тренування ───
  async function handleSubmit(e) {
    e.preventDefault();
    setErrors({});

    const coachId = localStorage.getItem('userId');
    if (!coachId) {
      setErrors({ general: 'Будь ласка, увійдіть повторно.' });
      return;
    }
    // базова валідація
    if (!trainingData.date || !trainingData.start_time || !trainingData.end_time) {
      setErrors({ general: 'Оберіть дату та час.' });
      return;
    }
    if (!trainingData.category || !trainingData.section) {
      setErrors({ general: 'Оберіть категорію/секцію.' });
      return;
    }
    if (trainingData.start_time >= trainingData.end_time) {
      setErrors({ end_time: 'Час завершення має бути пізніше.' });
      return;
    }
    const startDT = new Date(`${trainingData.date}T${trainingData.start_time}`);
    if (startDT < new Date()) {
      setErrors({ general: 'Не можна створювати в минулому.' });
      return;
    }

    const payload = {
      coach_id:    coachId,
      coach_name:  profile.name,
      start_time:  `${trainingData.date}T${trainingData.start_time}`,
      end_time:    `${trainingData.date}T${trainingData.end_time}`,
      price:       parseFloat(trainingData.price),
      spots:       parseInt(trainingData.spots, 10),
      currency:    trainingData.currency,
      category:    trainingData.category,
      section:     trainingData.section
    };

    try {
      await createTraining(payload);
      setModalMessage('Тренування успішно створено!');
      setShowModal(true);
      setTrainingData({
        date:'', start_time:'', end_time:'',
        price:'', spots:'', currency:'UAH',
        category:'', section:''
      });
      fetchTrainings();
      setShowCreateForm(false);
    } catch (err) {
      setErrors({ general: err.message });
    }
  }

  // ─── Перегляд учасників / запуск редагування ───
  async function handleViewParticipants(training) {
    const freeSeats = typeof training.spots === 'string'
      ? parseInt(training.spots, 10)
      : training.spots;
    setSelectedTraining({ ...training, spots: freeSeats });
    try {
      const { participants: fromServer } = await getTrainingParticipants(training._id);
      setParticipants(fromServer);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch (err) {
      console.error('Помилка завантаження учасників:', err);
    }
  }

  function handleDeleteClick(training) {
    setTrainingToDelete(training);
    setShowDeleteConfirm(true);
  }

  async function confirmDelete() {
    try {
      await deleteTraining(trainingToDelete._id);
      setModalMessage('Тренування успішно видалено.');
      setShowModal(true);
      fetchTrainings();
    } catch (err) {
      setModalMessage(`Помилка: ${err.message}`);
      setShowModal(true);
    } finally {
      setShowDeleteConfirm(false);
      setTrainingToDelete(null);
    }
  }

  function cancelDelete() {
    setShowDeleteConfirm(false);
    setTrainingToDelete(null);
  }

  function handleEdit() {
    const t = selectedTraining;
    const signed = t.participants?.length || 0;
    const freeSeats = parseInt(t.spots, 10);
    const totalCapacity = signed + freeSeats;
    setEditData({
      date:       new Date(t.start_time).toISOString().split('T')[0],
      start_time: new Date(t.start_time).toTimeString().slice(0,5),
      end_time:   new Date(t.end_time).toTimeString().slice(0,5),
      price:      t.price,
      currency:   t.currency,
      category:   t.category,
      section:    t.section,
      spots:      totalCapacity.toString()
    });
    setEditMode(true);
  }

  function handleEditChange(e) {
    setEditData({ ...editData, [e.target.name]: e.target.value });
  }

  async function handleEditSubmit(e) {
    e.preventDefault();
    setErrors({});

    if (!editData.date || !editData.start_time || !editData.end_time) {
      setErrors({ edit: 'Оберіть дату та час.' });
      return;
    }
    if (editData.start_time >= editData.end_time) {
      setErrors({ edit: 'Час завершення має бути пізніше.' });
      return;
    }
    const newStart = new Date(`${editData.date}T${editData.start_time}`);
    if (newStart < new Date()) {
      setErrors({ edit: 'Не можна у минулому.' });
      return;
    }
    const signed = selectedTraining.participants?.length || 0;
    const totalCapacity = parseInt(editData.spots, 10);
    if (isNaN(totalCapacity)) {
      setErrors({ edit: 'Кількість місць має бути числом.' });
      return;
    }
    if (totalCapacity < signed) {
      setErrors({ edit: `Загальна кількість (${totalCapacity}) менша за вже записані (${signed}).` });
      return;
    }

    const payload = {
      start_time: `${editData.date}T${editData.start_time}`,
      end_time:   `${editData.date}T${editData.end_time}`,
      price:      parseFloat(editData.price),
      currency:   editData.currency,
      category:   editData.category,
      section:    editData.section,
      spots:      totalCapacity
    };

    try {
      await updateTraining(selectedTraining._id, payload);
      setModalMessage('Тренування успішно оновлено!');
      setShowModal(true);
      setEditMode(false);
      fetchTrainings();
      closeParticipantsModal();
    } catch (err) {
      setErrors({ edit: err.message });
    }
  }

  function cancelEdit() {
    setEditMode(false);
  }

  function closeParticipantsModal() {
    setShowParticipantsModal(false);
    setSelectedTraining(null);
    setParticipants([]);
    setEditMode(false);
  }

  // ─── Профіль: редагування ───
  function handleProfileFieldChange(e) {
    const { name, value } = e.target;
    if (name === 'nameInput') setNameInput(value);
    if (name === 'ageInput') setAgeInput(value);
    if (name === 'phoneInput') setPhoneInput(value);
    if (name === 'description') setDescription(value);
    if (name === 'oldPass') setOldPass(value);
    if (name === 'newPass') setNewPass(value);
    if (name === 'confirmPass') setConfirmPass(value);
  }

  function toggleSport(sport) {
    setFavoriteSports(prev =>
      prev.includes(sport)
        ? prev.filter(s => s !== sport)
        : [...prev, sport]
    );
  }

  async function handleSaveProfile() {
    setProfileError('');
    if (!nameInput.trim()) {
      setProfileError('Поле "Ім\'я" обов\'язкове');
      return;
    }
    const ageNum = parseInt(ageInput, 10);
    if (isNaN(ageNum)) {
      setProfileError('Поле "Вік" обов\'язкове');
      return;
    }
    if (ageNum < 18) {
      setProfileError('Вік не може бути менше 18 років');
      return;
    }
    try {
      await updateCoachProfile({
        user_id:        profile.user_id,
        name:           nameInput.trim(),
        age:            ageNum,
        phone:          phoneInput,
        description,
        favorite_sports: favoriteSports
      });
      setShowProfileSuccess(true);
      setEditProfileMode(false);
      fetchProfileAndReviews();
    } catch (err) {
      setProfileError(err.message);
    }
  }

  function handleAvatarChange(e) {
    const file = e.target.files[0];
    if (!file) return;
    setAvatarFile(file);
    setAvatarPreview(URL.createObjectURL(file));
  }

  async function handleUploadAvatar() {
    if (!avatarFile) return;
    try {
      const { avatar_url } = await uploadCoachAvatar(profile.user_id, avatarFile);
      setAvatarPreview(avatar_url);
      fetchProfileAndReviews();
      setAvatarFile(null);
      setShowAvatarSuccess(true);
    } catch (err) {
      console.error('Помилка завантаження аватара:', err);
    }
  }

  async function handleChangePassword() {
    setPassError('');
    try {
      await changeCoachPassword({
        user_id:        profile.user_id,
        old_password:   oldPass,
        new_password:   newPass,
        confirm_password: confirmPass
      });
      setPassSuccess(true);
      setShowPassMode(false);
      setOldPass('');
      setNewPass('');
      setConfirmPass('');
    } catch (err) {
      setPassError(err.message);
    }
  }

  async function handleViewAthlete(athleteId) {
    try {
      const { profile: athlete } = await getAthleteProfile(athleteId);
      setAthleteProfile(athlete);
      setShowAthleteModal(true);
    } catch (err) {
      console.error('Помилка завантаження профілю спортсмена:', err);
    }
  }

  // ─── Форматери дати та часу ───
  function formatDate(dateString) {
    return new Date(dateString).toLocaleDateString('uk-UA', {
      day:   '2-digit',
      month: '2-digit',
      year:  'numeric'
    });
  }

  function formatTime(dateString) {
    return new Date(dateString).toLocaleTimeString([], {
      hour:   '2-digit',
      minute: '2-digit'
    });
  }

  // ─── JSX ───
  return (
    <div className="dashboard-container">

      <NavigationPanel
        activeTab={activeTab}
        onTabChange={setActiveTab}
        onLogout={handleLogout}
      />

      {activeTab === 'my-trainings' && (
        <>
          <div className="my-trainings-header">
            <button
              className="create-btn"
              onClick={() => setShowCreateForm(prev => !prev)}
            >
              {showCreateForm ? 'Закрити форму' : 'Створити тренування'}
            </button>
          </div>

          {showCreateForm && (
            <CreateTrainingSection
              trainingData={trainingData}
              errors={errors}
              onChangeField={handleChange}
              onChangeCategory={handleCategoryChange}
              onSubmit={handleSubmit}
              sportCategories={sportCategories}
              today={today}
              getMinTime={getMinTime}
            />
          )}

          <MyTrainingSection
            trainings={trainings}
            onView={handleViewParticipants}
            onDelete={handleDeleteClick}
            sportLogos={sportLogos}
          />
        </>
      )}

      {activeTab === 'profile' && profile && (
        <ProfileSection
          profile={profile}
          editMode={editProfileMode}
          nameInput={nameInput}
          ageInput={ageInput}
          phoneInput={phoneInput}
          description={description}
          favoriteSports={favoriteSports}
          allSections={allSections}
          profileError={profileError}
          showPassMode={showPassMode}
          oldPass={oldPass}
          newPass={newPass}
          confirmPass={confirmPass}
          passError={passError}
          onToggleEditMode={() => setEditProfileMode(true)}
          onChangeField={handleProfileFieldChange}
          onToggleSport={toggleSport}
          onSaveProfile={handleSaveProfile}
          onCancelEdit={() => setEditProfileMode(false)}
          onUploadAvatar={handleUploadAvatar}
          onTogglePasswordMode={() => setShowPassMode(true)}
          onChangePassword={handleChangePassword}
          onCancelPassword={() => setShowPassMode(false)}
          avatarPreview={avatarPreview}
          onAvatarFileChange={handleAvatarChange}
          avatarFile={avatarFile}
          reviews={reviews}
          avgRating={avgRating}
          reviewCount={reviewCount}
          reviewSectionRef={reviewSectionRef}
        />
      )}

      {activeTab === 'reviews' && (
     <ReviewsSection
       reviews={reviews}
       avgRating={avgRating}
       reviewCount={reviewCount}
        />
      )}

      {/* Підтвердження видалення */}
      {showDeleteConfirm && trainingToDelete && (
        <Modal
          modalType="confirm"
          onClose={cancelDelete}
          message={
            <div style={{ textAlign: 'center' }}>
              <p>
                Ви впевнені, що хочете видалити тренування<br/>
                <strong>{trainingToDelete.section}</strong> від {formatDate(trainingToDelete.start_time)}?
              </p>
              <div style={{ display: 'flex', justifyContent: 'center', gap: '1rem' }}>
                <button className="submit-btn" onClick={confirmDelete}>Так</button>
                <button className="cancel-btn" onClick={cancelDelete}>Ні</button>
              </div>
            </div>
          }
        />
      )}

      {/* Модалки успіху */}
      {showModal && (
        <Modal
          modalType="success"
          message={modalMessage}
          onClose={() => setShowModal(false)}
        />
      )}
      {showAvatarSuccess && (
        <Modal
          modalType="success"
          message="Фото успішно завантажено!"
          onClose={() => setShowAvatarSuccess(false)}
        />
      )}
      {passSuccess && (
        <Modal
          modalType="success"
          message="Пароль успішно змінено!"
          onClose={() => setPassSuccess(false)}
        />
      )}
      {showProfileSuccess && (
        <Modal
          modalType="success"
          message="Профіль оновлено!"
          onClose={() => setShowProfileSuccess(false)}
        />
      )}

      {/* Модалка учасників / редагування */}
      {showParticipantsModal && selectedTraining && (
        <Modal
          modalType={editMode ? "edit" : "info"}
          onClose={closeParticipantsModal}
          className="participants-modal"
          message={
            editMode ? (
              /* — РЕЖИМ РЕДАГУВАННЯ — */
              <div className="edit-modal-modern">
                <header className="edit-modal-modern__header">
                  <h2 className="edit-modal-modern__title">Редагувати тренування</h2>
                  <button
                    type="button"
                    className="edit-modal-modern__close"
                    onClick={cancelEdit}
                  >
                    ×
                  </button>
                </header>
                <form
                  onSubmit={handleEditSubmit}
                  className="edit-modal-modern__body"
                >
                  <div className="field-group">
                    <label htmlFor="editDate">Дата</label>
                    <input
                      type="date"
                      name="date"
                      id="editDate"
                      value={editData.date}
                      onChange={handleEditChange}
                      required
                    />
                  </div>
                  <div className="field-row">
                    <div className="field-group">
                      <label htmlFor="editStart">Початок</label>
                      <input
                        type="time"
                        name="start_time"
                        id="editStart"
                        value={editData.start_time}
                        onChange={handleEditChange}
                        required
                      />
                    </div>
                    <div className="field-group">
                      <label htmlFor="editEnd">Кінець</label>
                      <input
                        type="time"
                        name="end_time"
                        id="editEnd"
                        value={editData.end_time}
                        onChange={handleEditChange}
                        required
                      />
                    </div>
                  </div>
                  <div className="field-row">
                    <div className="field-group">
                      <label htmlFor="editPrice">Ціна</label>
                      <input
                        type="number"
                        step="0.01"
                        min="0"
                        name="price"
                        id="editPrice"
                        value={editData.price}
                        onChange={handleEditChange}
                        required
                      />
                    </div>
                    <div className="field-group">
                      <label htmlFor="editCurrency">Валюта</label>
                      <select
                        name="currency"
                        id="editCurrency"
                        value={editData.currency}
                        onChange={handleEditChange}
                        required
                      >
                        <option value="UAH">UAH</option>
                        <option value="EUR">EUR</option>
                        <option value="USD">USD</option>
                      </select>
                    </div>
                  </div>
                  <div className="field-row">
                    <div className="field-group">
                      <label htmlFor="editCategory">Категорія</label>
                      <select
                        name="category"
                        id="editCategory"
                        value={editData.category}
                        onChange={handleEditChange}
                        required
                      >
                        <option value="">-- Виберіть категорію --</option>
                        {Object.keys(sportCategories).map(cat => (
                          <option key={cat} value={cat}>{cat}</option>
                        ))}
                      </select>
                    </div>
                    <div className="field-group">
                      <label htmlFor="editSection">Секція</label>
                      <select
                        name="section"
                        id="editSection"
                        value={editData.section}
                        onChange={handleEditChange}
                        disabled={!editData.category}
                        required
                      >
                        <option value="">-- Виберіть секцію --</option>
                        {editData.category &&
                          sportCategories[editData.category].map(s => (
                            <option key={s} value={s}>{s}</option>
                          ))
                        }
                      </select>
                    </div>
                  </div>
                  <div className="field-group">
                    <label htmlFor="editSpots">Місць</label>
                    <input
                      type="number"
                      min="0"
                      name="spots"
                      id="editSpots"
                      value={editData.spots}
                      onChange={handleEditChange}
                      required
                    />
                  </div>
                  {errors.edit && (
                    <p className="error-message">{errors.edit}</p>
                  )}
                  <footer className="edit-modal-modern__footer">
                    <button
                      type="button"
                      className="btn-cancel"
                      onClick={cancelEdit}
                    >
                      Відмінити
                    </button>
                    <button
                      type="submit"
                      className="btn-save"
                    >
                      Зберегти
                    </button>
                  </footer>
                </form>
              </div>
            ) : (
              /* — РЕЖИМ ПЕРЕГЛЯДУ — */
              <div className="training-participants-modal">
                <div className="modal-left">
                  <h3 className="block-title">Загальна інформація</h3>
                  <div className="general-info">
                    {sportLogos[selectedTraining.section] && (
                      <img
                        src={sportLogos[selectedTraining.section]}
                        alt={selectedTraining.section}
                        className="section-logo-lg"
                      />
                    )}
                    <div className="general-info-text">
                      <p><strong>Секція:</strong> {selectedTraining.section}</p>
                      <p><strong>Дата:</strong> {formatDate(selectedTraining.start_time)}</p>
                      <p><strong>Час:</strong> {formatTime(selectedTraining.start_time)} – {formatTime(selectedTraining.end_time)}</p>
                      <p>
                        Записано: {participants.length} / {participants.length + selectedTraining.spots}
                      </p>
                    </div>
                  </div>
                  <h3 className="block-title">Фінансовий аналіз</h3>
                  <ul className="financial-list">
                    <li><strong>Ціна:</strong> {selectedTraining.price} {selectedTraining.currency}</li>
                    <li><strong>Макс. прибуток:</strong> {((participants.length + parseInt(selectedTraining.spots,10)) * selectedTraining.price).toFixed(2)} {selectedTraining.currency}</li>
                    <li><strong>Заповненість:</strong> {Math.round((participants.length / (participants.length + parseInt(selectedTraining.spots,10))) * 100)}%</li>
                    <li><strong>Очікуваний прибуток:</strong> {(participants.length * selectedTraining.price).toFixed(2)} {selectedTraining.currency}</li>
                  </ul>
                </div>
                <div className="modal-right">
                  <h3 className="block-title">Учасники</h3>
                  <div className="participants-list">
                    {participants.length > 0 ? participants.map((p, idx) => (
                      <div key={idx} className="participant-item">
                        <img
                          src={p.avatar_url}
                          alt={p.name}
                          className="participant-avatar"
                          onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = defaultAvatar; }}
                        />
                        <div className="participant-details">
                          <button
                            className="link-button"
                            onClick={() => handleViewAthlete(p.user_id)}
                          >
                            {p.name}
                          </button>
                          <p className="participant-age">Вік: {p.age}</p>
                        </div>
                      </div>
                    )) : (
                      <p className="no-participants"><em>Ніхто не записався.</em></p>
                    )}
                  </div>
                  <div className="participants-actions">
                    <button
                      className="modal-edit-btn"
                      onClick={handleEdit}
                    >
                      Редагувати
                    </button>
                  </div>
                </div>
              </div>
            )
          }
        />
      )}
    </div>
  );
}
