import React from 'react';
import '../../styles/Modal.css';

const Modal = ({ message, onClose, modalType }) => {
  // Закриваємо модальне вікно, якщо клік поза ним
  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Визначаємо клас для контейнера модального вікна залежно від типу
  const modalClass =
    modalType === 'success'
      ? 'modal-content success-modal'
      : modalType === 'info'
      ? 'modal-content info-modal'
      : modalType === 'edit'
      ? 'modal-content edit-modal'
      : 'modal-content';

  // Для кнопки закриття, якщо це success, використовується зелена кнопка
  const closeButtonClass =
    modalType === 'success' ? 'modal-success-btn' : 'modal-close-btn';

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className={modalClass} onClick={(e) => e.stopPropagation()}>
        {message}
        <button className={closeButtonClass} onClick={onClose}>
          ОК
        </button>
      </div>
    </div>
  );
};

export default Modal;
