import React from 'react';
import './NavigationPanel.css';

/**
 * Навігаційна панель Кабінету Тренера
 *
 * @param {'my-trainings'|'profile'|'reviews'} activeTab — яка вкладка відкрита
 * @param {(tab: 'my-trainings'|'profile'|'reviews') => void} onTabChange — колбек зміни вкладки
 */
export default function NavigationPanel({ activeTab, onTabChange }) {
  return (
    <nav className="navigation-panel">
      <button
        className={`tab ${activeTab === 'my-trainings' ? 'active' : ''}`}
        onClick={() => onTabChange('my-trainings')}
      >
        Ваші тренування
      </button>

      <button
        className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
        onClick={() => onTabChange('profile')}
      >
        Ваш профіль
      </button>

      <button
        className={`tab ${activeTab === 'reviews' ? 'active' : ''}`}
        onClick={() => onTabChange('reviews')}
      >
        Ваш рейтинг
      </button>
    </nav>
  );
}
