import React, { useState } from 'react';
import { registerUser, loginUser } from '../../services/api';
import Modal from '../../components/Common/Modal';
import { useHistory } from 'react-router-dom';

const AuthPage = () => {
  // Режим роботи: 'register' або 'login'
  const [mode, setMode] = useState('register');
  // Дані форми – початковий стан має однакову структуру для обох режимів
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    email: '',
    password: '',
    section: ''
  });
  // Тип користувача для режиму реєстрації ('athlete' або 'coach')
  const [userType, setUserType] = useState('athlete');
  // Стан для повідомлень про помилки
  const [error, setError] = useState('');
  // Стан модального вікна
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  // Стан для збереження ролі (отриманої при логіні)
  const [role, setRole] = useState('');
  const history = useHistory();

  // Обробка зміни значень інпутів
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Обробка реєстрації
  const handleRegister = async (e) => {
    e.preventDefault();
    if (formData.password.length < 6) {
      setError('Пароль має містити не менше 6 символів.');
      return;
    }
    const payload = {
      type: userType,
      data: {
        name: formData.name,
        age: parseInt(formData.age),
        email: formData.email,
        password: formData.password,
        ...(userType === 'coach' && { section: formData.section })
      }
    };
    try {
      await registerUser(payload);
      setModalMessage(
        `Акаунт типу ${userType === 'athlete' ? 'Спортсмен' : 'Тренер'} успішно зареєстрований!`
      );
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  // Обробка авторизації
  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await loginUser({
        email: formData.email,
        password: formData.password
      });
      setModalMessage(
        `Ваш акаунт успішно авторизовано! Ласкаво просимо до порталу, ${res.name}!`
      );
      setRole(res.role);
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  // Обробка закриття модального вікна:
  // Якщо режим – реєстрація, перемикаємо режим на авторизацію та очищуємо форму.
  // Якщо режим – авторизація, перенаправляємо користувача до відповідного кабінету.
  const handleModalClose = () => {
    setShowModal(false);
    if (mode === 'register') {
      setMode('login');
      setError('');
      setFormData({
        name: '',
        age: '',
        email: '',
        password: '',
        section: ''
      });
    } else if (mode === 'login') {
      if (role === 'athlete') {
        history.push('/athlete-dashboard');
      } else if (role === 'coach') {
        history.push('/coach-dashboard');
      }
    }
  };

  return (
    <div className="landing-container">
      <div className="welcome-text">
        <h1>Ласкаво просимо до Порталу Тренувань!</h1>
        <p>Найкращий керівник твоєго спортивного життя</p>
      </div>
      <div className="form-container">
        {mode === 'register' ? (
          <>
            <div className="form-header" style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '15px' }}>
              <h2 className="title" style={{ margin: 0, fontSize: '2rem' }}>Реєстрація</h2>
              <div className="user-type-container">
                <div className="user-type-switch">
                  <button
                    onClick={() => setUserType('athlete')}
                    className={userType === 'athlete' ? 'selected' : ''}
                  >
                    Спортсмен
                  </button>
                  <button
                    onClick={() => setUserType('coach')}
                    className={userType === 'coach' ? 'selected' : ''}
                  >
                    Тренер
                  </button>
                </div>
                <small className="user-type-label">Оберіть бажаний тип користувача</small>
              </div>
            </div>
            <form onSubmit={handleRegister}>
              <input
                type="text"
                name="name"
                placeholder="Ім'я"
                value={formData.name}
                onChange={handleChange}
                required
              />
              <input
                type="number"
                name="age"
                placeholder="Вік"
                value={formData.age}
                onChange={handleChange}
                required
              />
              {userType === 'coach' && (
                <input
                  type="text"
                  name="section"
                  placeholder="Секція"
                  value={formData.section}
                  onChange={handleChange}
                  required
                />
              )}
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Пароль"
                value={formData.password}
                onChange={handleChange}
                required
              />
              {error && <p className="error-message">{error}</p>}
              <button type="submit">Зареєструватися</button>
            </form>
            <div className="toggle-container">
              <span>Вже маєте акаунт?</span>
              <span
                className="toggle-link"
                onClick={() => {
                  setMode('login');
                  setError('');
                  setFormData({
                    name: '',
                    age: '',
                    email: '',
                    password: '',
                    section: ''
                  });
                }}
              >
                Увійдіть
              </span>
            </div>
          </>
        ) : (
          <>
            <h2>Авторизація</h2>
            <form onSubmit={handleLogin}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Пароль"
                value={formData.password}
                onChange={handleChange}
                required
              />
              {error && <p className="error-message">{error}</p>}
              <button type="submit">Увійти</button>
            </form>
            <div className="toggle-container">
              <span>Ще не маєте акаунт?</span>
              <span
                className="toggle-link"
                onClick={() => {
                  setMode('register');
                  setError('');
                  setFormData({
                    name: '',
                    age: '',
                    email: '',
                    password: '',
                    section: ''
                  });
                }}
              >
                Зареєструватися
              </span>
            </div>
          </>
        )}
        {showModal && <Modal message={modalMessage} onClose={handleModalClose} />}
      </div>
    </div>
  );
};

export default AuthPage;
