import React from 'react';

const Modal = ({ message, onClose, modalType }) => {
  // Якщо клік поза модальним вікном – закриваємо його
  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Визначаємо клас модального вікна залежно від типу
  const modalClass = modalType === 'info' ? 'modal-content info-modal' : 'modal-content';

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className={modalClass}>
        {message}
        <button onClick={onClose}>Ок</button>
      </div>
    </div>
  );
};

export default Modal;
