import React, { useState, useEffect } from 'react';
import { createTraining, getTrainings } from '../../services/api';
import Modal from '../Common/Modal';
import '../../styles/CoachDashboard.css';

const CoachDashboard = () => {
  const [trainingData, setTrainingData] = useState({
    date: '',
    start_time: '',
    end_time: '',
    price: '',
    spots: '',
    currency: 'UAH'
  });
  const [errors, setErrors] = useState({});
  const [modalMessage, setModalMessage] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [trainings, setTrainings] = useState([]);

  // Отримуємо сьогоднішню дату у форматі YYYY-MM-DD
  const today = new Date().toISOString().split('T')[0];

  // Функція для мінімального часу: якщо обрана дата — сьогодні, повертаємо поточний час, інакше "00:00"
  const getMinTime = () => {
    if (trainingData.date === today) {
      const now = new Date();
      let hours = now.getHours();
      let minutes = now.getMinutes();
      hours = hours < 10 ? `0${hours}` : hours;
      minutes = minutes < 10 ? `0${minutes}` : minutes;
      return `${hours}:${minutes}`;
    }
    return "00:00";
  };

  // Функція для форматування часу (HH:MM)
  const formatTime = (timeString) => {
    const date = new Date(timeString);
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  // Групування тренувань за датою (формат dd.mm.yyyy)
  const groupTrainingsByDate = (trainings) => {
    const groups = {};
    trainings.forEach((training) => {
      const dateObj = new Date(training.start_time);
      const formattedDate = dateObj.toLocaleDateString('uk-UA', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      });
      if (!groups[formattedDate]) {
        groups[formattedDate] = [];
      }
      groups[formattedDate].push(training);
    });
    return groups;
  };

  const handleChange = (e) => {
    setTrainingData({ ...trainingData, [e.target.name]: e.target.value });
  };

  const fetchTrainings = async () => {
    const coachEmail = localStorage.getItem('userEmail');
    if (coachEmail) {
      try {
        const data = await getTrainings(coachEmail);
        setTrainings(data.trainings);
      } catch (error) {
        console.error("Помилка отримання тренувань:", error.message);
      }
    }
  };

  useEffect(() => {
    fetchTrainings();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrors({});
    const coachEmail = localStorage.getItem('userEmail');
    if (!coachEmail) {
      setErrors({ general: 'Не знайдено даних про тренера. Будь ласка, увійдіть повторно.' });
      return;
    }
    if (!trainingData.date || !trainingData.start_time || !trainingData.end_time) {
      setErrors({ general: 'Будь ласка, оберіть дату та час проведення тренування.' });
      return;
    }
    if (trainingData.start_time >= trainingData.end_time) {
      setErrors({ end_time: "Час завершення не може бути раніше часу початку" });
      return;
    }

    const startDateTime = trainingData.date + "T" + trainingData.start_time;
    const endDateTime = trainingData.date + "T" + trainingData.end_time;

    const startDT = new Date(startDateTime);
    const now = new Date();
    if (startDT < now) {
      setErrors({ general: "Неможливо створити тренування для минулої дати або часу" });
      return;
    }

    const payload = {
      email: coachEmail,
      start_time: startDateTime,
      end_time: endDateTime,
      price: parseFloat(trainingData.price),
      spots: parseInt(trainingData.spots, 10),
      currency: trainingData.currency
    };

    try {
      await createTraining(payload);
      setModalMessage('Тренування успішно створено!');
      setShowModal(true);
      setTrainingData({
        date: '',
        start_time: '',
        end_time: '',
        price: '',
        spots: '',
        currency: 'UAH'
      });
      fetchTrainings();
    } catch (err) {
      if (typeof err === 'object') {
        setErrors({ general: err.message || "Сталася помилка" });
      } else {
        setErrors({ general: err.toString() });
      }
    }
  };

  const handleModalClose = () => setShowModal(false);

  const groupedTrainings = groupTrainingsByDate(trainings);
  const sortedDates = Object.keys(groupedTrainings).sort((a, b) => {
    const [dayA, monthA, yearA] = a.split('.');
    const [dayB, monthB, yearB] = b.split('.');
    return new Date(yearA, monthA - 1, dayA) - new Date(yearB, monthB - 1, dayB);
  });

  return (
    <div className="dashboard-container">
      <h1>Кабінет Тренера</h1>
      <div className="form-wrapper">
        <h2>Створення тренування</h2>
        <p>Заповніть форму нижче, щоб створити нове тренування.</p>
        {errors.general && <p className="error-message">{errors.general}</p>}
        <form onSubmit={handleSubmit} className="create-training-form">
          <div className="form-row">
            <label htmlFor="date">Дата проведення:</label>
            <input
              type="date"
              name="date"
              id="date"
              value={trainingData.date}
              onChange={handleChange}
              min={today}
              required
            />
          </div>
          <div className="form-row time-group">
            <div className="time-field">
              <label htmlFor="start_time">Початок:</label>
              <input
                type="time"
                name="start_time"
                id="start_time"
                value={trainingData.start_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
            </div>
            <div className="time-field">
              <label htmlFor="end_time">Завершення:</label>
              <input
                type="time"
                name="end_time"
                id="end_time"
                value={trainingData.end_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
              {errors.end_time && <p className="error-message">{errors.end_time}</p>}
            </div>
          </div>
          <div className="form-row price-currency-group">
            <div className="price-group">
              <label htmlFor="price">Ціна:</label>
              <input
                type="number"
                step="0.01"
                min="0"
                name="price"
                id="price"
                placeholder="Введіть ціну"
                value={trainingData.price}
                onChange={handleChange}
                required
              />
              {errors.price && <p className="error-message">{errors.price}</p>}
            </div>
            <div className="currency-group">
              <label htmlFor="currency">Валюта:</label>
              <select
                name="currency"
                id="currency"
                value={trainingData.currency}
                onChange={handleChange}
                required
              >
                <option value="UAH">UAH</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="spots">Доступні місця:</label>
            <input
              type="number"
              min="0"
              name="spots"
              id="spots"
              placeholder="Введіть кількість місць"
              value={trainingData.spots}
              onChange={handleChange}
              required
            />
            {errors.spots && <p className="error-message">{errors.spots}</p>}
          </div>
          <button type="submit" className="submit-btn">Створити тренування</button>
        </form>
      </div>

      {/* Секція "Ваші тренування" групуємо за датою */}
      <div className="trainings-wrapper">
        <h2>Ваші тренування</h2>
        {sortedDates.length === 0 ? (
          <p>Немає створених тренувань.</p>
        ) : (
          sortedDates.map((date) => (
            <div key={date} className="training-group">
              <h3 className="training-date-header">{date}</h3>
              <ul className="trainings-list">
                {groupedTrainings[date].map((t) => {
                  const startTime = formatTime(t.start_time);
                  const endTime = formatTime(t.end_time);
                  return (
                    <li key={t._id} className="training-item">
                      {startTime} - {endTime}, вільних місць: {t.spots}
                    </li>
                  );
                })}
              </ul>
            </div>
          ))
        )}
      </div>

      {showModal && <Modal message={modalMessage} onClose={handleModalClose} />}
    </div>
  );
};

export default CoachDashboard;
