import React, { useState, useEffect, useRef } from 'react';
import {
  createTraining,
  getCoachTrainings,
  getTrainingParticipants,
  updateTraining,
  deleteTraining,
  getCoachProfile,
  getAthleteProfile,
  updateCoachProfile,
  changeCoachPassword,
  uploadCoachAvatar,
  getCoachReviews, API_BASE_URL
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/CoachDashboard.css';

// –õ–æ–≥–æ—Ç–∏–ø–∏ —Å–µ–∫—Ü—ñ–π
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// –ú–∞–ø–∞ —Å–µ–∫—Ü—ñ—è ‚Üí –ª–æ–≥–æ—Ç–∏–ø
const sportLogos = {
  "–§—É—Ç–±–æ–ª": footballLogo,
  "–ë–∞—Å–∫–µ—Ç–±–æ–ª": basketballLogo,
  "–ë–∞–¥–º—ñ–Ω—Ç–æ–Ω": badmintonLogo,
  "–í–æ–ª–µ–π–±–æ–ª": volleyballLogo,
  "–ë–µ–π—Å–±–æ–ª": baseballLogo,
  "–†–µ–≥–±—ñ": rugbyLogo,
  "–•–æ–∫–µ–π": hockeyLogo,
  "–ö–∞—Ä–∞—Ç–µ": karateLogo,
  "–ë–æ–∫—Å": boxingLogo,
  "–£—à—É": wushuLogo,
  "–î–∂–∏—É-–¥–∂–∏—Ç—Å—É": jiuJitsuLogo,
  "–ú–ú–ê": mmaLogo,
  "–ë—ñ–≥": runningLogo,
  "–°—Ç—Ä–∏–±–∫–∏": jumpingLogo,
  "–ú–µ—Ç–∞–Ω–Ω—è": throwingLogo,
  "–ü–ª–∞–≤–∞–Ω–Ω—è": swimmingLogo,
  "–í—ñ—Ç—Ä–∏–ª—å–Ω–∏–π —Å–ø–æ—Ä—Ç": sailingLogo,
  "–ö–∞–Ω–æ—ó–∑–º": canoeingLogo,
  "–°–∫–µ–π–±–æ—Ä–¥–∏–Ω–≥": skateboardingLogo,
  "–°–Ω–æ—É–±–æ—Ä–¥–∏–Ω–≥": snowboardingLogo,
  "–ê–ª—å–ø—ñ–Ω—ñ–∑–º": climbingLogo,
  "–§—ñ—Ç–Ω–µ—Å": fitnessLogo,
  "–°–∏–ª–æ–≤—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è": strengthTrainingLogo,
  "–ô–æ–≥–∞": yogaLogo
};

// –ö–∞—Ç–µ–≥–æ—Ä—ñ—ó —Ç–∞ —Å–µ–∫—Ü—ñ—ó
const sportCategories = {
  "–Ü–≥—Ä–∏ –∑ –º'—è—á–µ–º": ["–§—É—Ç–±–æ–ª","–ë–∞—Å–∫–µ—Ç–±–æ–ª","–ë–∞–¥–º—ñ–Ω—Ç–æ–Ω","–í–æ–ª–µ–π–±–æ–ª","–ë–µ–π—Å–±–æ–ª","–†–µ–≥–±—ñ","–•–æ–∫–µ–π"],
  "–ë–æ–π–æ–≤—ñ –º–∏—Å—Ç–µ—Ü—Ç–≤–∞": ["–ö–∞—Ä–∞—Ç–µ","–ë–æ–∫—Å","–£—à—É","–î–∂–∏—É-–¥–∂–∏—Ç—Å—É","–ú–ú–ê"],
  "–õ–µ–≥–∫–∞ –∞—Ç–ª–µ—Ç–∏–∫–∞": ["–ë—ñ–≥","–°—Ç—Ä–∏–±–∫–∏","–ú–µ—Ç–∞–Ω–Ω—è"],
  "–í–æ–¥–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É": ["–ü–ª–∞–≤–∞–Ω–Ω—è","–í—ñ—Ç—Ä–∏–ª—å–Ω–∏–π —Å–ø–æ—Ä—Ç","–ö–∞–Ω–æ—ó–∑–º"],
  "–ï–∫—Å—Ç—Ä–µ–º–∞–ª—å–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É": ["–°–∫–µ–π–±–æ—Ä–¥–∏–Ω–≥","–°–Ω–æ—É–±–æ—Ä–¥–∏–Ω–≥","–ê–ª—å–ø—ñ–Ω—ñ–∑–º"],
  "–§—ñ—Ç–Ω–µ—Å —Ç–∞ —Ç—Ä–µ–Ω–∞–∂–µ—Ä–Ω–∏–π –∑–∞–ª": ["–§—ñ—Ç–Ω–µ—Å","–°–∏–ª–æ–≤—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è","–ô–æ–≥–∞"]
};

const defaultAvatar = `${API_BASE_URL}/avatars/default.png`;

const CoachDashboard = () => {
  // ‚Äî –°—Ç–∞–Ω–∏ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è —Ç—Ä–µ–Ω—É–≤–∞–Ω—å ‚Äî
  const [trainingData, setTrainingData]   = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'UAH',
    category:'', section:''
  });
  const [errors, setErrors]               = useState({});
  const [modalMessage, setModalMessage]   = useState('');
  const [showModal, setShowModal]         = useState(false);
  const [trainings, setTrainings]         = useState([]);
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants]   = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);
  const [editMode, setEditMode]           = useState(false);
  const [editData, setEditData]           = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'',
    category:'', section:''
  });

  // ‚Äî –°—Ç–∞–Ω–∏ –ø—Ä–æ—Ñ—ñ–ª—é ‚Äî
  const [profile, setProfile]             = useState(null);
  const [editProfileMode, setEditProfileMode] = useState(false);
  const [nameInput, setNameInput]         = useState('');
  const [ageInput, setAgeInput]           = useState('');
  const [phoneInput, setPhoneInput]       = useState('');
  const [description, setDescription]     = useState('');
  const [favoriteSports, setFavoriteSports] = useState([]);
  const [profileError, setProfileError]   = useState('');
  const [showProfileSuccess, setShowProfileSuccess] = useState(false);

  // ‚Äî –°—Ç–∞–Ω–∏ –∞–≤–∞—Ç–∞—Ä–∫–∏ ‚Äî
  const [avatarFile, setAvatarFile]       = useState(null);
  const [avatarPreview, setAvatarPreview] = useState('');
  const [showAvatarSuccess, setShowAvatarSuccess] = useState(false);

  // ‚Äî –°—Ç–∞–Ω–∏ –∑–º—ñ–Ω–∏ –ø–∞—Ä–æ–ª—è ‚Äî
  const [showPassMode, setShowPassMode]   = useState(false);
  const [oldPass, setOldPass]             = useState('');
  const [newPass, setNewPass]             = useState('');
  const [confirmPass, setConfirmPass]     = useState('');
  const [passError, setPassError]         = useState('');
  const [passSuccess, setPassSuccess]     = useState(false);

  const today = new Date().toISOString().split('T')[0];
  const allSections = Object.values(sportCategories).flat();

  // –¥–ª—è –ø—ñ–¥—Ç–≤–µ—Ä–¥–∂–µ–Ω–Ω—è –≤–∏–¥–∞–ª–µ–Ω–Ω—è
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [trainingToDelete, setTrainingToDelete]   = useState(null);

  // –¥–ª—è –≤—ñ–¥–≥—É–∫—ñ–≤
  const [reviews, setReviews] = useState([]);
  const [avgRating, setAvgRating] = useState(null);
  const [reviewCount, setReviewCount] = useState(0);
  const reviewSectionRef = useRef(null);

  // –¥–ª—è –º–æ–¥–∞–ª–æ–∫
  const [showAthleteModal, setShowAthleteModal] = useState(false);
  const [athleteProfile, setAthleteProfile]         = useState(null);

  // –§–æ—Ä–º–∞—Ç–µ—Ä–∏
  const formatDate = t => new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});

  // ‚Äî Fetch —Ç—Ä–µ–Ω—É–≤–∞–Ω—å —ñ –ø—Ä–æ—Ñ—ñ–ª—é ‚Äî
  const fetchTrainings = async () => {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { trainings } = await getCoachTrainings(coachId);
      setTrainings(trainings);
    } catch(err) {
      console.error(err);
    }
  };
  const fetchProfile = async () => {
  const coachId = localStorage.getItem('userId');
  if (!coachId) return;

  try {
    // 1) –ó–∞–≤–∞–Ω—Ç–∞–∂—É—î–º–æ –¥–∞–Ω—ñ –ø—Ä–æ—Ñ—ñ–ª—é —Ç—Ä–µ–Ω–µ—Ä–∞
    const { profile } = await getCoachProfile(coachId);
    setProfile(profile);
    setNameInput(profile.name);
    setAgeInput(profile.age);
    setPhoneInput(profile.phone || '');
    setDescription(profile.description || '');
    setFavoriteSports(profile.favorite_sports || []);
    // —â–æ–± –æ–±—ñ–π—Ç–∏ –∫–µ—à, –¥–æ–¥–∞—î–º–æ —Ç–∞–π–º—Å—Ç–µ–º–ø
    const src = profile.avatar_url
      ? `${profile.avatar_url}?t=${Date.now()}`
      : `${API_BASE_URL}/avatars/default.png`;
    setAvatarPreview(src);

    // 2) –ó–∞–≤–∞–Ω—Ç–∞–∂—É—î–º–æ —É—Å—ñ –≤—ñ–¥–≥—É–∫–∏ —Ç—Ä–µ–Ω–µ—Ä–∞
    const { reviews, average_rating, count } = await getCoachReviews(coachId);

    // 3) –î–ª—è –∫–æ–∂–Ω–æ–≥–æ –≤—ñ–¥–≥—É–∫—É –ø—ñ–¥—Ç—è–≥—É—î–º–æ –∞–≤–∞—Ç–∞—Ä–∫—É —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–∞
    const reviewsWithAvatars = await Promise.all(
      reviews.map(async r => {
        if (!r.athlete_id) {
          return { ...r, athlete_avatar_url: null };
        }
        try {
          const { profile: athlete } = await getAthleteProfile(r.athlete_id);
          return {
            ...r,
            athlete_avatar_url: athlete.avatar_url || null
          };
        } catch {
          return { ...r, athlete_avatar_url: null };
        }
      })
    );

    // 4) –ó–±–µ—Ä—ñ–≥–∞—î–º–æ –≤—ñ–¥–≥—É–∫–∏ –∑ –∞–≤–∞—Ç–∞—Ä–∫–∞–º–∏
    setReviews(reviewsWithAvatars);
    setAvgRating(average_rating);
    setReviewCount(count);

  } catch (err) {
    console.error('–ü–æ–º–∏–ª–∫–∞ –ø—Ä–∏ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—ñ –ø—Ä–æ—Ñ—ñ–ª—é/–≤—ñ–¥–≥—É–∫—ñ–≤:', err);
  }
  };

  useEffect(() => {
    fetchTrainings();
    fetchProfile();
  }, []);

  // ‚Äî –û–±—Ä–æ–±–Ω–∏–∫–∏ –ø—Ä–æ—Ñ—ñ–ª—é ‚Äî
  const toggleSport = sport => {
    setFavoriteSports(prev =>
      prev.includes(sport)
        ? prev.filter(s => s !== sport)
        : [...prev, sport]
    );
  };

// ‚Äî –û–±—Ä–æ–±–Ω–∏–∫ –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è –ø—Ä–æ—Ñ—ñ–ª—é —Ç—Ä–µ–Ω–µ—Ä–∞ –∑ –≤–∞–ª—ñ–¥–∞—Ü—ñ—î—é ‚Äî
const handleSaveProfile = async () => {
  // –°–∫–∏–¥–∞—î–º–æ –ø–æ–ø–µ—Ä–µ–¥–Ω—é –ø–æ–º–∏–ª–∫—É
  setProfileError('');

  // –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞: –Ü–º'—è –æ–±–æ–≤'—è–∑–∫–æ–≤–µ
  if (!nameInput.trim()) {
    setProfileError('–ü–æ–ª–µ "–Ü–º\'—è" –æ–±–æ–≤\'—è–∑–∫–æ–≤–µ');
    return;
  }

  // –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞: –í—ñ–∫ –æ–±–æ–≤'—è–∑–∫–æ–≤–∏–π —Ç–∞ —á–∏—Å–ª–æ–≤–∏–π
  const ageNum = parseInt(ageInput, 10);
  if (isNaN(ageNum)) {
    setProfileError('–ü–æ–ª–µ "–í—ñ–∫" –æ–±–æ–≤\'—è–∑–∫–æ–≤–µ');
    return;
  }
  // –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞: –í—ñ–∫ –Ω–µ –º–æ–∂–µ –±—É—Ç–∏ –≤—ñ–¥‚Äô—î–º–Ω–∏–º
  if (ageNum < 0) {
    setProfileError('–í—ñ–∫ –Ω–µ –º–æ–∂–µ –±—É—Ç–∏ –≤—ñ–¥‚Äô—î–º–Ω–∏–º');
    return;
  }
  // –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞: –í—ñ–∫ –Ω–µ –º–æ–∂–µ –±—É—Ç–∏ –º–µ–Ω—à–µ 18
  if (ageNum < 18) {
    setProfileError('–í—ñ–∫ –Ω–µ –º–æ–∂–µ –±—É—Ç–∏ –º–µ–Ω—à–µ 18 —Ä–æ–∫—ñ–≤');
    return;
  }

  // –Ø–∫—â–æ –≤—Å—ñ –ø–µ—Ä–µ–≤—ñ—Ä–∫–∏ –ø—Ä–æ–π–¥–µ–Ω—ñ ‚Äî –≤—ñ–¥–ø—Ä–∞–≤–ª—è—î–º–æ –Ω–∞ —Å–µ—Ä–≤–µ—Ä
  try {
    await updateCoachProfile({
      user_id: profile.user_id,
      name: nameInput.trim(),
      age: ageNum,
      phone: phoneInput,
      description,
      favorite_sports: favoriteSports
    });
    // –ü—ñ—Å–ª—è —É—Å–ø—ñ—Ö—É ‚Äî –∑–∞–∫—Ä–∏–≤–∞—î–º–æ —Ä–µ–∂–∏–º —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è, –ø–æ–∫–∞–∑—É—î–º–æ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è —ñ –æ–Ω–æ–≤–ª—é—î–º–æ –ø—Ä–æ—Ñ—ñ–ª—å
    setShowProfileSuccess(true);
    setEditProfileMode(false);
    fetchProfile();
  } catch (err) {
    // –Ø–∫—â–æ —Å–µ—Ä–≤–µ—Ä –ø–æ–≤–µ—Ä–Ω—É–≤ –ø–æ–º–∏–ª–∫—É ‚Äî –≤—ñ–¥–æ–±—Ä–∞–∂–∞—î–º–æ —ó—ó
    setProfileError(err.message);
  }
};

