                    <button onClick={() => setEditProfileMode(true)} className="btn-primary">–†–µ–¥–∞–≥—É–≤–∞—Ç–∏</button>
                    <button onClick={() => setShowPassMode(true)} className="btn-secondary">–ó–º—ñ–Ω–∏—Ç–∏ –ø–∞—Ä–æ–ª—å</button>
                  </div>
                </>
              )}
            </div>
          </div>

          {/* === –§–æ—Ä–º–∞ –∑–º—ñ–Ω–∏ –ø–∞—Ä–æ–ª—è === */}
          {showPassMode && (
            <div className="password-form">
              <label>–°—Ç–∞—Ä–∏–π –ø–∞—Ä–æ–ª—å:</label>
              <input type="password" value={oldPass} onChange={e => setOldPass(e.target.value)} />

              <label>–ù–æ–≤–∏–π –ø–∞—Ä–æ–ª—å:</label>
              <input type="password" value={newPass} onChange={e => setNewPass(e.target.value)} />

              <label>–ü—ñ–¥—Ç–≤–µ—Ä–¥—ñ—Ç—å –Ω–æ–≤–∏–π –ø–∞—Ä–æ–ª—å:</label>
              <input type="password" value={confirmPass} onChange={e => setConfirmPass(e.target.value)} />

              {passError && <p className="error-message">{passError}</p>}

              <div className="profile-actions">
                <button onClick={handleChangePassword} className="btn-primary">–ó–º—ñ–Ω–∏—Ç–∏</button>
                <button
                className="btn-secondary"
                onClick={() => {
                  // –•–æ–≤–∞—î–º–æ —Ñ–æ—Ä–º—É
                  setShowPassMode(false);
                  // –û—á–∏—â–∞—î–º–æ –ø–æ–ª—è —ñ –ø–æ–º–∏–ª–∫–∏
                  setPassError("");
                  setOldPass("");
                  setNewPass("");
                  setConfirmPass("");
                }}
              >
                –í—ñ–¥–º—ñ–Ω–∏—Ç–∏
              </button>
              </div>
            </div>
          )}
          {showAvatarSuccess && (
              <Modal
                modalType="success"
                message="–§–æ—Ç–æ —É—Å–ø—ñ—à–Ω–æ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–æ!"
                onClose={() => setShowAvatarSuccess(false)}
              />
            )}

          {passSuccess && (
            <Modal modalType="success" message="–ü–∞—Ä–æ–ª—å —É—Å–ø—ñ—à–Ω–æ –∑–º—ñ–Ω–µ–Ω–æ!" onClose={() => setPassSuccess(false)} />
          )}
          {showProfileSuccess && (
            <Modal modalType="success" message="–ü—Ä–æ—Ñ—ñ–ª—å –æ–Ω–æ–≤–ª–µ–Ω–æ!" onClose={() => setShowProfileSuccess(false)} />
          )}

          {/* === –í—ñ–¥–≥—É–∫–∏ –ø—Ä—è–º–æ –Ω–∞ —Å—Ç–æ—Ä—ñ–Ω—Ü—ñ === */}
          <section ref={reviewSectionRef} className="profile-reviews-section">
          <h3>–í—ñ–¥–≥—É–∫–∏ ({reviewCount})</h3>
          {reviews.length > 0 ? (
            <div className="reviews-list">
              {reviews.map(r => {
                const avatarSrc = r.athlete_avatar_url
                  ? (r.athlete_avatar_url.startsWith('http')
                      ? r.athlete_avatar_url
                      : `${API_BASE_URL}${r.athlete_avatar_url}`)
                  : defaultAvatar;

                return (
                  <div key={r.id} className="review-card">
                    <div className="review-card-header">
                      <div className="review-user-info">
                        <img
                          src={avatarSrc}
                          alt="–ê–≤–∞—Ç–∞—Ä —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–∞"
                          className="reviewer-avatar-small"
                          onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = defaultAvatar; }}
                        />
                        <span className="review-username">
                          {r.athlete_name || '–ê–Ω–æ–Ω—ñ–º'}
                        </span>
                      </div>
                      <div className="review-meta">
                        <span className="review-rating">
                          {'‚òÖ'.repeat(r.rating) + '‚òÜ'.repeat(5 - r.rating)}
                        </span>
                        <small className="review-date">
                          {new Date(r.created_at).toLocaleDateString('uk-UA', {
                            day: '2-digit', month: '2-digit', year: 'numeric'
                          })}
                        </small>
                      </div>
                    </div>
                    <div className="review-text">{r.comment}</div>
                  </div>
                );
              })}
            </div>
          ) : (
            <p><em>–ù–µ–º–∞—î –≤—ñ–¥–≥—É–∫—ñ–≤</em></p>
          )}
        </section>

        </>
      ) : (
        <p>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –ø—Ä–æ—Ñ—ñ–ª—é‚Ä¶</p>
      )}
    </section>

            {/* –ü—ñ–¥—Ç–≤–µ—Ä–¥–∂–µ–Ω–Ω—è –≤–∏–¥–∞–ª–µ–Ω–Ω—è */}
      {showDeleteConfirm && trainingToDelete && (
        <Modal
          modalType="confirm"
          onClose={cancelDelete}
          message={
            <div style={{ textAlign: 'center', padding: '1em' }}>
              <p>
                –í–∏ –≤–ø–µ–≤–Ω–µ–Ω—ñ, —â–æ —Ö–æ—á–µ—Ç–µ –≤–∏–¥–∞–ª–∏—Ç–∏ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è
                <strong>  {trainingToDelete.section} </strong>
                –≤—ñ–¥ {formatDate(trainingToDelete.start_time)}?
              </p>
              <div style={{ display: 'flex', gap: '1em', justifyContent: 'center' }}>
                <button className="submit-btn save-btn" onClick={confirmDelete}>
                  –¢–∞–∫
                </button>
                <button className="cancel-btn" onClick={cancelDelete}>
                  –ù—ñ
                </button>
              </div>
            </div>
          }
        />
      )}

      {/* –ú–æ–¥–∞–ª–∫–∞ —Ä–µ–∑—É–ª—å—Ç–∞—Ç—É –≤–∏–¥–∞–ª–µ–Ω–Ω—è */}
      {showModal && (
        <Modal
          modalType="success"
          message={modalMessage}
          onClose={() => setShowModal(false)}
        />
      )}


      {/* –ú–æ–¥–∞–ª—å–Ω—ñ –≤—ñ–∫–Ω–∞ –¥–ª—è –ø–µ—Ä–µ–≥–ª—è–¥—É/—Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è —Ç—Ä–µ–Ω—É–≤–∞–Ω—å */}
    {showParticipantsModal && selectedTraining && (
  <Modal
    modalType={editMode ? "edit" : "info"}
    onClose={closeParticipantsModal}
    className="participants-modal"
    message={
      editMode ? (
        /* ‚Äî –†–ï–ñ–ò–ú –†–ï–î–ê–ì–£–í–ê–ù–ù–Ø ‚Äî */
              <form onSubmit={handleEditSubmit} className="edit-training-form">
        {/* ===== –ë–ª–æ–∫ 1: –î–∞—Ç–∞ —Ç–∞ —á–∞—Å ===== */}
        <div className="form-block datetime-block" data-block-title="–î–∞—Ç–∞ —Ç–∞ —á–∞—Å">
          <div className="form-row date-row">
            <label htmlFor="editDate">–î–∞—Ç–∞</label>
            <input
              type="date"
              name="date"
              id="editDate"
              value={editData.date}
              onChange={handleEditChange}
              required
            />
          </div>

          <div className="form-row time-group">
            <div className="time-field">
              <label htmlFor="editStart">–ü–æ—á–∞—Ç–æ–∫</label>
              <input
                type="time"
                name="start_time"
                id="editStart"
                value={editData.start_time}
                onChange={handleEditChange}
                required
              />
            </div>
            <div className="time-field">
              <label htmlFor="editEnd">–ö—ñ–Ω–µ—Ü—å</label>
              <input
                type="time"
                name="end_time"
                id="editEnd"
                value={editData.end_time}
                onChange={handleEditChange}
                required
              />
            </div>
          </div>
        </div>

        {/* ===== –ë–ª–æ–∫ 2: –¶—ñ–Ω–∞ —Ç–∞ –≤–∞–ª—é—Ç–∞ ===== */}
        <div className="form-block price-block" data-block-title="–¶—ñ–Ω–∞ —Ç–∞ –≤–∞–ª—é—Ç–∞">
          <div className="form-row price-currency-group">
            <div className="price-group">
              <label htmlFor="editPrice">–¶—ñ–Ω–∞</label>
              <input
                type="number"
                step="0.01"
                min="0"
                name="price"
                id="editPrice"
                value={editData.price}
                onChange={handleEditChange}
                required
              />
            </div>
            <div className="currency-group">
              <label htmlFor="editCurrency">–í–∞–ª—é—Ç–∞</label>
              <select
                name="currency"
                id="editCurrency"
                value={editData.currency}
                onChange={handleEditChange}
                required
              >
                <option value="UAH">UAH</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </div>
          </div>
        </div>

        {/* ===== –ë–ª–æ–∫ 3: –ö–∞—Ç–µ–≥–æ—Ä—ñ—è —Ç–∞ —Å–µ–∫—Ü—ñ—è ===== */}
        <div className="form-block category-block" data-block-title="–ö–∞—Ç–µ–≥–æ—Ä—ñ—è —Ç–∞ —Å–µ–∫—Ü—ñ—è">
          <div className="form-row category-section-group">
            <div className="category-field">
              <label htmlFor="editCategory">–ö–∞—Ç–µ–≥–æ—Ä—ñ—è</label>
              <select
                name="category"
                id="editCategory"
                value={editData.category}
                onChange={handleEditChange}
                required
              >
                <option value="">-- –û–±–µ—Ä—ñ—Ç—å –∫–∞—Ç–µ–≥–æ—Ä—ñ—é --</option>
                {Object.keys(sportCategories).map(cat => (
                  <option key={cat} value={cat}>{cat}</option>
                ))}
              </select>
            </div>
            <div className="section-field">
              <label htmlFor="editSection">–°–µ–∫—Ü—ñ—è</label>
              <select
                name="section"
                id="editSection"
                value={editData.section}
                onChange={handleEditChange}
                disabled={!editData.category}
                required
              >
                <option value="">-- –û–±–µ—Ä—ñ—Ç—å —Å–µ–∫—Ü—ñ—é --</option>
                {editData.category && sportCategories[editData.category].map(s => (
                  <option key={s} value={s}>{s}</option>
                ))}
              </select>
            </div>
          </div>
        </div>

        {/* ===== –ë–ª–æ–∫ 4: –ö—ñ–ª—å–∫—ñ—Å—Ç—å –º—ñ—Å—Ü—å ===== */}
        <div className="form-block spots-block" data-block-title="–ö—ñ–ª—å–∫—ñ—Å—Ç—å –º—ñ—Å—Ü—å">
          <div className="form-row spots-row">
            <label htmlFor="editSpots">–ú—ñ—Å—Ü—å</label>
            <input
              type="number"
              min="0"
              name="spots"
              id="editSpots"
              value={editData.spots}
              onChange={handleEditChange}
              required
            />
          </div>
        </div>

        {/* ===== –ü–æ–º–∏–ª–∫–∞ ===== */}
        {errors.edit && <p className="error-message">{errors.edit}</p>}

        {/* ===== –î—ñ—ó: –í—ñ–¥–º—ñ–Ω–∏—Ç–∏ / –ó–±–µ—Ä–µ–≥—Ç–∏ ===== */}
        <div className="edit-form-actions">
          <button type="button" className="cancel-btn" onClick={cancelEdit}>
            –í—ñ–¥–º—ñ–Ω–∏—Ç–∏
          </button>
          <button type="submit" className="save-btn">
            –ó–±–µ—Ä–µ–≥—Ç–∏
          </button>
        </div>
      </form>

      ) : (
        /* ‚Äî –†–ï–ñ–ò–ú –ü–ï–†–ï–ì–õ–Ø–î–£ ‚Äî */
        <div className="training-participants-modal">
          {/* –õ—ñ–≤–∞ –∫–æ–ª–æ–Ω–∫–∞ */}
          <div className="modal-left">
            {/* –ë–ª–æ–∫ 1: –ó–∞–≥–∞–ª—å–Ω–∞ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—è */}
            <div className="block-general-info">
              <h3 className="block-title">–ó–∞–≥–∞–ª—å–Ω–∞ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—è</h3>
              <div className="general-info">
                {sportLogos[selectedTraining.section] && (
                  <img
                    src={sportLogos[selectedTraining.section]}
                    alt={selectedTraining.section}
                    className="section-logo-lg"
                  />
                )}
                <div className="general-info-text">
                  <p><strong>–°–µ–∫—Ü—ñ—è:</strong> {selectedTraining.section}</p>
                  <p><strong>–î–∞—Ç–∞:</strong> {formatDate(selectedTraining.start_time)}</p>
                  <p>
                    <strong>–ß–∞—Å:</strong>{' '}
                    {formatTime(selectedTraining.start_time)} ‚Äì {formatTime(selectedTraining.end_time)}
                  </p>
                  <p>
                    <strong>–ó–∞–ø–∏—Å–∞–Ω–æ:</strong>{' '}
                    {participants.length} / {participants.length + parseInt(selectedTraining.spots, 10)}
                  </p>
                </div>
              </div>
            </div>
            {/* –ë–ª–æ–∫ 2: –§—ñ–Ω–∞–Ω—Å–æ–≤–∏–π –∞–Ω–∞–ª—ñ–∑ */}
            <div className="block-financial">
              <h3 className="block-title">–§—ñ–Ω–∞–Ω—Å–æ–≤–∏–π –∞–Ω–∞–ª—ñ–∑</h3>
              {(() => {
                const price = selectedTraining.price;
                const signed = participants.length;
                const total = signed + parseInt(selectedTraining.spots, 10);
                const maxRevenue = total * price;
                const currentRevenue = signed * price;
                const fillPercent = total > 0 ? Math.round((signed / total) * 100) : 0;
                return (
                  <ul className="financial-list">
                    <li><strong>–¶—ñ–Ω–∞:</strong> {price} {selectedTraining.currency}</li>
                    <li><strong>–ú–∞–∫—Å. –ø—Ä–∏–±—É—Ç–æ–∫:</strong> {maxRevenue.toFixed(2)} {selectedTraining.currency}</li>
                    <li><strong>–ó–∞–ø–æ–≤–Ω–µ–Ω—ñ—Å—Ç—å:</strong> {fillPercent}% ({signed}/{total})</li>
                    <li><strong>–û—á—ñ–∫—É–≤–∞–Ω–∏–π –ø—Ä–∏–±—É—Ç–æ–∫:</strong> {currentRevenue.toFixed(2)} {selectedTraining.currency}</li>
                  </ul>
                );
              })()}
            </div>
          </div>

          {/* –ü—Ä–∞–≤–∞ –∫–æ–ª–æ–Ω–∫–∞ */}
          <div className="modal-right">
            <h3 className="block-title">–£—á–∞—Å–Ω–∏–∫–∏</h3>
            <div className="participants-list">
              {participants.length > 0 ? (
                participants.map((p, idx) => (
                  <div key={idx} className="participant-item">
                    <img
                      src={p.avatar_url}
                      alt={p.name}
                      className="participant-avatar"
                      onError={e => {
                        e.currentTarget.onerror = null;
                        e.currentTarget.src = defaultAvatar;
                      }}
                    />
                    <div className="participant-details">
                      <button
                        className="link-button"
                        onClick={() => handleViewAthlete(p.user_id)}
                      >
                        {p.name}
                      </button>
                      <p className="participant-age">–í—ñ–∫: {p.age}</p>
                    </div>
                  </div>
                ))
              ) : (
                <p className="no-participants"><em>–ù—ñ—Ö—Ç–æ –Ω–µ –∑–∞–ø–∏—Å–∞–≤—Å—è.</em></p>
              )}
            </div>

            {/* –ö–Ω–æ–ø–∫–∏ –û–∫ —Ç–∞ –†–µ–¥–∞–≥—É–≤–∞—Ç–∏ –Ω–∞ –æ–¥–Ω–æ–º—É —Ä—ñ–≤–Ω—ñ */}
            <div className="participants-actions">
              <button
                className="modal-edit-btn"
                onClick={handleEdit}
              >
                –†–µ–¥–∞–≥—É–≤–∞—Ç–∏
              </button>
            </div>
          </div>
        </div>
      )
    }
  />
)}

            {/* –ú–æ–¥–∞–ª–∫–∞ –∑ –ø—Ä–æ—Ñ—ñ–ª–µ–º —Å–ø–æ—Ä—Ç—Å–º–µ–Ω–∞ */}
    {showAthleteModal && athleteProfile && (
      <Modal
        modalType="info"
        onClose={() => setShowAthleteModal(false)}
        className="athlete-detail-modal"
        message={
          <div className="profile-container athlete-modal-container">
            {/* –õ—ñ–≤–∞ –∫–æ–ª–æ–Ω–∫–∞: –∞–≤–∞—Ç–∞—Ä */}
            <div className="profile-avatar-column">
              <img
                src={athleteProfile.avatar_url}
                alt={athleteProfile.name}
                className="profile-avatar-large"
                onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = defaultAvatar; }}
              />
            </div>

            {/* –ü—Ä–∞–≤–∞ –∫–æ–ª–æ–Ω–∫–∞: —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—è */}
            <div className="profile-info-column">
              <h2 className="athlete-name">{athleteProfile.name}</h2>
              <div className="info-row"><strong>–í—ñ–∫:</strong> {athleteProfile.age}</div>
              <div className="info-row"><strong>Email:</strong> {athleteProfile.email}</div>
              <div className="info-row"><strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong> {athleteProfile.phone || <em>‚Äî</em>}</div>

              <section className="athlete-section">
                <h3>–ü—Ä–æ —Å–µ–±–µ</h3>
                <p>{athleteProfile.description || <em>‚Äî</em>}</p>
              </section>

              <section className="athlete-section">
                <h3>–£–ª—é–±–ª–µ–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É</h3>
                <p>
                  {athleteProfile.favorite_sports.length
                    ? athleteProfile.favorite_sports.join(', ')
                    : <em>‚Äî</em>}
                </p>
              </section>
            </div>
          </div>
        }
      />
    )}


      {/* Modal —É—Å–ø—ñ—Ö—É –ø—Ä–∏ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—ñ/–æ–Ω–æ–≤–ª–µ–Ω–Ω—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è */}
      {showModal && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}
    </div>
  );
};

export default CoachDashboard;
