// src/components/CoachDashboard/HistorySection.js
import React, { useState } from 'react';
import Modal from '../../Common/Modal';
import { format } from 'date-fns';
import './CoachDashboards.css';

export default function HistorySection({ trainings, sportLogos }) {
  const [selected, setSelected] = useState(null);

  return (
    <>
      <div className="cards-grid">
        {trainings.map(t => {
          const date = format(new Date(t.start_time), 'dd.MM.yyyy');
          const time = `${format(new Date(t.start_time), 'HH:mm')} – ${format(new Date(t.end_time), 'HH:mm')}`;
          const logo = sportLogos[t.section];
          return (
            <div key={t._id} className="training-card">
              <div className="card-header">
                <span className="card-date">{date}</span>
                <span className="card-time">{time}</span>
              </div>
              <div className="card-body">
                {logo && <img src={logo} alt={t.section} className="card-logo" />}
                <p className="section-name">{t.section}</p>
              </div>
              <div className="card-footer">
                <button className="view-btn" onClick={() => setSelected(t)}>
                  Переглянути
                </button>
              </div>
            </div>
          );
        })}
      </div>

      {selected && (
        <Modal
          modalType="info"
          className="participants-modal"
          onClose={() => setSelected(null)}
          message={
            <div style={{ textAlign: 'center', padding: '1rem' }}>
              <h2>{selected.section}</h2>
              <p><strong>Дата:</strong> {format(new Date(selected.start_time), 'dd.MM.yyyy')}</p>
              <p><strong>Час:</strong> {format(new Date(selected.start_time), 'HH:mm')} – {format(new Date(selected.end_time), 'HH:mm')}</p>
              <p><strong>Ціна:</strong> {selected.price} {selected.currency}</p>
              {/* будь-яка інша детальна інформація */}
              <button className="modal-close-btn" onClick={() => setSelected(null)}>Ок</button>
            </div>
          }
        />
      )}
    </>
  );
}
