// src/components/AthleteDashboard/HistorySection.js

import React, { useMemo } from 'react';
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip
} from 'recharts';
import './AthleteDashboard.css';
import { API_BASE_URL } from '../../../services/api';

const DEFAULT_AVATAR = `${API_BASE_URL}/avatars/default.png`;

export default function HistorySection({
  historyTrainings,
  sportLogos,
  onViewDetails,
  onViewCoach
}) {
  const fmtDate = iso =>
    new Date(iso).toLocaleDateString('uk-UA', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  const fmtTime = iso =>
    new Date(iso).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit'
    });

  // 1) Сортуємо за end_time від найближчого до найстарішого
  const sorted = useMemo(() => {
    return [...historyTrainings].sort(
      (a, b) => new Date(b.end_time) - new Date(a.end_time)
    );
  }, [historyTrainings]);

  // 2) Топ-3 секції за кількістю відвідувань
  const top3Sections = useMemo(() => {
    const counts = {};
    historyTrainings.forEach(t => {
      counts[t.section] = (counts[t.section] || 0) + 1;
    });
    return Object.entries(counts)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 3)
      .map(([section, count]) => ({ section, count }));
  }, [historyTrainings]);

  // 3) Улюблені тренери (топ-3 за кількістю ваших відвідувань)
  const top3Coaches = useMemo(() => {
    const counts = {};
    historyTrainings.forEach(t => {
      const name = t.coach_name || '—';
      counts[name] = (counts[name] || 0) + 1;
    });
    return Object.entries(counts)
      .sort(([, a], [, b]) => b - a)
      .slice(0, 3)
      .map(([coach_name, count]) => ({ coach_name, count }));
  }, [historyTrainings]);

  return (
    <div className="history-trainings-section">
      <h2>Історія тренувань</h2>

      {sorted.length === 0 ? (
        <p>Немає історії тренувань.</p>
      ) : (
        <div className="cards-grid">
          {sorted.map(t => {
            // Побудова абсолютного шляху до аватарки тренера
            const avatarSrc = t.avatar_url
              ? (t.avatar_url.startsWith('http')
                  ? t.avatar_url
                  : `${API_BASE_URL}${t.avatar_url}`)
              : DEFAULT_AVATAR;

            return (
              <div key={t._id} className="training-card">
                <div className="card-header">
                  <span className="card-date">{fmtDate(t.start_time)}</span>
                  <span className="card-time">
                    {fmtTime(t.start_time)} – {fmtTime(t.end_time)}
                  </span>
                </div>
                <div className="card-body">
                  {sportLogos[t.section] && (
                    <img
                      src={sportLogos[t.section]}
                      alt={t.section}
                      className="card-logo"
                    />
                  )}
                  <p>
                    <strong>Секція:</strong> {t.section}
                  </p>
                  <p className="trainer-line">
                    <strong>Тренер:</strong>{' '}
                    <img
                      src={avatarSrc}
                      alt="Avatar тренера"
                      className="coach-avatar-small"
                      onError={e => {
                        e.currentTarget.onerror = null;
                        e.currentTarget.src = DEFAULT_AVATAR;
                      }}
                    />{' '}
                    <span
                      className="coach-name-link"
                      onClick={() => onViewCoach(t.coach_id)}
                    >
                      {t.coach_name}
                    </span>
                  </p>
                </div>
                <div className="card-footer">
                  <button
                    className="action-btn view-btn"
                    onClick={() => onViewDetails(t)}
                  >
                    Переглянути
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}

      {/* === Блок статистики: графіки === */}
      <h2>Статистика</h2>
      <div className="charts-grid">
        {/* Top-3 секції */}
        <div className="chart-card">
          <h3>Топ-3 секції</h3>
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={top3Sections}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="section" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#82ca9d" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Улюблені тренери */}
        <div className="chart-card">
          <h3>Улюблені тренери</h3>
          <ResponsiveContainer width="100%" height={200}>
            <BarChart data={top3Coaches}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="coach_name" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="count" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
}
