import React, { useState, useEffect } from 'react';
import {
  createTraining,
  getCoachTrainings,
  getTrainingParticipants,
  updateTraining,
  getCoachProfile,
  updateCoachProfile
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/CoachDashboard.css';

// –Ü–º–ø–æ—Ä—Ç –ª–æ–≥–æ—Ç–∏–ø—ñ–≤ —Å–µ–∫—Ü—ñ–π
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// –ú–∞–ø–∞ —Å–µ–∫—Ü—ñ—è ‚Üí –ª–æ–≥–æ—Ç–∏–ø
const sportLogos = {
  "–§—É—Ç–±–æ–ª": footballLogo,
  "–ë–∞—Å–∫–µ—Ç–±–æ–ª": basketballLogo,
  "–ë–∞–¥–º—ñ–Ω—Ç–æ–Ω": badmintonLogo,
  "–í–æ–ª–µ–π–±–æ–ª": volleyballLogo,
  "–ë–µ–π—Å–±–æ–ª": baseballLogo,
  "–†–µ–≥–±—ñ": rugbyLogo,
  "–•–æ–∫–µ–π": hockeyLogo,
  "–ö–∞—Ä–∞—Ç–µ": karateLogo,
  "–ë–æ–∫—Å": boxingLogo,
  "–£—à—É": wushuLogo,
  "–î–∂–∏—É-–¥–∂–∏—Ç—Å—É": jiuJitsuLogo,
  "–ú–ú–ê": mmaLogo,
  "–ë—ñ–≥": runningLogo,
  "–°—Ç—Ä–∏–±–∫–∏": jumpingLogo,
  "–ú–µ—Ç–∞–Ω–Ω—è": throwingLogo,
  "–ü–ª–∞–≤–∞–Ω–Ω—è": swimmingLogo,
  "–í—ñ—Ç—Ä–∏–ª—å–Ω–∏–π —Å–ø–æ—Ä—Ç": sailingLogo,
  "–ö–∞–Ω–æ—ó–∑–º": canoeingLogo,
  "–°–∫–µ–π–±–æ—Ä–¥–∏–Ω–≥": skateboardingLogo,
  "–°–Ω–æ—É–±–æ—Ä–¥–∏–Ω–≥": snowboardingLogo,
  "–ê–ª—å–ø—ñ–Ω—ñ–∑–º": climbingLogo,
  "–§—ñ—Ç–Ω–µ—Å": fitnessLogo,
  "–°–∏–ª–æ–≤—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è": strengthTrainingLogo,
  "–ô–æ–≥–∞": yogaLogo
};

// –ö–∞—Ç–µ–≥–æ—Ä—ñ—ó —Ç–∞ —Å–µ–∫—Ü—ñ—ó
const sportCategories = {
  "–Ü–≥—Ä–∏ –∑ –º'—è—á–µ–º": ["–§—É—Ç–±–æ–ª","–ë–∞—Å–∫–µ—Ç–±–æ–ª","–ë–∞–¥–º—ñ–Ω—Ç–æ–Ω","–í–æ–ª–µ–π–±–æ–ª","–ë–µ–π—Å–±–æ–ª","–†–µ–≥–±—ñ","–•–æ–∫–µ–π"],
  "–ë–æ–π–æ–≤—ñ –º–∏—Å—Ç–µ—Ü—Ç–≤–∞": ["–ö–∞—Ä–∞—Ç–µ","–ë–æ–∫—Å","–£—à—É","–î–∂–∏—É-–¥–∂–∏—Ç—Å—É","–ú–ú–ê"],
  "–õ–µ–≥–∫–∞ –∞—Ç–ª–µ—Ç–∏–∫–∞": ["–ë—ñ–≥","–°—Ç—Ä–∏–±–∫–∏","–ú–µ—Ç–∞–Ω–Ω—è"],
  "–í–æ–¥–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É": ["–ü–ª–∞–≤–∞–Ω–Ω—è","–í—ñ—Ç—Ä–∏–ª—å–Ω–∏–π —Å–ø–æ—Ä—Ç","–ö–∞–Ω–æ—ó–∑–º"],
  "–ï–∫—Å—Ç—Ä–µ–º–∞–ª—å–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É": ["–°–∫–µ–π–±–æ—Ä–¥–∏–Ω–≥","–°–Ω–æ—É–±–æ—Ä–¥–∏–Ω–≥","–ê–ª—å–ø—ñ–Ω—ñ–∑–º"],
  "–§—ñ—Ç–Ω–µ—Å —Ç–∞ —Ç—Ä–µ–Ω–∞–∂–µ—Ä–Ω–∏–π –∑–∞–ª": ["–§—ñ—Ç–Ω–µ—Å","–°–∏–ª–æ–≤—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è","–ô–æ–≥–∞"]
};

const CoachDashboard = () => {
  // ‚Äî‚Äî‚Äî –ü—Ä–æ—Ñ—ñ–ª—å ‚Äî‚Äî‚Äî
  const [profile, setProfile] = useState({
    name: '', age: '', email: '', createdAt: '', description: '', favoriteSports: []
  });
  const [profileLoading, setProfileLoading] = useState(true);
  const [profileError, setProfileError] = useState('');

  // ‚Äî‚Äî‚Äî –°—Ç–≤–æ—Ä–µ–Ω–Ω—è —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è ‚Äî‚Äî‚Äî
  const [trainingData, setTrainingData] = useState({
    date: '', start_time: '', end_time: '',
    price: '', spots: '', currency: 'UAH',
    category: '', section: ''
  });
  const [errors, setErrors] = useState({});
  const [modalMessage, setModalMessage] = useState('');
  const [showModal, setShowModal] = useState(false);

  // ‚Äî‚Äî‚Äî –í–∞—à—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è ‚Äî‚Äî‚Äî
  const [trainings, setTrainings] = useState([]);
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editData, setEditData] = useState({
    date:'', start_time:'', end_time:'', price:'', spots:'', currency:'', category:'', section:''
  });

  const today = new Date().toISOString().split('T')[0];

  // –§–æ—Ä–º–∞—Ç–µ—Ä–∏
  const formatDate = t => new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});
  const calcTenure = createdAt => {
    if (!createdAt) return '';
    const diff = Date.now() - new Date(createdAt).getTime();
    const days = Math.floor(diff/(1000*60*60*24));
    const years = Math.floor(days/365);
    const months = Math.floor((days%365)/30);
    return `${years} —Ä. ${months} –º.`;
  };

  // –ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ –ø—Ä–æ—Ñ—ñ–ª—å
  const loadProfile = async () => {
    const coachId = localStorage.getItem('userId');
    try {
      const data = await getCoachProfile(coachId);
      setProfile(data);
    } catch (err) {
      setProfileError(err.message);
    } finally {
      setProfileLoading(false);
    }
  };

  // –ó–±–µ—Ä–µ–≥—Ç–∏ –ø—Ä–æ—Ñ—ñ–ª—å
  const saveProfile = async () => {
    try {
      await updateCoachProfile(profile);
      alert('–ü—Ä–æ—Ñ—ñ–ª—å –∑–±–µ—Ä–µ–∂–µ–Ω–æ');
    } catch (err) {
      alert('–ü–æ–º–∏–ª–∫–∞: ' + err.message);
    }
  };

  // –ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è
  const fetchTrainings = async () => {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { trainings } = await getCoachTrainings(coachId);
      setTrainings(trainings);
    } catch (err) {
      console.error(err);
    }
  };

  // on mount
  useEffect(() => {
    loadProfile();
    fetchTrainings();
  }, []);

  // === –û–±—Ä–æ–±–Ω–∏–∫–∏ –ø—Ä–æ—Ñ—ñ–ª—é ===
  const handleProfileChange = (field, value) =>
    setProfile({ ...profile, [field]: value });

  // === –û–±—Ä–æ–±–Ω–∏–∫–∏ —Ç—Ä–µ–Ω—É–≤–∞–Ω—å ===
  const handleChange = e =>
    setTrainingData({ ...trainingData, [e.target.name]: e.target.value });
  const handleCategoryChange = e =>
    setTrainingData({ ...trainingData, category: e.target.value, section: '' });
  const getMinTime = () =>
    trainingData.date === today
      ? new Date().toTimeString().slice(0,5)
      : '00:00';

  const handleSubmit = async e => {
    e.preventDefault();
    setErrors({});
    const coachId = localStorage.getItem('userId');
    // –≤–∞–ª—ñ–¥–∞—Ü—ñ—è‚Ä¶
    try {
      await createTraining({
        coach_id: coachId,
        start_time: `${trainingData.date}T${trainingData.start_time}`,
        end_time:   `${trainingData.date}T${trainingData.end_time}`,
        price:      parseFloat(trainingData.price),
        spots:      parseInt(trainingData.spots,10),
        currency:   trainingData.currency,
        category:   trainingData.category,
        section:    trainingData.section
      });
      setModalMessage('–¢—Ä–µ–Ω—É–≤–∞–Ω–Ω—è —Å—Ç–≤–æ—Ä–µ–Ω–æ üëç');
      setShowModal(true);
      setTrainingData({
        date:'', start_time:'', end_time:'',
        price:'', spots:'', currency:'UAH',
        category:'', section:''
      });
      fetchTrainings();
    } catch (err) {
      setErrors({ general: err.message });
    }
  };

  const handleModalClose = () => setShowModal(false);

  // –¥–∏–≤–∏—Ç–∏—Å—è/—Ä–µ–¥–∞–≥—É–≤–∞—Ç–∏ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è
  const handleViewParticipants = async t => {
    setSelectedTraining(t);
    try {
      const { participants } = await getTrainingParticipants(t._id);
      setParticipants(participants);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch (err) {
      console.error(err);
    }
  };
  const closeParticipantsModal = () =>
    { setShowParticipantsModal(false); setSelectedTraining(null); setParticipants([]); setEditMode(false); };

  const handleEdit = () => {
    const t = selectedTraining;
    const s = new Date(t.start_time), e = new Date(t.end_time), signed = t.participants?.length||0;
    setEditData({
      date:       s.toISOString().split('T')[0],
      start_time: s.toTimeString().slice(0,5),
      end_time:   e.toTimeString().slice(0,5),
      price:      t.price,
      spots:      signed + parseInt(t.spots,10),
      currency:   t.currency,
      category:   t.category,
      section:    t.section
    });
    setEditMode(true);
  };
  const handleEditChange = e =>
    setEditData({ ...editData, [e.target.name]: e.target.value });

  const handleEditSubmit = async e => {
    e.preventDefault();
    setErrors({});
    const signed = selectedTraining.participants?.length||0;
    const total  = parseInt(editData.spots,10);
    const payload = {
      start_time: `${editData.date}T${editData.start_time}`,
      end_time:   `${editData.date}T${editData.end_time}`,
      price:      parseFloat(editData.price),
      currency:   editData.currency,
      category:   editData.category,
      section:    editData.section
    };
    if (total !== signed + parseInt(selectedTraining.spots,10)) {
      if (total < signed) {
        setErrors({ edit: '–ú—ñ—Å—Ü—å –º–µ–Ω—à–µ –∑–∞ –≤–∂–µ –∑–∞–ø–∏—Å–∞–Ω—ñ.' });
        return;
      }
      payload.spots = total - signed;
    }
    try {
      await updateTraining(selectedTraining._id, payload);
      setModalMessage('–¢—Ä–µ–Ω—É–≤–∞–Ω–Ω—è –æ–Ω–æ–≤–ª–µ–Ω–æ üëç');
      setShowModal(true);
      setEditMode(false);
      fetchTrainings();
      closeParticipantsModal();
    } catch (err) {
      setErrors({ edit: err.message });
    }
  };
  const cancelEdit = () => setEditMode(false);

  // render
  return (
    <div className="dashboard-container">
      <h1>–ö–∞–±—ñ–Ω–µ—Ç –¢—Ä–µ–Ω–µ—Ä–∞</h1>

      {/* ==== –ú—ñ–π –ü—Ä–æ—Ñ—ñ–ª—å ==== */}
      <section className="profile-section">
        <h2>–ú—ñ–π –ü—Ä–æ—Ñ—ñ–ª—å</h2>
        {profileLoading
          ? <p>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</p>
          : profileError
            ? <p className="error-message">{profileError}</p>
            : (
              <div className="profile-card">
                <div className="profile-field">
                  <label>–Ü–º'—è:</label>
                  <input
                    type="text"
                    value={profile.name}
                    onChange={e => handleProfileChange('name', e.target.value)}
                  />
                </div>
                <div className="profile-field">
                  <label>–í—ñ–∫:</label>
                  <input
                    type="number"
                    min="0"
                    value={profile.age}
                    onChange={e => handleProfileChange('age', e.target.value)}
                  />
                </div>
                <div className="profile-field">
                  <label>Email:</label>
                  <input type="email" value={profile.email} disabled />
                </div>
                <div className="profile-field">
                  <label>–°—Ç–∞–∂:</label>
                  <span>{calcTenure(profile.createdAt)}</span>
                </div>
                <div className="profile-field">
                  <label>–ü—Ä–æ —Å–µ–±–µ:</label>
                  <textarea
                    rows="3"
                    value={profile.description}
                    onChange={e => handleProfileChange('description', e.target.value)}
                  />
                </div>
                <div className="profile-field">
                  <label>–£–ª—é–±–ª–µ–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É:</label>
                  <div className="favorite-sports">
                    {Object.values(sportCategories).flat().map(sport => (
                      <label key={sport}>
                        <input
                          type="checkbox"
                          value={sport}
                          checked={profile.favoriteSports.includes(sport)}
                          onChange={() => {
                            const fav = profile.favoriteSports.includes(sport)
                              ? profile.favoriteSports.filter(x=>x!==sport)
                              : [...profile.favoriteSports, sport];
                            setProfile({...profile, favoriteSports: fav});
                          }}
                        />
                        {sport}
                      </label>
                    ))}
                  </div>
                </div>
                <button className="profile-save-btn" onClick={saveProfile}>
                  –ó–±–µ—Ä–µ–≥—Ç–∏ –ø—Ä–æ—Ñ—ñ–ª—å
                </button>
              </div>
            )
        }
      </section>

      {/* ==== –§–æ—Ä–º–∞ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è ==== */}
      <div className="form-wrapper">
        {/* –¢—É—Ç ‚Äî –±–µ–∑ –∑–º—ñ–Ω, —è–∫ —É –ø–æ–ø–µ—Ä–µ–¥–Ω—å–æ–º—É —Ñ–∞–π–ª—ñ */}
      </div>

      {/* ==== –í–∞—à—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è (–∫–∞—Ä—Ç–æ—á–∫–∏) ==== */}
      <section className="registered-trainings-section">
        <h2>–í–∞—à—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è</h2>
        {trainings.length === 0
          ? <p>–ù–µ–º–∞—î —Å—Ç–≤–æ—Ä–µ–Ω–∏—Ö —Ç—Ä–µ–Ω—É–≤–∞–Ω—å.</p>
          : (
            <div className="cards-grid">
              {trainings
                .sort((a,b)=>new Date(a.start_time)-new Date(b.start_time))
                .map(t => {
                  const date = formatDate(t.start_time);
                  const time = `${formatTime(t.start_time)} ‚Äì ${formatTime(t.end_time)}`;
                  const logo = sportLogos[t.section];
                  const count = t.participants?.length||0;
                  const total = count + parseInt(t.spots,10);
                  return (
                    <div key={t._id} className="training-card">
                      <div className="card-header">
                        <span className="card-date">{date}</span>
                        <span className="card-time">{time}</span>
                      </div>
                      <div className="card-body">
                        {logo && <img src={logo} alt={t.section} className="card-logo" />}
                        <p className="section-name">{t.section}</p>
                        <p className="participants-count">–ö—ñ–ª—å–∫—ñ—Å—Ç—å —É—á–∞—Å–Ω–∏–∫—ñ–≤: {count}\{total}</p>
                      </div>
                      <div className="card-footer">
                        <button
                          className="view-btn"
                          onClick={()=>handleViewParticipants(t)}
                        >
                          –ü–µ—Ä–µ–≥–ª—è–Ω—É—Ç–∏
                        </button>
                      </div>
                    </div>
                  );
                })}
            </div>
          )
        }
      </section>

      {/* ==== –ú–æ–¥–∞–ª—å–Ω—ñ –≤—ñ–∫–Ω–∞ ==== */}
      {showParticipantsModal && selectedTraining && (
        <Modal
          modalType="info"
          onClose={closeParticipantsModal}
          message={
            editMode
              ? /* –§–æ—Ä–º–∞ —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è —è–∫ —Ä–∞–Ω—ñ—à–µ */
                <form onSubmit={handleEditSubmit} className="edit-training-form">
                  {/* ‚Ä¶ */}
                </form>
              : /* –î–µ—Ç–∞–ª—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è */
                <div>
                  {/* ‚Ä¶ */}
                </div>
          }
        />
      )}
      {showModal && <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />}
    </div>
  );
};

export default CoachDashboard;
