import React, { useState, useEffect, useRef } from 'react';
import {
  createTraining,
  getCoachTrainings,
  getTrainingParticipants,
  updateTraining,
  deleteTraining,
  getCoachProfile,
  getAthleteProfile,
  updateCoachProfile,
  changeCoachPassword,
  uploadCoachAvatar,
  getCoachReviews, API_BASE_URL
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/CoachDashboard.css';

// Логотипи секцій
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// Мапа секція → логотип
const sportLogos = {
  "Футбол": footballLogo,
  "Баскетбол": basketballLogo,
  "Бадмінтон": badmintonLogo,
  "Волейбол": volleyballLogo,
  "Бейсбол": baseballLogo,
  "Регбі": rugbyLogo,
  "Хокей": hockeyLogo,
  "Карате": karateLogo,
  "Бокс": boxingLogo,
  "Ушу": wushuLogo,
  "Джиу-джитсу": jiuJitsuLogo,
  "ММА": mmaLogo,
  "Біг": runningLogo,
  "Стрибки": jumpingLogo,
  "Метання": throwingLogo,
  "Плавання": swimmingLogo,
  "Вітрильний спорт": sailingLogo,
  "Каноїзм": canoeingLogo,
  "Скейбординг": skateboardingLogo,
  "Сноубординг": snowboardingLogo,
  "Альпінізм": climbingLogo,
  "Фітнес": fitnessLogo,
  "Силові тренування": strengthTrainingLogo,
  "Йога": yogaLogo
};

// Категорії та секції
const sportCategories = {
  "Ігри з м'ячем": ["Футбол","Баскетбол","Бадмінтон","Волейбол","Бейсбол","Регбі","Хокей"],
  "Бойові мистецтва": ["Карате","Бокс","Ушу","Джиу-джитсу","ММА"],
  "Легка атлетика": ["Біг","Стрибки","Метання"],
  "Водні види спорту": ["Плавання","Вітрильний спорт","Каноїзм"],
  "Екстремальні види спорту": ["Скейбординг","Сноубординг","Альпінізм"],
  "Фітнес та тренажерний зал": ["Фітнес","Силові тренування","Йога"]
};

const defaultAvatar = `${API_BASE_URL}/avatars/default.png`;

const CoachDashboard = () => {
  // — Стани створення тренувань —
  const [trainingData, setTrainingData]   = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'UAH',
    category:'', section:''
  });
  const [errors, setErrors]               = useState({});
  const [modalMessage, setModalMessage]   = useState('');
  const [showModal, setShowModal]         = useState(false);
  const [trainings, setTrainings]         = useState([]);
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants]   = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);
  const [editMode, setEditMode]           = useState(false);
  const [editData, setEditData]           = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'',
    category:'', section:''
  });

  // — Стани профілю —
  const [profile, setProfile]             = useState(null);
  const [editProfileMode, setEditProfileMode] = useState(false);
  const [nameInput, setNameInput]         = useState('');
  const [ageInput, setAgeInput]           = useState('');
  const [phoneInput, setPhoneInput]       = useState('');
  const [description, setDescription]     = useState('');
  const [favoriteSports, setFavoriteSports] = useState([]);
  const [profileError, setProfileError]   = useState('');
  const [showProfileSuccess, setShowProfileSuccess] = useState(false);

  // — Стани аватарки —
  const [avatarFile, setAvatarFile]       = useState(null);
  const [avatarPreview, setAvatarPreview] = useState('');
  const [showAvatarSuccess, setShowAvatarSuccess] = useState(false);

  // — Стани зміни пароля —
  const [showPassMode, setShowPassMode]   = useState(false);
  const [oldPass, setOldPass]             = useState('');
  const [newPass, setNewPass]             = useState('');
  const [confirmPass, setConfirmPass]     = useState('');
  const [passError, setPassError]         = useState('');
  const [passSuccess, setPassSuccess]     = useState(false);

  const today = new Date().toISOString().split('T')[0];
  const allSections = Object.values(sportCategories).flat();

  // для підтвердження видалення
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);
  const [trainingToDelete, setTrainingToDelete]   = useState(null);

  // для відгуків
  const [reviews, setReviews] = useState([]);
  const [avgRating, setAvgRating] = useState(null);
  const [reviewCount, setReviewCount] = useState(0);
  const reviewSectionRef = useRef(null);


  // Форматери
  const formatDate = t => new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});

  // — Fetch тренувань і профілю —
  const fetchTrainings = async () => {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { trainings } = await getCoachTrainings(coachId);
      setTrainings(trainings);
    } catch(err) {
      console.error(err);
    }
  };
  const fetchProfile = async () => {
  const coachId = localStorage.getItem('userId');
  if (!coachId) return;

  try {
    // 1) Завантажуємо дані профілю тренера
    const { profile } = await getCoachProfile(coachId);
    setProfile(profile);
    setNameInput(profile.name);
    setAgeInput(profile.age);
    setPhoneInput(profile.phone || '');
    setDescription(profile.description || '');
    setFavoriteSports(profile.favorite_sports || []);
    // щоб обійти кеш, додаємо таймстемп
    const src = profile.avatar_url
      ? `${profile.avatar_url}?t=${Date.now()}`
      : `${API_BASE_URL}/avatars/default.png`;
    setAvatarPreview(src);

    // 2) Завантажуємо усі відгуки тренера
    const { reviews, average_rating, count } = await getCoachReviews(coachId);

    // 3) Для кожного відгуку підтягуємо аватарку спортсмена
    const reviewsWithAvatars = await Promise.all(
      reviews.map(async r => {
        if (!r.athlete_id) {
          return { ...r, athlete_avatar_url: null };
        }
        try {
          const { profile: athlete } = await getAthleteProfile(r.athlete_id);
          return {
            ...r,
            athlete_avatar_url: athlete.avatar_url || null
          };
        } catch {
          return { ...r, athlete_avatar_url: null };
        }
      })
    );

    // 4) Зберігаємо відгуки з аватарками
    setReviews(reviewsWithAvatars);
    setAvgRating(average_rating);
    setReviewCount(count);

  } catch (err) {
    console.error('Помилка при завантаженні профілю/відгуків:', err);
  }
  };

  useEffect(() => {
    fetchTrainings();
    fetchProfile();
  }, []);

  // — Обробники профілю —
  const toggleSport = sport => {
    setFavoriteSports(prev =>
      prev.includes(sport)
        ? prev.filter(s => s !== sport)
        : [...prev, sport]
    );
  };

// — Обробник збереження профілю тренера з валідацією —
const handleSaveProfile = async () => {
  // Скидаємо попередню помилку
  setProfileError('');

  // Перевірка: Ім'я обов'язкове
  if (!nameInput.trim()) {
    setProfileError('Поле "Ім\'я" обов\'язкове');
    return;
  }

  // Перевірка: Вік обов'язковий та числовий
  const ageNum = parseInt(ageInput, 10);
  if (isNaN(ageNum)) {
    setProfileError('Поле "Вік" обов\'язкове');
    return;
  }
  // Перевірка: Вік не може бути від’ємним
  if (ageNum < 0) {
    setProfileError('Вік не може бути від’ємним');
    return;
  }
  // Перевірка: Вік не може бути менше 18
  if (ageNum < 18) {
    setProfileError('Вік не може бути менше 18 років');
    return;
  }

  // Якщо всі перевірки пройдені — відправляємо на сервер
  try {
    await updateCoachProfile({
      user_id: profile.user_id,
      name: nameInput.trim(),
      age: ageNum,
      phone: phoneInput,
      description,
      favorite_sports: favoriteSports
    });
    // Після успіху — закриваємо режим редагування, показуємо повідомлення і оновлюємо профіль
    setShowProfileSuccess(true);
    setEditProfileMode(false);
    fetchProfile();
  } catch (err) {
    // Якщо сервер повернув помилку — відображаємо її
    setProfileError(err.message);
  }
};

    // Обробник вибору файлу
    const handleAvatarChange = e => {
      const file = e.target.files[0];
      if (!file) return;
      setAvatarFile(file);
      // відразу показуємо локальну прев’ю
      setAvatarPreview(URL.createObjectURL(file));
    };

    // Обробник завантаження на сервер
const handleUploadAvatar = async () => {
  if (!avatarFile) return;
  try {
    const { avatar_url } = await uploadCoachAvatar(profile.user_id, avatarFile);
    // Свіже прев’ю
    setAvatarPreview(avatar_url);
    // Підтягнути оновлений профіль
    fetchProfile();
    // очистити файл
    setAvatarFile(null);
+   // показати модалку успіху
+   setShowAvatarSuccess(true);
  } catch(err) {
    console.error(err);
  }
};


  // — Обробники зміни пароля —
  const handleChangePassword = async () => {
    setPassError('');
    try {
      await changeCoachPassword({
        user_id: profile.user_id,
        old_password: oldPass,
        new_password: newPass,
        confirm_password: confirmPass
      });
      setPassSuccess(true);
      setShowPassMode(false);
      setOldPass(''); setNewPass(''); setConfirmPass('');
    } catch(err) {
      setPassError(err.message);
    }
  };

  // — Обробники тренувань —
  const handleChange = e => setTrainingData({...trainingData, [e.target.name]: e.target.value});
  const handleCategoryChange = e => setTrainingData({
    ...trainingData,
    category: e.target.value,
    section: ''
  });
  const getMinTime = () => trainingData.date === today
    ? new Date().toTimeString().slice(0,5)
    : '00:00';

  const handleSubmit = async e => {
  e.preventDefault();
  setErrors({});

  const coachId = localStorage.getItem('userId');
  if (!coachId) {
    setErrors({ general: 'Будь ласка, увійдіть повторно.' });
    return;
  }

  // Перевіряємо, що профіль уже в state
  if (!profile || !profile.name) {
    setErrors({ general: 'Профіль не завантажений. Спробуйте оновити сторінку.' });
    return;
  }

  // Базові валідації дати та часу
  if (!trainingData.date || !trainingData.start_time || !trainingData.end_time) {
    setErrors({ general: 'Оберіть дату та час.' });
    return;
  }
  if (!trainingData.category || !trainingData.section) {
    setErrors({ general: 'Оберіть категорію/секцію.' });
    return;
  }
  if (trainingData.start_time >= trainingData.end_time) {
    setErrors({ end_time: 'Час завершення має бути пізніше.' });
    return;
  }
  const startDT = new Date(`${trainingData.date}T${trainingData.start_time}`);
  if (startDT < new Date()) {
    setErrors({ general: 'Не можна створювати в минулому.' });
    return;
  }

  // Формуємо payload з coach_name
  const payload = {
    coach_id: coachId,
    coach_name: profile.name,           // ← Додаємо ім'я тренера
    start_time: `${trainingData.date}T${trainingData.start_time}`,
    end_time:   `${trainingData.date}T${trainingData.end_time}`,
    price:      parseFloat(trainingData.price),
    spots:      parseInt(trainingData.spots, 10),
    currency:   trainingData.currency,
    category:   trainingData.category,
    section:    trainingData.section
  };

  // Дебаг: переконаємось, що coach_name туда пішов
  console.log('🔔 createTraining payload:', payload);

  try {
    await createTraining(payload);
    setModalMessage('Тренування успішно створено!');
    setShowModal(true);
    setTrainingData({
      date: '', start_time: '', end_time: '',
      price: '', spots: '', currency: 'UAH',
      category: '', section: ''
    });
    fetchTrainings();
  } catch (err) {
    setErrors({ general: err.message });
  }
};

  const handleDelete = async training => {
  const ok = window.confirm(
    `Видалити тренування "${training.section}" ${new Date(training.start_time).toLocaleDateString('uk-UA')}?`
  );
  if (!ok) return;
  try {
    await deleteTraining(training._id);
    fetchTrainings();  // перезавантажує список тренувань
  } catch (err) {
    console.error(err);
    setModalMessage(err.message);
    setShowModal(true);
  }
  };

      // при кліку — відкриваємо модалку
    const handleDeleteClick = training => {
      setTrainingToDelete(training);
      setShowDeleteConfirm(true);
    };

    // якщо користувач погодився — видаляємо
    const confirmDelete = async () => {
      try {
        await deleteTraining(trainingToDelete._id);
        setModalMessage('Тренування успішно видалено.');
        setShowModal(true);
        fetchTrainings();           // оновлюємо список
      } catch (err) {
        setModalMessage(`Помилка: ${err.message}`);
        setShowModal(true);
      } finally {
        setShowDeleteConfirm(false);
        setTrainingToDelete(null);
      }
    };

    // якщо відмінив — ховаємо підтвердження
    const cancelDelete = () => {
      setShowDeleteConfirm(false);
      setTrainingToDelete(null);
    };


  const handleModalClose = () => setShowModal(false);

  const handleViewParticipants = async t => {
    setSelectedTraining(t);
    try {
      const { participants } = await getTrainingParticipants(t._id);
      setParticipants(participants);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch(err) {
      console.error(err);
    }
  };
  const closeParticipantsModal = () => {
    setShowParticipantsModal(false);
    setSelectedTraining(null);
    setParticipants([]);
    setEditMode(false);
  };

  const handleEdit = () => {
    const t = selectedTraining;
    const start = new Date(t.start_time),
          end   = new Date(t.end_time),
          signed= t.participants?.length || 0;
    setEditData({
      date: start.toISOString().split('T')[0],
      start_time: start.toTimeString().slice(0,5),
      end_time: end.toTimeString().slice(0,5),
      price: t.price,
      spots: signed + parseInt(t.spots,10),
      currency: t.currency,
      category: t.category,
      section: t.section
    });
    setEditMode(true);
  };
  const handleEditChange = e => setEditData({...editData, [e.target.name]: e.target.value});

  const handleEditSubmit = async e => {
    e.preventDefault();
    setErrors({});
    if (!editData.date || !editData.start_time || !editData.end_time) {
      setErrors({ edit:'Оберіть дату та час.' });
      return;
    }
    if (editData.start_time >= editData.end_time) {
      setErrors({ edit:'Час завершення має бути пізніше.' });
      return;
    }
    const newStart = new Date(`${editData.date}T${editData.start_time}`);
    if (newStart < new Date()) {
      setErrors({ edit:'Не можна у минулому.' });
      return;
    }
    const signed = selectedTraining.participants?.length || 0;
    const total  = parseInt(editData.spots,10);
    const payload = {
      start_time: `${editData.date}T${editData.start_time}`,
      end_time: `${editData.date}T${editData.end_time}`,
      price: parseFloat(editData.price),
      currency: editData.currency,
      category: editData.category,
      section: editData.section
    };
    if (total !== signed + parseInt(selectedTraining.spots,10)) {
      if (total < signed) {
        setErrors({ edit:'Місць менше за вже записані.' });
        return;
      }
      payload.spots = total - signed;
    }
    try {
      await updateTraining(selectedTraining._id, payload);
      setModalMessage('Тренування успішно оновлено!');
      setShowModal(true);
      setEditMode(false);
      fetchTrainings();
      closeParticipantsModal();
    } catch(err) {
      setErrors({ edit: err.message });
    }
  };
  const cancelEdit = () => setEditMode(false);

  const sorted = [...trainings].sort(
    (a,b) => new Date(a.start_time) - new Date(b.start_time)
  );

  return (
    <div className="dashboard-container">
      <h1>Кабінет Тренера</h1>

      {/* Форма створення тренування */}
      <div className="form-wrapper">
        <h2>Створити тренування</h2>
        <p>Заповніть форму, щоб додати нове тренування.</p>
        {errors.general && <p className="error-message">{errors.general}</p>}
        <form onSubmit={handleSubmit} className="create-training-form">
          <div className="form-row">
            <label htmlFor="date">Дата:</label>
            <input
              type="date"
              name="date"
              id="date"
              value={trainingData.date}
              onChange={handleChange}
              min={today}
              required
            />
          </div>
          <div className="form-row time-group">
            <div className="time-field">
              <label htmlFor="start_time">Початок:</label>
              <input
                type="time"
                name="start_time"
                id="start_time"
                value={trainingData.start_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
            </div>
            <div className="time-field">
              <label htmlFor="end_time">Кінець:</label>
              <input
                type="time"
                name="end_time"
                id="end_time"
                value={trainingData.end_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
              {errors.end_time && <p className="error-message">{errors.end_time}</p>}
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="category">Категорія:</label>
            <select
              name="category"
              id="category"
              value={trainingData.category}
              onChange={handleCategoryChange}
              required
            >
              <option value="">-- Виберіть категорію --</option>
              {Object.keys(sportCategories).map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <div className="form-row">
            <label htmlFor="section">Секція:</label>
            <select
              name="section"
              id="section"
              value={trainingData.section}
              onChange={handleChange}
              disabled={!trainingData.category}
              required
            >
              <option value="">-- Виберіть секцію --</option>
              {trainingData.category &&
                sportCategories[trainingData.category].map(s => (
                  <option key={s} value={s}>{s}</option>
                ))
              }
            </select>
          </div>
          <div className="form-row price-currency-group">
            <div className="price-group">
              <label htmlFor="price">Ціна:</label>
              <input
                type="number"
                step="0.01"
                min="0"
                name="price"
                id="price"
                value={trainingData.price}
                onChange={handleChange}
                required
              />
              {errors.price && <p className="error-message">{errors.price}</p>}
            </div>
            <div className="currency-group">
              <label htmlFor="currency">Валюта:</label>
              <select
                name="currency"
                id="currency"
                value={trainingData.currency}
                onChange={handleChange}
                required
              >
                <option value="UAH">UAH</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="spots">Місць:</label>
            <input
              type="number"
              min="0"
              name="spots"
              id="spots"
              value={trainingData.spots}
              onChange={handleChange}
              required
            />
            {errors.spots && <p className="error-message">{errors.spots}</p>}
          </div>
          <button type="submit" className="submit-btn">Створити</button>
        </form>
      </div>

      {/* Секція “Ваші тренування” */}
      <section className="registered-trainings-section">
        <h2>Ваші тренування</h2>
        {sorted.length === 0 ? (
          <p>Немає створених тренувань.</p>
        ) : (
          <div className="cards-grid">
            {sorted.map(t => {
              const date = formatDate(t.start_time);
              const time = `${formatTime(t.start_time)} – ${formatTime(t.end_time)}`;
              const logo = sportLogos[t.section];
              const signed = t.participants?.length || 0;
              const total  = signed + parseInt(t.spots,10);
              return (
                <div key={t._id} className="training-card">
                  <div className="card-header">
                    <span className="card-date">{date}</span>
                    <span className="card-time">{time}</span>
                  </div>
                  <div className="card-body">
                    {logo && <img src={logo} alt={t.section} className="card-logo" />}
                    <p className="section-name">{t.section}</p>
                    <p className="participants-count">
                      Кількість учасників: {signed}\{total}
                    </p>
                  </div>
                  <div className="card-footer">
                    <button
                      className="view-btn"
                      onClick={() => handleViewParticipants(t)}
                    >
                      Переглянути
                    </button>
                      <button
                      className="delete-btn"
                      onClick={() => handleDeleteClick(t)}
                    >
                      Видалити
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Нова секція “Мій Профіль” */}
       <section className="profile-section">
      {profile ? (
        <>
          {/* === Особиста інформація === */}
          <div className="profile-container">
            <div className="profile-avatar-column">
              <img
                src={avatarPreview}
                alt="Аватар тренера"
                className="profile-avatar-large"
              />
              {editProfileMode && (
                <>
                  <input type="file" accept="image/*" onChange={handleAvatarChange} />
                  <button onClick={handleUploadAvatar} className="btn-secondary" disabled={!avatarFile}>
                    Завантажити
                  </button>
                </>
              )}
            </div>
            <div className="profile-info-column">
              {editProfileMode ? (
                <>
                  <label>Ім'я:</label>
                  <input value={nameInput} onChange={e => setNameInput(e.target.value)} />

                  <label>Вік:</label>
                  <input type="number" min="18" value={ageInput} onChange={e => setAgeInput(e.target.value)} />

                  <label>Телефон:</label>
                  <input value={phoneInput} onChange={e => setPhoneInput(e.target.value)} />

                  <label>Опис:</label>
                  <textarea value={description} onChange={e => setDescription(e.target.value)} />

                  <label>Улюблені види спорту:</label>
                  <div className="favorite-sports-list">
                    {allSections.map(s => (
                      <label key={s} className="sport-checkbox">
                        <input
                          type="checkbox"
                          checked={favoriteSports.includes(s)}
                          onChange={() => toggleSport(s)}
                        />
                        {s}
                      </label>
                    ))}
                  </div>
                  {profileError && <p className="error-message">{profileError}</p>}

                  <div className="profile-actions">
                    <button onClick={handleSaveProfile} className="btn-primary">Зберегти</button>
                    <button
                    className="btn-secondary"
                    onClick={() => {
                      // Ховаємо форму
                      setEditProfileMode(false);
                      setProfileError("");
                      // Відновлюємо значення з profile
                      setNameInput(profile.name);
                      setAgeInput(profile.age);
                      setPhoneInput(profile.phone || "");
                      setDescription(profile.description || "");
                      setFavoriteSports(profile.favorite_sports || []);
                    }}
                  >
                    Відмінити
                  </button>
                  </div>
                </>
              ) : (
                <>
                  <h2>{profile.name}</h2>
                  <p><strong>Вік:</strong> {profile.age}</p>
                  <p><strong>Email:</strong> {profile.email}</p>
                  <p><strong>Телефон:</strong> {profile.phone || <em>—</em>}</p>
                  <p><strong>Опис:</strong> {profile.description || <em>—</em>}</p>
                  <p><strong>Улюблені спорт:</strong> {favoriteSports.length ? favoriteSports.join(', ') : <em>—</em>}</p>
                  <p>
                    <strong>Середній рейтинг:</strong>{' '}
                    <span
                      className="profile-rating-link"
                      onClick={() => reviewSectionRef.current.scrollIntoView({ behavior: 'smooth' })}
                    >
                      {avgRating !== null ? `${avgRating} ★ (${reviewCount})` : '—'}
                    </span>
                  </p>
                  <div className="profile-actions">
                    <button onClick={() => setEditProfileMode(true)} className="btn-primary">Редагувати</button>
                    <button onClick={() => setShowPassMode(true)} className="btn-secondary">Змінити пароль</button>
                  </div>
                </>
              )}
            </div>
          </div>

          {/* === Форма зміни пароля === */}
          {showPassMode && (
            <div className="password-form">
              <label>Старий пароль:</label>
              <input type="password" value={oldPass} onChange={e => setOldPass(e.target.value)} />

              <label>Новий пароль:</label>
              <input type="password" value={newPass} onChange={e => setNewPass(e.target.value)} />

              <label>Підтвердіть новий пароль:</label>
              <input type="password" value={confirmPass} onChange={e => setConfirmPass(e.target.value)} />

              {passError && <p className="error-message">{passError}</p>}

              <div className="profile-actions">
                <button onClick={handleChangePassword} className="btn-primary">Змінити</button>
                <button
                className="btn-secondary"
                onClick={() => {
                  // Ховаємо форму
                  setShowPassMode(false);
                  // Очищаємо поля і помилки
                  setPassError("");
                  setOldPass("");
                  setNewPass("");
                  setConfirmPass("");
                }}
              >
                Відмінити
              </button>
              </div>
            </div>
          )}
          {showAvatarSuccess && (
              <Modal
                modalType="success"
                message="Фото успішно завантажено!"
                onClose={() => setShowAvatarSuccess(false)}
              />
            )}

          {passSuccess && (
            <Modal modalType="success" message="Пароль успішно змінено!" onClose={() => setPassSuccess(false)} />
          )}
          {showProfileSuccess && (
            <Modal modalType="success" message="Профіль оновлено!" onClose={() => setShowProfileSuccess(false)} />
          )}

          {/* === Відгуки прямо на сторінці === */}
          <section ref={reviewSectionRef} className="profile-reviews-section">
          <h3>Відгуки ({reviewCount})</h3>
          {reviews.length > 0 ? (
            <div className="reviews-list">
              {reviews.map(r => {
                const avatarSrc = r.athlete_avatar_url
                  ? (r.athlete_avatar_url.startsWith('http')
                      ? r.athlete_avatar_url
                      : `${API_BASE_URL}${r.athlete_avatar_url}`)
                  : defaultAvatar;

                return (
                  <div key={r.id} className="review-card">
                    <div className="review-card-header">
                      <div className="review-user-info">
                        <img
                          src={avatarSrc}
                          alt="Аватар спортсмена"
                          className="reviewer-avatar-small"
                          onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = defaultAvatar; }}
                        />
                        <span className="review-username">
                          {r.athlete_name || 'Анонім'}
                        </span>
                      </div>
                      <div className="review-meta">
                        <span className="review-rating">
                          {'★'.repeat(r.rating) + '☆'.repeat(5 - r.rating)}
                        </span>
                        <small className="review-date">
                          {new Date(r.created_at).toLocaleDateString('uk-UA', {
                            day: '2-digit', month: '2-digit', year: 'numeric'
                          })}
                        </small>
                      </div>
                    </div>
                    <div className="review-text">{r.comment}</div>
                  </div>
                );
              })}
            </div>
          ) : (
            <p><em>Немає відгуків</em></p>
          )}
        </section>

        </>
      ) : (
        <p>Завантаження профілю…</p>
      )}
    </section>

            {/* Підтвердження видалення */}
      {showDeleteConfirm && trainingToDelete && (
        <Modal
          modalType="confirm"
          onClose={cancelDelete}
          message={
            <div style={{ textAlign: 'center', padding: '1em' }}>
              <p>
                Ви впевнені, що хочете видалити тренування
                <strong>  {trainingToDelete.section} </strong>
                від {formatDate(trainingToDelete.start_time)}?
              </p>
              <div style={{ display: 'flex', gap: '1em', justifyContent: 'center' }}>
                <button className="submit-btn save-btn" onClick={confirmDelete}>
                  Так
                </button>
                <button className="cancel-btn" onClick={cancelDelete}>
                  Ні
                </button>
              </div>
            </div>
          }
        />
      )}

      {/* Модалка результату видалення */}
      {showModal && (
        <Modal
          modalType="success"
          message={modalMessage}
          onClose={() => setShowModal(false)}
        />
      )}


      {/* Модальні вікна для перегляду/редагування тренувань */}
     



      {/* Modal успіху при створенні/оновленні тренування */}
      {showModal && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}
    </div>
  );
};

export default CoachDashboard;
