export const API_BASE_URL = 'http://localhost:8000';

async function checkResponse(res, defaultMsg) {
  if (!res.ok) {
    const errorData = await res.json();
    throw new Error(errorData.detail || defaultMsg);
  }
  return res.json();
}

// — Реєстрація та логін —
export const registerUser = payload =>
  fetch(`${API_BASE_URL}/register`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }).then(res => checkResponse(res, 'Помилка при реєстрації'));

export const loginUser = credentials =>
  fetch(`${API_BASE_URL}/login`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(credentials)
  }).then(res => checkResponse(res, 'Помилка авторизації'));

// — Універсальне отримання тренувань з додаванням avatar_url —
export const getTrainings = async () => {
  const res = await fetch(`${API_BASE_URL}/trainings`);
  const data = await checkResponse(res, 'Помилка отримання тренувань');
  data.trainings = data.trainings.map(t => ({
    ...t,
    avatar_url: t.avatar_url
      ? `${API_BASE_URL}${t.avatar_url}`
      : ''
  }));
  return data;
};

export const getAthleteTrainings = async athleteId => {
  const res = await fetch(
    `${API_BASE_URL}/athlete/trainings?athlete_id=${encodeURIComponent(athleteId)}`
  );
  const data = await checkResponse(res, 'Помилка отримання тренувань спортсмена');
  data.trainings = data.trainings.map(t => ({
    ...t,
    avatar_url: t.avatar_url
      ? `${API_BASE_URL}${t.avatar_url}`
      : ''
  }));
  return data;
};

// — Запис і скасування —
export const signupForTraining = (trainingId, athleteId) =>
  fetch(`${API_BASE_URL}/trainings/${trainingId}/signup`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ athlete_id: athleteId })
  }).then(res => checkResponse(res, 'Помилка запису на тренування'));

export const cancelEnrollment = (trainingId, athleteId) =>
  fetch(`${API_BASE_URL}/trainings/${trainingId}/cancel`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify({ athlete_id: athleteId })
  }).then(res => checkResponse(res, 'Помилка скасування запису'));

// — Створення та оновлення тренувань —
export const createTraining = trainingData =>
  fetch(`${API_BASE_URL}/create-training`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(trainingData)
  }).then(res => checkResponse(res, 'Помилка створення тренування'));

export const getCoachTrainings = coachId =>
  fetch(
    `${API_BASE_URL}/coach/trainings?coach_id=${encodeURIComponent(coachId)}`
  ).then(res => checkResponse(res, 'Помилка отримання тренувань'));

export const getTrainingParticipants = trainingId =>
  fetch(`${API_BASE_URL}/trainings/${trainingId}/participants`).then(res =>
    checkResponse(res, 'Помилка отримання учасників')
  );

export const updateTraining = (trainingId, updateData) =>
  fetch(`${API_BASE_URL}/trainings/${trainingId}`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(updateData)
  }).then(res => checkResponse(res, 'Помилка оновлення тренування'));

// — Профіль тренера та аватарка —
export const getCoachProfile = coachId =>
  fetch(
    `${API_BASE_URL}/coach/profile?coach_id=${encodeURIComponent(coachId)}`
  ).then(res => checkResponse(res, 'Помилка завантаження профілю'));

export const updateCoachProfile = payload =>
  fetch(`${API_BASE_URL}/coach/profile`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }).then(res => checkResponse(res, 'Помилка оновлення профілю'));

export const changeCoachPassword = payload =>
  fetch(`${API_BASE_URL}/coach/change-password`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(payload)
  }).then(res => checkResponse(res, 'Помилка зміни пароля'));

export const uploadCoachAvatar = (userId, file) => {
  const formData = new FormData();
  formData.append('file', file);
  return fetch(
    `${API_BASE_URL}/coach/avatar?user_id=${encodeURIComponent(userId)}`,
    {
      method: 'POST',
      body: formData
    }
  )
    .then(res => checkResponse(res, 'Помилка завантаження аватарки'))
    .then(json => ({
      avatar_url: `${API_BASE_URL}${json.avatar_url}`
    }));
};
