import React from 'react';

const overlayStyle = {
  position: 'fixed',
  top: 0,
  left: 0,
  width: '100%',
  height: '100%',
  background: 'rgba(0, 0, 0, 0.6)',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  zIndex: 1000,
};

const baseContentStyle = {
  padding: '20px',
  borderRadius: '10px',
  textAlign: 'center',
  maxWidth: '600px',
  width: '90%',
  boxShadow: '0 4px 12px rgba(0,0,0,0.15)',
};

const typeStyles = {
  success: {
    backgroundColor: '#dff0d8', // ніжний зелений фон
    border: '2px solid #3c763d', // темнозелений бордер
  },
  info: {
    backgroundColor: '#d9edf7', // ніжний блакитний фон
    border: '2px solid #31708f', // синій бордер
  },
  edit: {
    backgroundColor: '#fcf8e3', // світло-жовтий фон
    border: '2px solid #8a6d3b', // бурштиновий бордер
  },
};

const closeButtonBaseStyle = {
  border: 'none',
  padding: '8px 16px',
  marginTop: '20px',
  borderRadius: '5px',
  cursor: 'pointer',
};

const buttonStyles = {
  success: {
    backgroundColor: '#5cb85c', // зелений фон
    color: '#fff',
  },
  default: {
    backgroundColor: '#dc3545', // червоний фон
    color: '#fff',
  },
};

const Modal = ({ message, onClose, modalType }) => {
  // Визначаємо стиль модального вікна, комбінуючи базовий стиль з типовим
  const contentStyle = {
    ...baseContentStyle,
    ...(modalType && typeStyles[modalType] ? typeStyles[modalType] : {}),
  };

  // Якщо modalType "success", використовуємо зелений стиль для кнопки, інакше – стандартний
  const buttonStyle = {
    ...closeButtonBaseStyle,
    ...(modalType === 'success' ? buttonStyles.success : buttonStyles.default),
  };

  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  return (
    <div style={overlayStyle} onClick={handleOverlayClick}>
      <div style={contentStyle} onClick={(e) => e.stopPropagation()}>
        {message}
        <button style={buttonStyle} onClick={onClose}>
          ОК
        </button>
      </div>
    </div>
  );
};

export default Modal;
