// src/components/AthleteDashboard/YourTrainingsSection.js

import React from 'react';
import './AthleteDashboard.css';
import { API_BASE_URL } from '../../../services/api';

const DEFAULT_AVATAR = `${API_BASE_URL}/avatars/default.png`;

export default function YourTrainingsSection({
  registeredTrainings,
  sportLogos,
  onViewDetails,
  onCancel,
  onViewCoach
}) {
  const fmtDate = isoString =>
    new Date(isoString).toLocaleDateString('uk-UA', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric'
    });
  const fmtTime = isoString =>
    new Date(isoString).toLocaleTimeString([], {
      hour: '2-digit',
      minute: '2-digit'
    });

  // Сортуємо за датою початку
  const sorted = [...registeredTrainings].sort(
    (a, b) => new Date(a.start_time) - new Date(b.start_time)
  );

  return (
    <div>
      <h2>Ваші тренування</h2>
      {sorted.length === 0 ? (
        <p>Немає записів.</p>
      ) : (
        <div className="registered-cards-grid">
          {sorted.map(training => {
            // Формуємо абсолютний URL аватарки
            const avatar = training.coach_avatar_url
              ? (training.coach_avatar_url.startsWith('http')
                  ? training.coach_avatar_url
                  : `${API_BASE_URL}${training.coach_avatar_url}`)
              : DEFAULT_AVATAR;

            return (
              <div
                key={training._id}
                className="training-card registered-card new-design"
              >
                <div className="card-header">
                  <span className="card-date">
                    {fmtDate(training.start_time)}
                  </span>
                  <span className="card-time">
                    {`${fmtTime(training.start_time)} – ${fmtTime(
                      training.end_time
                    )}`}
                  </span>
                </div>
                <div className="card-body">
                  {sportLogos[training.section] && (
                    <img
                      src={sportLogos[training.section]}
                      alt={training.section}
                      className="registered-card-logo"
                    />
                  )}
                  <p className="registered-section-name">
                    {training.section}
                  </p>
                  <p className="registered-trainer-line">
                    із{' '}
                    <img
                      src={avatar}
                      alt="Avatar тренера"
                      className="coach-avatar-small"
                      onError={e => {
                        e.currentTarget.onerror = null;
                        e.currentTarget.src = DEFAULT_AVATAR;
                      }}
                    />{' '}
                    <span
                      className="coach-name-link"
                      onClick={() => onViewCoach(training.coach_id)}
                    >
                      {training.coach_name}
                    </span>
                  </p>
                </div>
                <div className="card-footer">
                  <button
                    className="action-btn view-btn"
                    onClick={() => onViewDetails(training)}
                  >
                    Переглянути
                  </button>
                  <button
                    className="action-btn cancel-btn"
                    onClick={() => onCancel(training)}
                  >
                    Відмінити запис
                  </button>
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}
