import React from 'react';

const Modal = ({ message, onClose, modalType }) => {
  const handleOverlayClick = (e) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  // Визначаємо клас для модального вікна залежно від типу
  // Для success, info та edit додамо відповідні класи
  let modalClass = 'modal-content';
  if (modalType === 'success') {
    modalClass += ' success-modal';
  } else if (modalType === 'info') {
    modalClass += ' info-modal';
  } else if (modalType === 'edit') {
    modalClass += ' edit-modal';
  }

  return (
    <div className="modal-overlay" onClick={handleOverlayClick}>
      <div className={modalClass} onClick={(e) => e.stopPropagation()}>
        {message}
        <button onClick={onClose}>Ок</button>
      </div>
    </div>
  );
};

export default Modal;
