// src/components/Dashboards/CoachDashboards/NavigationPanel.jsx
import React from 'react';
import './NavigationPanel.css';

export default function NavigationPanel({ activeTab, onTabChange, onLogout }) {
  return (
    <nav className="navigation-panel">
      {/* Ліва група вкладок */}
      <div className="tabs">
        <button
          className={`tab ${activeTab === 'my-trainings' ? 'active' : ''}`}
          onClick={() => onTabChange('my-trainings')}
        >
          Ваші тренування
        </button>
        <button
          className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => onTabChange('profile')}
        >
          Ваш профіль
        </button>
        <button
          className={`tab ${activeTab === 'rating' ? 'active' : ''}`}
          onClick={() => onTabChange('rating')}
        >
          Ваш рейтинг
        </button>
      </div>

      {/* Права кнопка виходу */}
      <button className="logout-btn" onClick={onLogout} title="Вийти">
        {/* Проста SVG-іконка виходу */}
        <svg viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
          <path d="M16 13v-2H7V8l-5 4 5 4v-3zM12 2h8a2 2 0 012 2v4h-2V4h-8V2zm8 14v-4h2v4a2 2 0 01-2 2h-8v-2h8z"/>
        </svg>
      </button>
    </nav>
  );
}
