import React, { useState } from 'react';
import { CSSTransition } from 'react-transition-group';
import { registerUser, loginUser } from '../../services/api';
import Modal from '../../components/Common/Modal';
import { useHistory } from 'react-router-dom';
import '../../styles.css';

const AuthPage = () => {
  const [mode, setMode] = useState('register'); // 'register' або 'login'
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    email: '',
    password: '',
    section: ''
  });
  // За замовчуванням реєстрація для тренера
  const [userType, setUserType] = useState('coach');
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [role, setRole] = useState('');
  const history = useHistory();

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (formData.password.length < 6) {
      setError('Пароль має містити не менше 6 символів.');
      return;
    }
    const payload = {
      type: userType,
      data: {
        name: formData.name,
        age: parseInt(formData.age, 10),
        email: formData.email,
        password: formData.password,
        ...(userType === 'coach' && { section: formData.section })
      }
    };
    try {
      await registerUser(payload);
      setModalMessage(
        `Акаунт типу ${userType === 'coach' ? 'Тренер' : 'Спортсмен'} успішно зареєстрований!`
      );
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await loginUser({
        email: formData.email,
        password: formData.password
      });
      setModalMessage(
        `Ваш акаунт успішно авторизовано! Ласкаво просимо до порталу, ${res.name}!`
      );
      setRole(res.role);
      setShowModal(true);
    } catch (err) {
      setError(err.message);
    }
  };

  const handleModalClose = () => {
    setShowModal(false);
    if (mode === 'register') {
      setMode('login');
      setError('');
      setFormData({ name: '', age: '', email: '', password: '', section: '' });
    } else if (mode === 'login') {
      if (role === 'athlete') {
        history.push('/athlete-dashboard');
      } else if (role === 'coach') {
        history.push('/coach-dashboard');
      }
    }
  };

  // Використовуємо властивість height замість maxHeight для плавної анімації
  const sectionStyles = {
    height: userType === 'coach' ? '40px' : '0px',
    opacity: userType === 'coach' ? 1 : 0,
    transition: 'height 0.15s ease, opacity 0.15s ease'
  };

  return (
    <div className="page-container">
      <div className="main-content">
        <div className="welcome-text">
          <h1>Ласкаво просимо до Порталу Тренувань!</h1>
          <p>Найкращий керівник твоєго спортивного життя</p>
        </div>
        <CSSTransition in={true} appear={true} timeout={300} classNames="form-transition">
          <div className={`form-container ${mode === 'register' ? 'register-form' : 'login-form'}`}>
            <div className="form-content">
              {mode === 'register' ? (
                <>
                  <h2>Реєстрація</h2>
                  <div className="user-type-group">
                    <span className="user-type-label">Оберіть бажаний тип користувача</span>
                    <div className="user-type-switch">
                      <button
                        onClick={() => setUserType('coach')}
                        className={userType === 'coach' ? 'selected' : ''}
                      >
                        Тренер
                      </button>
                      <button
                        onClick={() => setUserType('athlete')}
                        className={userType === 'athlete' ? 'selected' : ''}
                      >
                        Спортсмен
                      </button>
                    </div>
                  </div>
                  <form onSubmit={handleRegister}>
                    <input type="text" name="name" placeholder="Ім'я" value={formData.name} onChange={handleChange} required />
                    <input type="number" name="age" placeholder="Вік" value={formData.age} onChange={handleChange} required />

                    {/* Контейнер для поля "Секція" з анімацією та фіксованою висотою */}
                    <div className="field-container" style={sectionStyles}>
                      <input type="text" name="section" placeholder="Секція" value={formData.section} onChange={handleChange} required />
                    </div>

                    <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                    <input type="password" name="password" placeholder="Пароль" value={formData.password} onChange={handleChange} required />
                    {error && <p className="error-message">{error}</p>}
                    <button type="submit">Зареєструватися</button>
                  </form>
                  <div className="toggle-container">
                    <span>Вже маєте акаунт? </span>
                    <span className="toggle-link" onClick={() => {
                      setMode('login');
                      setError('');
                      setFormData({ name: '', age: '', email: '', password: '', section: '' });
                    }}>
                      Увійдіть
                    </span>
                  </div>
                </>
              ) : (
                <>
                  <h2>Авторизація</h2>
                  <form onSubmit={handleLogin}>
                    <input type="email" name="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                    <input type="password" name="password" placeholder="Пароль" value={formData.password} onChange={handleChange} required />
                    {error && <p className="error-message">{error}</p>}
                    <button type="submit">Увійдіть</button>
                  </form>
                  <div className="toggle-container">
                    <span>Ще не маєте акаунт? </span>
                    <span className="toggle-link" onClick={() => {
                      setMode('register');
                      setError('');
                      setFormData({ name: '', age: '', email: '', password: '', section: '' });
                    }}>
                      Реєстрація
                    </span>
                  </div>
                </>
              )}
              {showModal && <Modal message={modalMessage} onClose={handleModalClose} />}
            </div>
          </div>
        </CSSTransition>
      </div>
      <footer className="footer">
        <p>
          Технічна підтримка: +380 (XX) XXX-XX-XX | Email: support@company.ua | Адреса: вул. Хрещатик, 1, Київ
        </p>
      </footer>
    </div>
  );
};

export default AuthPage;
