import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import Modal from '../../Common/Modal';
import './NavigationPanel.css';

export default function NavigationPanel({ activeTab, onTabChange }) {
  const history = useHistory();
  const [showLogoutConfirm, setShowLogoutConfirm] = useState(false);

  const handleLogoutClick = () => {
    setShowLogoutConfirm(true);
  };

  const handleConfirmLogout = () => {
    // –û—á–∏—Å—Ç–∏—Ç–∏ –ª–æ–∫–∞–ª—å–Ω–µ —Å—Ö–æ–≤–∏—â–µ
    localStorage.removeItem('userId');
    // –ü–µ—Ä–µ–Ω–∞–ø—Ä–∞–≤–∏—Ç–∏ –Ω–∞ –∫–æ—Ä—ñ–Ω—å (/)
    history.push('/');
  };

  const handleCancelLogout = () => {
    setShowLogoutConfirm(false);
  };

  return (
    <>
      <nav className="navigation-panel">
        <button
          className={`tab ${activeTab === 'my-trainings' ? 'active' : ''}`}
          onClick={() => onTabChange('my-trainings')}
        >
          –í–∞—à—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è
        </button>
        <button
          className={`tab ${activeTab === 'profile' ? 'active' : ''}`}
          onClick={() => onTabChange('profile')}
        >
          –í–∞—à –ø—Ä–æ—Ñ—ñ–ª—å
        </button>
        <button
          className="logout-btn"
          onClick={handleLogoutClick}
          title="–í–∏–π—Ç–∏ –∑ –∞–∫–∞—É–Ω—Ç—É"
        >
          üîí
        </button>
      </nav>

      {showLogoutConfirm && (
        <Modal
          modalType="confirm"
          onClose={handleCancelLogout}
          message={
            <div style={{ textAlign: 'center', padding: '1em' }}>
              <p>–í–∏ –≤–ø–µ–≤–Ω–µ–Ω—ñ, —â–æ —Ö–æ—á–µ—Ç–µ –≤–∏–π—Ç–∏ –∑ –∞–∫–∞—É–Ω—Ç—É?</p>
              <div style={{ display: 'flex', justifyContent: 'center', gap: '1em' }}>
                <button
                  className="submit-btn"
                  onClick={handleConfirmLogout}
                >
                  –¢–∞–∫
                </button>
                <button
                  className="cancel-btn"
                  onClick={handleCancelLogout}
                >
                  –ù—ñ
                </button>
              </div>
            </div>
          }
        />
      )}
    </>
  );
}
