import React, { useState, useEffect } from 'react';
import {
  API_BASE_URL,
  getTrainings,
  getAthleteTrainings,
  getCoachProfile,
  getCoachReviews,
  submitReview,
  updateReview,
  deleteReview,
  signupForTraining,
  cancelEnrollment
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/AthleteDashboard.css';

// Логотипи секцій
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// Мапа секція → логотип
const sportLogos = {
  "Футбол": footballLogo,
  "Баскетбол": basketballLogo,
  "Бадмінтон": badmintonLogo,
  "Волейбол": volleyballLogo,
  "Бейсбол": baseballLogo,
  "Регбі": rugbyLogo,
  "Хокей": hockeyLogo,
  "Карате": karateLogo,
  "Бокс": boxingLogo,
  "Ушу": wushuLogo,
  "Джиу-джитсу": jiuJitsuLogo,
  "ММА": mmaLogo,
  "Біг": runningLogo,
  "Стрибки": jumpingLogo,
  "Метання": throwingLogo,
  "Плавання": swimmingLogo,
  "Вітрильний спорт": sailingLogo,
  "Каноїзм": canoeingLogo,
  "Скейбординг": skateboardingLogo,
  "Сноубординг": snowboardingLogo,
  "Альпінізм": climbingLogo,
  "Фітнес": fitnessLogo,
  "Силові тренування": strengthTrainingLogo,
  "Йога": yogaLogo
};

// Мапа категорія → список секцій
const sportCategories = {
  "Ігри з м'ячем": ["Футбол","Баскетбол","Бадмінтон","Волейбол","Бейсбол","Регбі","Хокей"],
  "Бойові мистецтва": ["Карате","Бокс","Ушу","Джиу-джитсу","ММА"],
  "Легка атлетика": ["Біг","Стрибки","Метання"],
  "Водні види спорту": ["Плавання","Вітрильний спорт","Каноїзм"],
  "Екстремальні види спорту": ["Скейбординг","Сноубординг","Альпінізм"],
  "Фітнес та тренажерний зал": ["Фітнес","Силові тренування","Йога"]
};

const DEFAULT_AVATAR = `${API_BASE_URL}/avatars/default.png`;

const AthleteDashboard = () => {
  const [allTrainings, setAllTrainings]               = useState([]);
  const [registeredTrainings, setRegisteredTrainings] = useState([]);
  const [error, setError]                              = useState('');
  const [showModal, setShowModal]                      = useState(false);
  const [modalMessage, setModalMessage]                = useState('');
  const [showCancelModal, setShowCancelModal]          = useState(false);
  const [selectedCancelTraining, setSelectedCancelTraining] = useState(null);
  const [selectedTraining, setSelectedTraining]        = useState(null);

  // Coach profile & reviews
  const [coachProfile, setCoachProfile] = useState(null);
  const [showCoachModal, setShowCoachModal] = useState(false);
  const [coachError, setCoachError] = useState('');
  const [reviews, setReviews] = useState([]);
  const [avgRating, setAvgRating] = useState(null);
  const [reviewCount, setReviewCount] = useState(0);
  const [newRating, setNewRating] = useState(5);
  const [newComment, setNewComment] = useState('');
  const [editingReviewId, setEditingReviewId] = useState(null);

  const [filterCategory, setFilterCategory] = useState('');
  const [filterSection, setFilterSection]   = useState('');
  const [sortOption, setSortOption]         = useState("date_asc");

  useEffect(() => {
    fetchAllTrainings();
    fetchRegisteredTrainings();
  }, []);

  const fetchAllTrainings = async () => {
    try {
      const { trainings } = await getTrainings();
      setAllTrainings(trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchRegisteredTrainings = async () => {
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен");
      return;
    }
    try {
      const { trainings } = await getAthleteTrainings(athleteId);
      setRegisteredTrainings(trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDate = t => new Date(t)
    .toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t)
    .toLocaleTimeString([],{hour:'2-digit',minute:'2-digit'});

  const registeredIds = new Set(registeredTrainings.map(t => t._id));
  let available = allTrainings.filter(t => !registeredIds.has(t._id));
  if (filterCategory) available = available.filter(t => t.category === filterCategory);
  if (filterSection)  available = available.filter(t => t.section  === filterSection);
  available.sort((a,b)=>{
    switch(sortOption){
      case "date_asc":  return new Date(a.start_time) - new Date(b.start_time);
      case "date_desc": return new Date(b.start_time) - new Date(a.start_time);
      case "price_asc": return a.price - b.price;
      case "price_desc":return b.price - a.price;
      default: return 0;
    }
  });

  const handleCoachClick = async coachId => {
    setCoachError('');
    try {
      const { profile } = await getCoachProfile(coachId);
      setCoachProfile(profile);
      const { reviews, average_rating, count } = await getCoachReviews(coachId);
      setReviews(reviews);
      setAvgRating(average_rating);
      setReviewCount(count);
      setNewRating(5);
      setNewComment('');
      setEditingReviewId(null);
      setShowCoachModal(true);
    } catch (err) {
      setCoachError(err.message);
    }
  };

  const handleSubmitReview = async () => {
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setCoachError("Увійдіть щоб залишити відгук");
      return;
    }
    try {
      if (editingReviewId) {
        await updateReview(editingReviewId, { rating: newRating, comment: newComment });
      } else {
        await submitReview({ coach_id: coachProfile.user_id, athlete_id: athleteId, rating: newRating, comment: newComment });
      }
      const { reviews, average_rating, count } = await getCoachReviews(coachProfile.user_id);
      setReviews(reviews);
      setAvgRating(average_rating);
      setReviewCount(count);
      setEditingReviewId(null);
      setNewRating(5);
      setNewComment('');
    } catch (err) {
      setCoachError(err.message);
    }
  };

  const handleDeleteReview = async id => {
    const athleteId = localStorage.getItem('userId');
    try {
      await deleteReview(id, athleteId);
      const { reviews, average_rating, count } = await getCoachReviews(coachProfile.user_id);
      setReviews(reviews);
      setAvgRating(average_rating);
      setReviewCount(count);
      setEditingReviewId(null);
      setNewRating(5);
      setNewComment('');
    } catch (err) {
      setCoachError(err.message);
    }
  };

  const startEditReview = r => {
    setEditingReviewId(r.id);
    setNewRating(r.rating);
    setNewComment(r.comment);
  };

  const handleSignup = async id => {
    setError('');
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) { setError("Увійдіть як спортсмен"); return; }
    try {
      const res = await signupForTraining(id, athleteId);
      setModalMessage(res.message);
      setShowModal(true);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
    }
  };
  const openCancelModal = t => {
    setSelectedCancelTraining(t);
    setShowCancelModal(true);
  };
  const handleCancelEnrollment = async () => {
    const athleteId = localStorage.getItem('userId');
    try {
      await cancelEnrollment(selectedCancelTraining._id, athleteId);
      setModalMessage("Запис успішно скасовано.");
      setShowModal(true);
      setShowCancelModal(false);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
      setShowCancelModal(false);
    }
  };

  const handleViewDetails = t => { setSelectedTraining(t); setShowModal(true); };
  const handleModalClose = () => { setShowModal(false); setSelectedTraining(null); };
  const handleCancelModalClose = () => setShowCancelModal(false);

  const sortedRegistered = [...registeredTrainings].sort(
    (a,b)=> new Date(a.start_time) - new Date(b.start_time)
  );

  return (
    <div className="dashboard-container">
      <h1>Кабінет Спортсмена</h1>
      {error && <p className="error-message">{error}</p>}

      {/* — Доступні тренування — */}
      <section className="available-trainings-section">
        {/* ... тут код доступних тренувань без змін ... */}
      </section>

      {/* — Ваші тренування — */}
      <section className="registered-trainings-section">
        {/* ... тут код ваших тренувань без змін ... */}
      </section>

      {/* Підтвердження скасування */}
      {showCancelModal && (
        <Modal modalType="confirm" onClose={handleCancelModalClose} message={
          <div className="cancel-modal">
            <p>Ви впевнені, що хочете відмінити запис?</p>
            <div className="cancel-modal-buttons">
              <button className="confirm-btn" onClick={handleCancelEnrollment}>Так</button>
              <button className="cancel-btn" onClick={handleCancelModalClose}>Ні</button>
            </div>
          </div>
        }/>
      )}

      {/* Деталі тренування */}
      {showModal && selectedTraining && (
        <Modal modalType="info" onClose={handleModalClose} message={
          <div className="details-modal">
            {/* ... тут код деталей тренування без змін ... */}
          </div>
        }/>
      )}
      {showModal && !selectedTraining && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose}/>
      )}

      {/* Профіль та відгуки тренера */}
      {showCoachModal && coachProfile && (
        <Modal modalType="info" onClose={() => setShowCoachModal(false)} message={
          <div className="coach-details-container">
            {/* — Інформаційний блок */}
            <div className="coach-info-block">
              <img src={coachProfile.avatar_url||DEFAULT_AVATAR} alt="Аватар тренера" className="coach-avatar-large" />
              <h3 className="coach-name">{coachProfile.name}</h3>
              <p><strong>Вік:</strong> {coachProfile.age}</p>
              <p><strong>Email:</strong> {coachProfile.email}</p>
              <p><strong>Телефон:</strong> {coachProfile.phone||<em>Немає</em>}</p>
              <p className="coach-description"><strong>Опис:</strong> {coachProfile.description||<em>Немає опису</em>}</p>
              <div className="reviews-summary-block">
                <p><strong>Середня оцінка:</strong> {avgRating??'—'}</p>
                <p><strong>Відгуків:</strong> {reviewCount}</p>
              </div>
            </div>

            {/* — Блок відгуків та форми */}
            <div className="coach-reviews-block">
              <h4>Відгуки</h4>
              <div className="reviews-list-scroll">
                {reviews.length > 0 ? reviews.map(r=>(
                  <div key={r.id} className="review-item">
                    <div className="review-header">
                      <span className="review-rating">{'★'.repeat(r.rating)+'☆'.repeat(5-r.rating)}</span>
                      <small>{new Date(r.created_at).toLocaleString('uk-UA')}</small>
                    </div>
                    <p className="review-comment"><strong>{r.athlete_name||'Анонім'}:</strong> {r.comment}</p>
                    {r.athlete_id===localStorage.getItem('userId') && (
                      <div className="review-actions">
                        <button onClick={()=>startEditReview(r)}>Редагувати</button>
                        <button onClick={()=>handleDeleteReview(r.id)}>Видалити</button>
                      </div>
                    )}
                  </div>
                )) : <p className="no-reviews"><em>Немає відгуків</em></p>}
              </div>

              {/* — Форма створення / редагування відгуку */}
              <div className="submit-review">
                <h4>{editingReviewId ? 'Редагувати відгук' : 'Залишити відгук'}</h4>
                {coachError && <p className="error-message">{coachError}</p>}
                <label>Оцінка:</label>
                <select value={newRating} onChange={e=>setNewRating(parseInt(e.target.value,10))}>
                  {[5,4,3,2,1].map(n=><option key={n} value={n}>{'★'.repeat(n)}</option>)}
                </select>
                <label>Коментар:</label>
                <textarea className="review-textarea" value={newComment} onChange={e=>setNewComment(e.target.value)} placeholder="Ваш відгук..." />
                <button className="submit-review-btn" onClick={handleSubmitReview}>
                  {editingReviewId? 'Оновити відгук' : 'Відправити відгук'}
                </button>
              </div>
            </div>
          </div>
        }/>
      )}
    </div>
  );
};

export default AthleteDashboard;
