// src/pages/CoachDashboard.js

import React, { useState, useEffect } from 'react';
import {
  createTraining,
  getCoachTrainings,
  getTrainingParticipants,
  updateTraining,
  getCoachProfile,
  updateCoachProfile,
  changeCoachPassword
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/CoachDashboard.css';

// Логотипи секцій
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// Мапа секція → логотип
const sportLogos = {
  "Футбол": footballLogo,
  "Баскетбол": basketballLogo,
  "Бадмінтон": badmintonLogo,
  "Волейбол": volleyballLogo,
  "Бейсбол": baseballLogo,
  "Регбі": rugbyLogo,
  "Хокей": hockeyLogo,
  "Карате": karateLogo,
  "Бокс": boxingLogo,
  "Ушу": wushuLogo,
  "Джиу-джитсу": jiuJitsuLogo,
  "ММА": mmaLogo,
  "Біг": runningLogo,
  "Стрибки": jumpingLogo,
  "Метання": throwingLogo,
  "Плавання": swimmingLogo,
  "Вітрильний спорт": sailingLogo,
  "Каноїзм": canoeingLogo,
  "Скейбординг": skateboardingLogo,
  "Сноубординг": snowboardingLogo,
  "Альпінізм": climbingLogo,
  "Фітнес": fitnessLogo,
  "Силові тренування": strengthTrainingLogo,
  "Йога": yogaLogo
};

// Категорії та секції
const sportCategories = {
  "Ігри з м'ячем": ["Футбол","Баскетбол","Бадмінтон","Волейбол","Бейсбол","Регбі","Хокей"],
  "Бойові мистецтва": ["Карате","Бокс","Ушу","Джиу-джитсу","ММА"],
  "Легка атлетика": ["Біг","Стрибки","Метання"],
  "Водні види спорту": ["Плавання","Вітрильний спорт","Каноїзм"],
  "Екстремальні види спорту": ["Скейбординг","Сноубординг","Альпінізм"],
  "Фітнес та тренажерний зал": ["Фітнес","Силові тренування","Йога"]
};

const CoachDashboard = () => {
  // — Стани створення тренувань —
  const [trainingData, setTrainingData]   = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'UAH',
    category:'', section:''
  });
  const [errors, setErrors]               = useState({});
  const [modalMessage, setModalMessage]   = useState('');
  const [showModal, setShowModal]         = useState(false);
  const [trainings, setTrainings]         = useState([]);
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants]   = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);
  const [editMode, setEditMode]           = useState(false);
  const [editData, setEditData]           = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'',
    category:'', section:''
  });

  // — Стани профілю —
  const [profile, setProfile]             = useState(null);
  const [editProfileMode, setEditProfileMode] = useState(false);
  const [nameInput, setNameInput]         = useState('');
  const [ageInput, setAgeInput]           = useState('');
  const [phoneInput, setPhoneInput]       = useState('');
  const [description, setDescription]     = useState('');
  const [favoriteSports, setFavoriteSports] = useState([]);
  const [profileError, setProfileError]   = useState('');
  const [showProfileSuccess, setShowProfileSuccess] = useState(false);

  // — Стани зміни пароля —
  const [showPassMode, setShowPassMode]   = useState(false);
  const [oldPass, setOldPass]             = useState('');
  const [newPass, setNewPass]             = useState('');
  const [confirmPass, setConfirmPass]     = useState('');
  const [passError, setPassError]         = useState('');
  const [passSuccess, setPassSuccess]     = useState(false);

  const today = new Date().toISOString().split('T')[0];
  const allSections = Object.values(sportCategories).flat();

  // Форматери
  const formatDate = t => new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});

  // — Fetch тренувань і профілю —
  const fetchTrainings = async () => {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { trainings } = await getCoachTrainings(coachId);
      setTrainings(trainings);
    } catch(err) {
      console.error(err);
    }
  };
  const fetchProfile = async () => {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { profile } = await getCoachProfile(coachId);
      setProfile(profile);
      setNameInput(profile.name);
      setAgeInput(profile.age);
      setPhoneInput(profile.phone || '');
      setDescription(profile.description || '');
      setFavoriteSports(profile.favorite_sports || []);
    } catch(err) {
      console.error(err);
    }
  };
  useEffect(() => {
    fetchTrainings();
    fetchProfile();
  }, []);

  // — Обробники профілю —
  const toggleSport = sport => {
    setFavoriteSports(prev =>
      prev.includes(sport)
        ? prev.filter(s => s !== sport)
        : [...prev, sport]
    );
  };

  const handleSaveProfile = async () => {
    setProfileError('');
    try {
      await updateCoachProfile({
        user_id: profile.user_id,
        name: nameInput,
        age: parseInt(ageInput, 10),
        phone: phoneInput,
        description,
        favorite_sports: favoriteSports
      });
      setShowProfileSuccess(true);
      setEditProfileMode(false);
      fetchProfile();
    } catch (err) {
      setProfileError(err.message);
    }
  };

  // — Обробники зміни пароля —
  const handleChangePassword = async () => {
    setPassError('');
    if (!oldPass || !newPass || !confirmPass) {
      setPassError('Всі поля мають бути заповнені');
      return;
    }
    if (newPass.length < 6) {
      setPassError('Новий пароль має містити не менше 6 символів');
      return;
    }
    if (newPass !== confirmPass) {
      setPassError('Паролі не збігаються, перевірте ще раз');
      return;
    }
    try {
      await changeCoachPassword({
        user_id: profile.user_id,
        old_password: oldPass,
        new_password: newPass,
        confirm_password: confirmPass
      });
      setPassSuccess(true);
      setShowPassMode(false);
      setOldPass(''); setNewPass(''); setConfirmPass('');
    } catch(err) {
      setPassError(err.message);
    }
  };

  // — Обробники тренувань —
  const handleChange = e => setTrainingData({...trainingData, [e.target.name]: e.target.value});
  const handleCategoryChange = e => setTrainingData({
    ...trainingData,
    category: e.target.value,
    section: ''
  });
  const getMinTime = () => trainingData.date === today
    ? new Date().toTimeString().slice(0,5)
    : '00:00';

  const handleSubmit = async e => {
    e.preventDefault();
    setErrors({});
    const coachId = localStorage.getItem('userId');
    if (!coachId) {
      setErrors({ general:'Будь ласка, увійдіть повторно.' });
      return;
    }
    if (!trainingData.date || !trainingData.start_time || !trainingData.end_time) {
      setErrors({ general:'Оберіть дату та час.' });
      return;
    }
    if (!trainingData.category || !trainingData.section) {
      setErrors({ general:'Оберіть категорію/секцію.' });
      return;
    }
    if (trainingData.start_time >= trainingData.end_time) {
      setErrors({ end_time:'Час завершення має бути пізніше.' });
      return;
    }
    const startDT = new Date(`${trainingData.date}T${trainingData.start_time}`);
    if (startDT < new Date()) {
      setErrors({ general:'Не можна створювати в минулому.' });
      return;
    }
    const payload = {
      coach_id: coachId,
      start_time: `${trainingData.date}T${trainingData.start_time}`,
      end_time:   `${trainingData.date}T${trainingData.end_time}`,
      price:      parseFloat(trainingData.price),
      spots:      parseInt(trainingData.spots,10),
      currency:   trainingData.currency,
      category:   trainingData.category,
      section:    trainingData.section
    };
    try {
      await createTraining(payload);
      setModalMessage('Тренування успішно створено!');
      setShowModal(true);
      setTrainingData({
        date:'', start_time:'', end_time:'',
        price:'', spots:'', currency:'UAH',
        category:'', section:''
      });
      fetchTrainings();
    } catch(err) {
      setErrors({ general: err.message });
    }
  };

  const handleModalClose = () => setShowModal(false);

  const handleViewParticipants = async t => {
    setSelectedTraining(t);
    try {
      const { participants } = await getTrainingParticipants(t._id);
      setParticipants(participants);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch(err) {
      console.error(err);
    }
  };
  const closeParticipantsModal = () => {
    setShowParticipantsModal(false);
    setSelectedTraining(null);
    setParticipants([]);
    setEditMode(false);
  };

  const handleEdit = () => {
    const t = selectedTraining;
    const start = new Date(t.start_time),
          end   = new Date(t.end_time),
          signed= t.participants?.length || 0;
    setEditData({
      date: start.toISOString().split('T')[0],
      start_time: start.toTimeString().slice(0,5),
      end_time:   end  .toTimeString().slice(0,5),
      price:      t.price,
      spots:      signed + parseInt(t.spots,10),
      currency:   t.currency,
      category:   t.category,
      section:    t.section
    });
    setEditMode(true);
  };
  const handleEditChange = e => setEditData({...editData, [e.target.name]: e.target.value});

  const handleEditSubmit = async e => {
    e.preventDefault();
    setErrors({});
    if (!editData.date || !editData.start_time || !editData.end_time) {
      setErrors({ edit:'Оберіть дату та час.' });
      return;
    }
    if (editData.start_time >= editData.end_time) {
      setErrors({ edit:'Час завершення має бути пізніше.' });
      return;
    }
    const newStart = new Date(`${editData.date}T${editData.start_time}`);
    if (newStart < new Date()) {
      setErrors({ edit:'Не можна у минулому.' });
      return;
    }
    const signed = selectedTraining.participants?.length || 0;
    const total  = parseInt(editData.spots,10);
    const payload = {
      start_time: `${editData.date}T${editData.start_time}`,
      end_time:   `${editData.date}T${editData.end_time}`,
      price:      parseFloat(editData.price),
      currency:   editData.currency,
      category:   editData.category,
      section:    editData.section
    };
    if (total !== signed + parseInt(selectedTraining.spots,10)) {
      if (total < signed) {
        setErrors({ edit:'Місць менше за вже записані.' });
        return;
      }
      payload.spots = total - signed;
    }
    try {
      await updateTraining(selectedTraining._id, payload);
      setModalMessage('Тренування успішно оновлено!');
      setShowModal(true);
      setEditMode(false);
      fetchTrainings();
      closeParticipantsModal();
    } catch(err) {
      setErrors({ edit: err.message });
    }
  };
  const cancelEdit = () => setEditMode(false);

  const sorted = [...trainings].sort(
    (a,b) => new Date(a.start_time) - new Date(b.start_time)
  );

  return (
    <div className="dashboard-container">
      <h1>Кабінет Тренера</h1>

      {/* Форма створення тренування */}
      <div className="form-wrapper">
        <h2>Створити тренування</h2>
        <p>Заповніть форму, щоб додати нове тренування.</p>
        {errors.general && <p className="error-message">{errors.general}</p>}
        <form onSubmit={handleSubmit} className="create-training-form">
          <div className="form-row">
            <label htmlFor="date">Дата:</label>
            <input
              type="date"
              name="date"
              id="date"
              value={trainingData.date}
              onChange={handleChange}
              min={today}
              required
            />
          </div>
          <div className="form-row time-group">
            <div className="time-field">
              <label htmlFor="start_time">Початок:</label>
              <input
                type="time"
                name="start_time"
                id="start_time"
                value={trainingData.start_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
            </div>
            <div className="time-field">
              <label htmlFor="end_time">Кінець:</label>
              <input
                type="time"
                name="end_time"
                id="end_time"
                value={trainingData.end_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
              {errors.end_time && <p className="error-message">{errors.end_time}</p>}
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="category">Категорія:</label>
            <select
              name="category"
              id="category"
              value={trainingData.category}
              onChange={handleCategoryChange}
              required
            >
              <option value="">-- Виберіть категорію --</option>
              {Object.keys(sportCategories).map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <div className="form-row">
            <label htmlFor="section">Секція:</label>
            <select
              name="section"
              id="section"
              value={trainingData.section}
              onChange={handleChange}
              disabled={!trainingData.category}
              required
            >
              <option value="">-- Виберіть секцію --</option>
              {trainingData.category &&
                sportCategories[trainingData.category].map(s => (
                  <option key={s} value={s}>{s}</option>
                ))
              }
            </select>
          </div>
          <div className="form-row price-currency-group">
            <div className="price-group">
              <label htmlFor="price">Ціна:</label>
              <input
                type="number"
                step="0.01"
                min="0"
                name="price"
                id="price"
                value={trainingData.price}
                onChange={handleChange}
                required
              />
              {errors.price && <p className="error-message">{errors.price}</p>}
            </div>
            <div className="currency-group">
              <label htmlFor="currency">Валюта:</label>
              <select
                name="currency"
                id="currency"
                value={trainingData.currency}
                onChange={handleChange}
                required
              >
                <option value="UAH">UAH</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="spots">Місць:</label>
            <input
              type="number"
              min="0"
              name="spots"
              id="spots"
              value={trainingData.spots}
              onChange={handleChange}
              required
            />
            {errors.spots && <p className="error-message">{errors.spots}</p>}
          </div>
          <button type="submit" className="submit-btn">Створити</button>
        </form>
      </div>

      {/* Секція “Ваші тренування” */}
      <section className="registered-trainings-section">
        <h2>Ваші тренування</h2>
        {sorted.length === 0 ? (
          <p>Немає створених тренувань.</p>
        ) : (
          <div className="cards-grid">
            {sorted.map(t => {
              const date = formatDate(t.start_time);
              const time = `${formatTime(t.start_time)} – ${formatTime(t.end_time)}`;
              const logo = sportLogos[t.section];
              const signed = t.participants?.length || 0;
              const total  = signed + parseInt(t.spots,10);
              return (
                <div key={t._id} className="training-card">
                  <div className="card-header">
                    <span className="card-date">{date}</span>
                    <span className="card-time">{time}</span>
                  </div>
                  <div className="card-body">
                    {logo && <img src={logo} alt={t.section} className="card-logo" />}
                    <p className="section-name">{t.section}</p>
                    <p className="participants-count">
                      Кількість учасників: {signed}\{total}
                    </p>
                  </div>
                  <div className="card-footer">
                    <button
                      className="view-btn"
                      onClick={() => handleViewParticipants(t)}
                    >
                      Переглянути
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Нова секція “Мій Профіль” */}
      <section className="profile-section">
        <h2>Мій Профіль</h2>
        {profile ? (
          editProfileMode ? (
            <div className="profile-edit">
              <label>Ім'я:</label>
              <input
                type="text"
                value={nameInput}
                onChange={e => setNameInput(e.target.value)}
                required
              />

              <label>Вік:</label>
              <input
                type="number"
                min="0"
                value={ageInput}
                onChange={e => setAgeInput(e.target.value)}
                required
              />

              <label>Телефон:</label>
              <input
                type="text"
                value={phoneInput}
                onChange={e => setPhoneInput(e.target.value)}
                required
              />

              <label>Опис про себе:</label>
              <textarea
                value={description}
                onChange={e => setDescription(e.target.value)}
                placeholder="Розкажіть про себе…"
                required
              />

              <label>Улюблені види спорту:</label>
              <div className="favorite-sports-list">
                {allSections.map(s => (
                  <label key={s} className="sport-checkbox">
                    <input
                      type="checkbox"
                      checked={favoriteSports.includes(s)}
                      onChange={() => toggleSport(s)}
                    />
                    {s}
                  </label>
                ))}
              </div>

              {profileError && <p className="profile-error">{profileError}</p>}

              <button className="save-profile-btn" onClick={handleSaveProfile}>
                Зберегти
              </button>
              <button
                className="cancel-profile-btn"
                onClick={() => setEditProfileMode(false)}
              >
                Відмінити
              </button>
            </div>
          ) : (
            <div className="profile-info">
              <p><strong>Ім'я:</strong> {profile.name}</p>
              <p><strong>Вік:</strong> {profile.age}</p>
              <p><strong>Email:</strong> {profile.email}</p>
              <p><strong>Телефон:</strong> {profile.phone || <em>Немає</em>}</p>
              <p>
                <strong>Опис:</strong>{' '}
                {profile.description ? profile.description : <em>Немає опису</em>}
              </p>
              <p>
                <strong>Улюблені види спорту:</strong>{' '}
                {favoriteSports.length > 0 ? favoriteSports.join(', ') : <em>Немає</em>}
              </p>
              <button
                className="edit-profile-btn"
                onClick={() => setEditProfileMode(true)}
              >
                Редагувати профіль
              </button>
              <button
                className="edit-profile-btn"
                onClick={() => setShowPassMode(true)}
              >
                Змінити пароль
              </button>
            </div>
          )
        ) : (
          <p>Завантаження профілю…</p>
        )}

        {showProfileSuccess && (
          <Modal
            modalType="success"
            message="Профіль успішно оновлено!"
            onClose={() => setShowProfileSuccess(false)}
          />
        )}

        {/* Форма зміни пароля */}
        {showPassMode && (
          <div className="password-form">
            <label>Старий пароль:</label>
            <input
              type="password"
              value={oldPass}
              onChange={e => setOldPass(e.target.value)}
              required
            />
            <label>Новий пароль:</label>
            <input
              type="password"
              value={newPass}
              onChange={e => setNewPass(e.target.value)}
              minLength={6}
              required
            />
            <label>Підтвердіть новий пароль:</label>
            <input
              type="password"
              value={confirmPass}
              onChange={e => setConfirmPass(e.target.value)}
              minLength={6}
              required
            />
            {passError && <p className="profile-error">{passError}</p>}
            <button className="save-profile-btn" onClick={handleChangePassword}>
              Змінити
            </button>
            <button
              className="cancel-profile-btn"
              onClick={() => setShowPassMode(false)}
            >
              Відмінити
            </button>
          </div>
        )}

        {passSuccess && (
          <Modal
            modalType="success"
            message="Пароль успішно змінено!"
            onClose={() => setPassSuccess(false)}
          />
        )}
      </section>

      {/* Модальні вікна для перегляду/редагування тренувань */}
      {showParticipantsModal && selectedTraining && (
        <Modal
          modalType="info"
          onClose={closeParticipantsModal}
          message={
            editMode ? (
              <form onSubmit={handleEditSubmit} className="edit-training-form">
                <div className="form-row">
                  <label htmlFor="editDate">Дата:</label>
                  <input
                    type="date"
                    name="date"
                    id="editDate"
                    value={editData.date}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-row time-group">
                  <div className="time-field">
                    <label htmlFor="editStart">Початок:</label>
                    <input
                      type="time"
                      name="start_time"
                      id="editStart"
                      value={editData.start_time}
                      onChange={handleEditChange}
                      required
                    />
                  </div>
                  <div className="time-field">
                    <label htmlFor="editEnd">Кінець:</label>
                    <input
                      type="time"
                      name="end_time"
                      id="editEnd"
                      value={editData.end_time}
                      onChange={handleEditChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-row">
                  <label htmlFor="editPrice">Ціна:</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    name="price"
                    id="editPrice"
                    value={editData.price}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-row">
                  <label htmlFor="editSpots">Загальна місткість:</label>
                  <input
                    type="number"
                    min="0"
                    name="spots"
                    id="editSpots"
                    value={editData.spots}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-row">
                  <label htmlFor="editCurrency">Валюта:</label>
                  <select
                    name="currency"
                    id="editCurrency"
                    value={editData.currency}
                    onChange={handleEditChange}
                    required
                  >
                    <option value="UAH">UAH</option>
                    <option value="EUR">EUR</option>
                    <option value="USD">USD</option>
                  </select>
                </div>
                <div className="form-row">
                  <label htmlFor="editCategory">Категорія:</label>
                  <select
                    name="category"
                    id="editCategory"
                    value={editData.category}
                    onChange={handleEditChange}
                    required
                  >
                    <option value="">-- Оберіть категорію --</option>
                    {Object.keys(sportCategories).map(cat => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                </div>
                <div className="form-row">
                  <label htmlFor="editSection">Секція:</label>
                  <select
                    name="section"
                    id="editSection"
                    value={editData.section}
                    onChange={handleEditChange}
                    disabled={!editData.category}
                    required
                  >
                    <option value="">-- Оберіть секцію --</option>
                    {editData.category && sportCategories[editData.category].map(s => (
                      <option key={s} value={s}>{s}</option>
                    ))}
                  </select>
                </div>
                {errors.edit && <p className="error-message">{errors.edit}</p>}
                <button type="button" className="submit-btn cancel-btn" onClick={cancelEdit}>
                  Відмінити
                </button>
                <button type="submit" className="submit-btn save-btn">
                  Зберегти
                </button>
              </form>
            ) : (
              <div>
                <h2>Деталі тренування</h2>
                <p><strong>Секція:</strong> {selectedTraining.section}</p>
                <p><strong>Дата:</strong> {formatDate(selectedTraining.start_time)}</p>
                <p><strong>Час:</strong> {formatTime(selectedTraining.start_time)} – {formatTime(selectedTraining.end_time)}</p>
                <p><strong>Учасників:</strong> {selectedTraining.participants?.length||0}\{ parseInt(selectedTraining.spots)+ (selectedTraining.participants?.length||0)}</p>
                <p><strong>Зароблено:</strong> {(selectedTraining.participants?.length||0) * selectedTraining.price} {selectedTraining.currency}</p>
                <hr/>
                <h3>Список учасників:</h3>
                {participants.length===0
                  ? <p>Ніхто не записався.</p>
                  : <ul>{participants.map((p,i)=><li key={i}>{p.name}, вік {p.age}</li>)}</ul>
                }
                <button className="view-btn" onClick={handleEdit}>Редагувати</button>
              </div>
            )
          }
        />
      )}

      {/* Modal успіху при створенні/оновленні тренування */}
      {showModal && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}
    </div>
  );
};

export default CoachDashboard;
