import React, { useState, useEffect } from 'react';
import {
  API_BASE_URL,
  getTrainings,
  getAthleteTrainings,
  getCoachProfile,
  getCoachReviews,
  submitReview,
  updateReview,
  deleteReview,
  signupForTraining,
  cancelEnrollment,
  getAthleteProfile,
  updateAthleteProfile,
  changeAthletePassword,
  uploadAthleteAvatar,
  getTrainingParticipants
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/AthleteDashboard.css';

// Логотипи секцій
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// Мапа секція → логотип
const sportLogos = {
  "Футбол": footballLogo,
  "Баскетбол": basketballLogo,
  "Бадмінтон": badmintonLogo,
  "Волейбол": volleyballLogo,
  "Бейсбол": baseballLogo,
  "Регбі": rugbyLogo,
  "Хокей": hockeyLogo,
  "Карате": karateLogo,
  "Бокс": boxingLogo,
  "Ушу": wushuLogo,
  "Джиу-джитсу": jiuJitsuLogo,
  "ММА": mmaLogo,
  "Біг": runningLogo,
  "Стрибки": jumpingLogo,
  "Метання": throwingLogo,
  "Плавання": swimmingLogo,
  "Вітрильний спорт": sailingLogo,
  "Каноїзм": canoeingLogo,
  "Скейбординг": skateboardingLogo,
  "Сноубординг": snowboardingLogo,
  "Альпінізм": climbingLogo,
  "Фітнес": fitnessLogo,
  "Силові тренування": strengthTrainingLogo,
  "Йога": yogaLogo
};

// Мапа категорія → список секцій
const sportCategories = {
  "Ігри з м'ячем": ["Футбол","Баскетбол","Бадмінтон","Волейбол","Бейсбол","Регбі","Хокей"],
  "Бойові мистецтва": ["Карате","Бокс","Ушу","Джиу-джитсу","ММА"],
  "Легка атлетика": ["Біг","Стрибки","Метання"],
  "Водні види спорту": ["Плавання","Вітрильний спорт","Каноїзм"],
  "Екстремальні види спорту": ["Скейбординг","Сноубординг","Альпінізм"],
  "Фітнес та тренажерний зал": ["Фітнес","Силові тренування","Йога"]
};

const DEFAULT_AVATAR = `${API_BASE_URL}/avatars/default.png`;

const AthleteDashboard = () => {
  const [allTrainings, setAllTrainings]               = useState([]);
  const [registeredTrainings, setRegisteredTrainings] = useState([]);
  const [error, setError]                              = useState('');
  const [showModal, setShowModal]                      = useState(false);
  const [modalMessage, setModalMessage]                = useState('');
  const [showCancelModal, setShowCancelModal]          = useState(false);
  const [selectedCancelTraining, setSelectedCancelTraining] = useState(null);
  const [selectedTraining, setSelectedTraining]        = useState(null);

  // Coach profile & reviews
  const [coachProfile, setCoachProfile] = useState(null);
  const [showCoachModal, setShowCoachModal] = useState(false);
  const [coachError, setCoachError] = useState('');
  const [reviews, setReviews] = useState([]);
  const [avgRating, setAvgRating] = useState(null);
  const [reviewCount, setReviewCount] = useState(0);
  const [newRating, setNewRating] = useState(5);
  const [newComment, setNewComment] = useState('');
  const [editingReviewId, setEditingReviewId] = useState(null);

  const [filterCategory, setFilterCategory] = useState('');
  const [filterSection, setFilterSection]   = useState('');
  const [sortOption, setSortOption]         = useState("date_asc");

  // Athlete profile
  const [profile, setProfile] = useState(null);
  const [editProfileMode, setEditProfileMode] = useState(false);
  const [nameInput, setNameInput] = useState('');
  const [ageInput, setAgeInput] = useState('');
  const [avatarFile, setAvatarFile] = useState(null);
  const [avatarPreview, setAvatarPreview] = useState('');
  const [showProfileSuccess, setShowProfileSuccess] = useState(false);
  const [showPassMode, setShowPassMode] = useState(false);
  const [oldPass, setOldPass] = useState('');
  const [newPass, setNewPass] = useState('');
  const [confirmPass, setConfirmPass] = useState('');
  const [profileError, setProfileError] = useState('');
  const [passError, setPassError] = useState('');
  const [passSuccess, setPassSuccess] = useState(false);
  const [phoneInput, setPhoneInput] = useState('');
  const [descriptionInput, setDescriptionInput] = useState('');
  const [favoriteSports, setFavoriteSports] = useState([]);

  const [participants, setParticipants] = useState([]);


  useEffect(() => {
    fetchAllTrainings();
    fetchRegisteredTrainings();
  const athleteId = localStorage.getItem('userId');
  if (athleteId) {
    getAthleteProfile(athleteId).then(({ profile }) => {
      setProfile(profile);
      setNameInput(profile.name);
      setAgeInput(profile.age);
      setPhoneInput(profile.phone);
      setDescriptionInput(profile.description);
      setFavoriteSports(profile.favorite_sports || []);
      setAvatarPreview(profile.avatar_url || DEFAULT_AVATAR);
    });
  }
  }, []);

  const fetchAllTrainings = async () => {
    try {
      const { trainings } = await getTrainings();
      setAllTrainings(trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchRegisteredTrainings = async () => {
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен");
      return;
    }
    try {
      const { trainings } = await getAthleteTrainings(athleteId);
      setRegisteredTrainings(trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDate = t => new Date(t)
    .toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t)
    .toLocaleTimeString([],{hour:'2-digit',minute:'2-digit'});

  // Фільтрація доступних тренувань
  const registeredIds = new Set(registeredTrainings.map(t => t._id));
  let available = allTrainings.filter(t => !registeredIds.has(t._id));
  if (filterCategory) available = available.filter(t => t.category === filterCategory);
  if (filterSection)  available = available.filter(t => t.section  === filterSection);
  available.sort((a,b)=>{
    switch(sortOption){
      case "date_asc":  return new Date(a.start_time) - new Date(b.start_time);
      case "date_desc": return new Date(b.start_time) - new Date(a.start_time);
      case "price_asc": return a.price - b.price;
      case "price_desc":return b.price - a.price;
      default: return 0;
    }
  });

  // Обробники
const handleCoachClick = async coachId => {
  setCoachError('');
  try {
    const { profile } = await getCoachProfile(coachId);
    setCoachProfile(profile);
    const { reviews: rawReviews, average_rating, count } = await getCoachReviews(coachId);
    const reviewsWithAvatars = await Promise.all(
      rawReviews.map(async r => {
        try {
          const { profile: athProfile } = await getAthleteProfile(r.athlete_id);
          return {
            ...r,
            athlete_avatar_url: athProfile.avatar_url
          };
        } catch {
          return {
            ...r,
            athlete_avatar_url: null
          };
        }
      })
    );
    setReviews(reviewsWithAvatars);
    setAvgRating(average_rating);
    setReviewCount(count);
    setNewRating(5);
    setNewComment('');
    setEditingReviewId(null);
    setShowCoachModal(true);

  } catch (err) {
    setCoachError(err.message);
  }
};


  const handleSubmitReview = async () => {
  const athleteId = localStorage.getItem('userId');

  // 1) Валідація
  if (!newComment.trim()) {
    setCoachError("Коментар не може бути порожнім");
    return;
  }
  if (!athleteId) {
    setCoachError("Увійдіть щоб залишити відгук");
    return;
  }

  try {
    // 2) Відправляємо або оновлюємо відгук
    if (editingReviewId) {
      await updateReview(editingReviewId, {
        athlete_id: athleteId,
        rating: newRating,
        comment: newComment
      });
    } else {
      await submitReview({
        coach_id: coachProfile.user_id,
        athlete_id: athleteId,
        rating: newRating,
        comment: newComment
      });
    }

    // 3) Підтягуємо всі відгуки один раз
    const {
      reviews: rawReviews,
      average_rating: avg,
      count: cnt
    } = await getCoachReviews(coachProfile.user_id);

    // 4) Для кожного відгуку докачуємо avatar_url спортсмена
    const reviewsWithAvatars = await Promise.all(
      rawReviews.map(async r => {
        try {
          const { profile: ath } = await getAthleteProfile(r.athlete_id);
          // Формуємо повний URL і додаємо таймстамп, щоб не брати з кешу
          const baseUrl = ath.avatar_url.startsWith('http')
            ? ath.avatar_url
            : `${API_BASE_URL}${ath.avatar_url}`;
          return {
            ...r,
            athlete_avatar_url: `${baseUrl}?t=${Date.now()}`
          };
        } catch {
          return {
            ...r,
            athlete_avatar_url: DEFAULT_AVATAR
          };
        }
      })
    );

    // 5) Оновлюємо стани
    setReviews(reviewsWithAvatars);
    setAvgRating(avg);
    setReviewCount(cnt);

    // 6) Скидаємо форму
    setEditingReviewId(null);
    setNewRating(5);
    setNewComment('');
  } catch (err) {
    setCoachError(err.message);
  }
};


  const handleDeleteReview = async id => {
  setCoachError('');
  const athleteId = localStorage.getItem('userId');
  try {
    // 1) Видаляємо відгук
    await deleteReview(id, athleteId);

    // 2) Підтягуємо новий список відгуків
    const {
      reviews: rawReviews,
      average_rating: avg,
      count: cnt
    } = await getCoachReviews(coachProfile.user_id);

    // 3) Додаємо до кожного r.athlete_avatar_url
    const reviewsWithAvatars = await Promise.all(
      rawReviews.map(async r => {
        try {
          const { profile: ath } = await getAthleteProfile(r.athlete_id);
          const baseUrl = ath.avatar_url.startsWith('http')
            ? ath.avatar_url
            : `${API_BASE_URL}${ath.avatar_url}`;
          return {
            ...r,
            athlete_avatar_url: `${baseUrl}?t=${Date.now()}`
          };
        } catch {
          return {
            ...r,
            athlete_avatar_url: DEFAULT_AVATAR
          };
        }
      })
    );

    // 4) Оновлюємо стани
    setReviews(reviewsWithAvatars);
    setAvgRating(avg);
    setReviewCount(cnt);

    // 5) Скидаємо режим редагування
    setEditingReviewId(null);
    setNewComment('');
    setNewRating(5);
  } catch (err) {
    setCoachError(err.message);
  }
};


  const startEditReview = r => {
    setCoachError('');      // <-- очищаємо попередню помилку
    setEditingReviewId(r.id);
    setNewRating(r.rating);
    setNewComment(r.comment);
  };

  const handleSignup = async id => {
    setError('');
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) { setError("Увійдіть як спортсмен"); return; }
    try {
      const res = await signupForTraining(id, athleteId);
      setModalMessage(res.message);
      setShowModal(true);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
    }
  };

  const openCancelModal = t => {
    setSelectedCancelTraining(t);
    setShowCancelModal(true);
  };

  const handleCancelEnrollment = async () => {
    const athleteId = localStorage.getItem('userId');
    try {
      await cancelEnrollment(selectedCancelTraining._id, athleteId);
      setModalMessage("Запис успішно скасовано.");
      setShowModal(true);
      setShowCancelModal(false);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
      setShowCancelModal(false);
    }
  };

  const handleViewDetails = t => {
    setSelectedTraining(t);
    setShowModal(true);
  };

    const handleViewParticipants = async t => {
    setSelectedTraining(t);
    try {
      const { participants: raw } = await getTrainingParticipants(t._id);
      const enriched = await Promise.all(
        raw.map(async p => {
          if (!p.athlete_id) return { ...p, avatar_url: null };
          try {
            const { profile: ath } = await getAthleteProfile(p.athlete_id);
            return { ...p, avatar_url: ath.avatar_url };
          } catch {
            return { ...p, avatar_url: null };
          }
        })
      );
      setParticipants(enriched);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch (err) {
      console.error(err);
    }
  };


  const handleModalClose = () => {
    setShowModal(false);
    setSelectedTraining(null);
  };

  const handleCancelModalClose = () => setShowCancelModal(false);

  const sortedRegistered = [...registeredTrainings].sort(
    (a,b)=> new Date(a.start_time) - new Date(b.start_time)
  );

  const toggleSport = sport => {
  setFavoriteSports(prev =>
    prev.includes(sport)
      ? prev.filter(s => s !== sport)
      : [...prev, sport]
  );
};

  // В середині вашого компонента AthleteDashboard
const handleSaveProfile = async () => {
  // Скидаємо попередню помилку
  setProfileError('');

  // Перевірка: Ім'я обов'язкове
  if (!nameInput.trim()) {
    setProfileError('Поле "Ім\'я" обов\'язкове');
    return;
  }

  // Перевірка: Вік обов'язковий та числовий
  const ageNum = parseInt(ageInput, 10);
  if (isNaN(ageNum)) {
    setProfileError('Поле "Вік" обов\'язкове');
    return;
  }
  // Перевірка: Вік не може бути від’ємним
  if (ageNum < 0) {
    setProfileError('Вік не може бути від’ємним');
    return;
  }

    // Перевірка: Вік не може бути від’ємним
  if (ageNum < 18) {
    setProfileError('Вік не може бути менше 18');
    return;
  }

  // Усі перевірки пройдені — надсилаємо на сервер
  try {
    const { profile: updated } = await updateAthleteProfile({
      user_id: profile.user_id,
      name: nameInput.trim(),
      age: ageNum,
      phone: phoneInput,
      description: descriptionInput,
      favorite_sports: favoriteSports
    });
    // Після успіху оновлюємо стан
    setProfile(updated);
    setShowProfileSuccess(true);
    setEditProfileMode(false);
    setProfileError('');
  } catch (err) {
    setProfileError(err.message);
  }
};


// всередині AthleteDashboard
const handleAvatarUpload = async () => {
  if (!avatarFile) return;
  setProfileError('');
  try {
    // відправляємо файл на бекенд
    const { avatar_url } = await uploadAthleteAvatar(profile.user_id, avatarFile);
    // будуємо повний URL
    const fullUrl = avatar_url.startsWith('http')
      ? avatar_url
      : `${API_BASE_URL}${avatar_url}`;
    // додаємо таймстамп, щоб браузер не взяв із кешу старий файл
    const cacheBustedUrl = `${fullUrl}?t=${Date.now()}`;
    // оновлюємо прев’ю
    setAvatarPreview(cacheBustedUrl);
    // оновлюємо профіль в state, щоб у інших місцях зразу було актуальне avatar_url
    setProfile(prev => ({ ...prev, avatar_url: fullUrl }));
    // прибираємо вибір файлу
    setAvatarFile(null);
    // показуємо підтвердження
    setShowProfileSuccess(true);
  } catch (err) {
    setProfileError(err.message);
  }
};


  return (
    <div className="dashboard-container">
      <h1>Кабінет Спортсмена</h1>
      {error && <p className="error-message">{error}</p>}

      {/* — Доступні тренування — */}
      <section className="available-trainings-section">
        <div className="filter-form">
          <div className="filter-row">
            <label>Категорія:</label>
            <select
              value={filterCategory}
              onChange={e => { setFilterCategory(e.target.value); setFilterSection(''); }}
            >
              <option value="">— Всі —</option>
              {Object.keys(sportCategories).map(cat=>(
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <div className="filter-row">
            <label>Секція:</label>
            <select
              value={filterSection}
              disabled={!filterCategory}
              onChange={e=>setFilterSection(e.target.value)}
            >
              <option value="">— Всі —</option>
              {filterCategory && sportCategories[filterCategory].map(s=>(
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
          </div>
          <div className="filter-row">
            <label>Сортування:</label>
            <select
              value={sortOption}
              onChange={e=>setSortOption(e.target.value)}
            >
              <option value="date_asc">Дата ↑</option>
              <option value="date_desc">Дата ↓</option>
              <option value="price_asc">Ціна ↑</option>
              <option value="price_desc">Ціна ↓</option>
            </select>
          </div>
          <button
            className="reset-filter-btn"
            onClick={()=>{ setFilterCategory(''); setFilterSection(''); setSortOption("date_asc"); }}
          >Скинути</button>
        </div>

        <h2>Доступні тренування</h2>
        {available.length === 0
          ? <p>Немає доступних тренувань.</p>
          : (
            <div className="cards-grid">
              {available.map(t=>{
                const date = formatDate(t.start_time);
                const time = `${formatTime(t.start_time)} – ${formatTime(t.end_time)}`;
                const logo = sportLogos[t.section];
                let avatarSrc = DEFAULT_AVATAR;
                if (t.avatar_url) {
                  avatarSrc = t.avatar_url.startsWith('http')
                    ? t.avatar_url
                    : `${API_BASE_URL}${t.avatar_url}`;
                }
                return (
                  <div key={t._id} className="training-card">
                    <div className="card-header">
                      <span className="card-date">{date}</span>
                      <span className="card-time">{time}</span>
                    </div>
                    <div className="card-body">
                      {logo && <img src={logo} alt={t.section} className="card-logo" />}
                      <p><strong>Секція:</strong> {t.section}</p>
                      <p className="trainer-line">
                        <strong>Тренер:</strong>{' '}
                        <img
                          src={avatarSrc}
                          alt="Avatar"
                          className="coach-avatar-small"
                          onError={e=>{ e.currentTarget.onerror=null; e.currentTarget.src=DEFAULT_AVATAR; }}
                        />{' '}
                        <span
                          className="coach-name-link"
                          onClick={()=>handleCoachClick(t.coach_id)}
                        >
                          {t.coach_name}
                        </span>
                      </p>
                      <p><strong>Вартість:</strong> {t.price} {t.currency}</p>
                      <p><strong>Місць:</strong> {t.spots}</p>
                    </div>
                    <div className="card-footer">
                      <button
                        className="signup-btn"
                        onClick={()=>handleSignup(t._id)}
                        disabled={t.spots===0}
                      >Записатись</button>
                    </div>
                  </div>
                );
              })}
            </div>
          )
        }
      </section>

      {/* — Ваші тренування — */}
      <section className="registered-trainings-section">
        <h2>Ваші тренування</h2>
        {sortedRegistered.length===0
          ? <p>Ви ще не записалися на тренування.</p>
          : (
            <div className="registered-cards-grid">
              {sortedRegistered.map(t=>{
                const date = formatDate(t.start_time);
                const time = `${formatTime(t.start_time)} – ${formatTime(t.end_time)}`;
                const logo = sportLogos[t.section];
                let avatarSrc = DEFAULT_AVATAR;
                if (t.avatar_url) {
                  avatarSrc = t.avatar_url.startsWith('http')
                    ? t.avatar_url
                    : `${API_BASE_URL}${t.avatar_url}`;
                }
                return (
                  <div key={t._id} className="training-card registered-card new-design">
                    <div className="card-header">
                      <span className="card-date">{date}</span>
                      <span className="card-time">{time}</span>
                    </div>
                    <div className="card-body">
                      {logo && <img src={logo} alt={t.section} className="registered-card-logo" />}
                      <p className="registered-section-name">{t.section}</p>
                      <p className="registered-trainer-line">
                        із{' '}
                        <img
                          src={avatarSrc}
                          alt="Avatar"
                          className="coach-avatar-small"
                          onError={e=>{ e.currentTarget.onerror=null; e.currentTarget.src=DEFAULT_AVATAR; }}
                        />{' '}
                        <span
                          className="coach-name-link"
                          onClick={()=>handleCoachClick(t.coach_id)}
                        >
                          {t.coach_name}
                        </span>
                      </p>
                    </div>
                    <div className="card-footer">
                      <button className="action-btn view-btn"
                        onClick={()=>handleViewDetails(t)}>Переглянути</button>
                      <button className="action-btn cancel-btn"
                        onClick={()=>openCancelModal(t)}>Відмінити запис</button>
                    </div>
                  </div>
                );
              })}
            </div>
          )
        }
      </section>

      {/* Підтвердження скасування */}
      {showCancelModal && (
        <Modal
          modalType="confirm"
          onClose={handleCancelModalClose}
          message={
            <div className="cancel-modal">
              <p>Ви впевнені, що хочете відмінити запис?</p>
              <div className="cancel-modal-buttons">
                <button className="confirm-btn" onClick={handleCancelEnrollment}>Так</button>
                <button className="cancel-btn" onClick={handleCancelModalClose}>Ні</button>
              </div>
            </div>
          }
        />
      )}

      {/* Деталі тренування */}
      {showModal && selectedTraining && registeredParticipants && (
  <Modal
    modalType="info"
    onClose={handleModalClose}
    className="training-detail-modal"
    message={
      <div className="training-detail-modal">
        {/* — Ліва колонка: загальна інформація */}
        <div className="modal-left">
          <h3 className="block-title">Загальна інформація</h3>
          <div className="general-info">
            {sportLogos[selectedTraining.section] && (
              <img
                src={sportLogos[selectedTraining.section]}
                alt={selectedTraining.section}
                className="section-logo-lg"
              />
            )}
            <div className="general-info-text">
              <p><strong>Секція:</strong> {selectedTraining.section}</p>
              <p><strong>Дата:</strong> {formatDate(selectedTraining.start_time)}</p>
              <p><strong>Час:</strong> {`${formatTime(selectedTraining.start_time)} – ${formatTime(selectedTraining.end_time)}`}</p>
              <p><strong>Тренер:</strong> {selectedTraining.coach_name}</p>
              <p><strong>Учасників:</strong> {registeredParticipants.length}</p>
            </div>
          </div>
        </div>

        {/* — Права колонка: список учасників */}
        <div className="modal-right">
          <h3 className="block-title">Учасники</h3>
          <div className="participants-list">
            {registeredParticipants.length > 0 ? (
              registeredParticipants.map(p => (
                <div key={p.user_id} className="participant-item">
                  <img
                    src={p.avatar_url}
                    alt={p.name}
                    className="participant-avatar"
                    onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = DEFAULT_AVATAR; }}
                  />
                  <div className="participant-details">
                    <p className="participant-name">{p.name}</p>
                    <p className="participant-age">Вік: {p.age}</p>
                  </div>
                </div>
              ))
            ) : (
              <p className="no-participants"><em>Ніхто не записався.</em></p>
            )}
          </div>
        </div>
      </div>
    }
  />
)}


      {showModal && !selectedTraining && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}

      {/* Профіль та відгуки тренера */}
      {showCoachModal && coachProfile && (
        <Modal
          modalType="info"
          onClose={() => setShowCoachModal(false)}
          message={
            <div className="coach-details-container">
              {/* Блок з профілем і середньою оцінкою */}
              <div className="coach-info-block">
                <img
                  src={coachProfile.avatar_url || DEFAULT_AVATAR}
                  alt="Аватар тренера"
                  className="coach-avatar-large"
                  onError={e=>{ e.currentTarget.src = DEFAULT_AVATAR; }}
                />
                <h3 className="coach-name">{coachProfile.name}</h3>
                <p><strong>Вік:</strong> {coachProfile.age}</p>
                <p><strong>Email:</strong> {coachProfile.email}</p>
                <p><strong>Телефон:</strong> {coachProfile.phone || <em>Немає</em>}</p>
                <p className="coach-description">
                  <strong>Опис:</strong> {coachProfile.description || <em>Немає опису</em>}
                </p>
                <p>
                 <strong>Улюблені види спорту:</strong>{' '}
                 {coachProfile.favorite_sports && coachProfile.favorite_sports.length > 0
                   ? coachProfile.favorite_sports.join(', ')
                   : <em>Немає</em>}
               </p>
                <div className="reviews-summary-block">
                  <p><strong>Середня оцінка:</strong> {avgRating ?? '—'}</p>
                  <p><strong>Відгуків:</strong> {reviewCount}</p>
                </div>
              </div>

              {/* Блок зі списком відгуків та формою */}
              <div className="coach-reviews-block">
               <h4>Відгуки</h4>
                <div className="reviews-list-scroll">
                  {reviews.length > 0 ? (
                    reviews.map(r => (
                      <div key={r.id} className="review-card">
                        <div className="review-card-header">
                          <div className="review-user-info">
                            {/* Якщо є аватарка користувача — можна підставити */}
                            <img
                              src={ r.athlete_avatar_url
                                      ? (r.athlete_avatar_url.startsWith('http')
                                          ? r.athlete_avatar_url
                                          : `${API_BASE_URL}${r.athlete_avatar_url}`)
                                      : DEFAULT_AVATAR }
                              alt="Аватар спортсмена"
                              className="reviewer-avatar-small"
                              onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = DEFAULT_AVATAR; }}
                            />
                            <span className="review-username">
                              {r.athlete_name || 'Анонім'}
                            </span>
                          </div>
                          <div className="review-meta">
                            <span className="review-rating">
                              {'★'.repeat(r.rating) + '☆'.repeat(5 - r.rating)}
                            </span>
                            <small className="review-date">
                              {new Date(r.created_at).toLocaleDateString('uk-UA', {
                                day: '2-digit', month: '2-digit', year: 'numeric'
                              })}
                            </small>
                          </div>
                        </div>
                        <p className="review-text">{r.comment}</p>
                        {r.athlete_id === localStorage.getItem('userId') && (
                          <div className="review-actions">
                            <button onClick={() => startEditReview(r)}>Редагувати</button>
                            <button onClick={() => handleDeleteReview(r.id)}>Видалити</button>
                          </div>
                        )}
                      </div>
                    ))
                  ) : (
                    <p className="no-reviews"><em>Немає відгуків</em></p>
                  )}
                </div>

                {/* Форма створення / редагування відгуку */}
                <div className="submit-review">
                  <h4>{editingReviewId ? 'Редагувати відгук' : 'Залишити відгук'}</h4>
                  {coachError && <p className="error-message">{coachError}</p>}
                  <label>Оцінка:</label>
                  <select
                    value={newRating}
                    onChange={e => setNewRating(parseInt(e.target.value, 10))}
                  >
                    {[5,4,3,2,1].map(n => (
                      <option key={n} value={n}>{'★'.repeat(n)}</option>
                    ))}
                  </select>
                  <label>Коментар:</label>
                  <textarea
                    className="review-textarea"
                    value={newComment}
                    onChange={e => setNewComment(e.target.value)}
                    placeholder="Ваш відгук..."
                  />
                  <button
                    className="submit-review-btn"
                    className="submit-review-btn"
                    disabled={!newComment.trim()}
                    onClick={handleSubmitReview}
                  >
                    {editingReviewId ? 'Оновити відгук' : 'Відправити відгук'}
                  </button>
                </div>
              </div>
            </div>
          }
        />
      )}

      <section className="profile-section">
  {profile ? (
    <div className="profile-container">
      {/* Аватар */}
      <div className="profile-avatar-column">
        <img src={avatarPreview} className="profile-avatar-large" alt="Аватар" />
              {editProfileMode && (
          <>
            <input
              type="file"
              accept="image/*"
              onChange={e => {
                const file = e.target.files[0];
                setAvatarFile(file);
                // поки клієнт вибрав файл, можна показати локальне прев’ю
                setAvatarPreview(URL.createObjectURL(file));
              }}
            />
            <button className="btn-primary" onClick={handleAvatarUpload}>
              Завантажити фото
            </button>
          </>
        )}


      </div>

      {/* Інформація */}
      <div className="profile-info-column">
        {editProfileMode ? (
          <>
            <label>Ім'я:</label>
            <input
              value={nameInput}
              onChange={e => setNameInput(e.target.value)}
            />

            <label>Вік:</label>
            <input
              type="number"
              value={ageInput}
              min="18"
              onChange={e => setAgeInput(e.target.value)}
            />

            <label>Телефон:</label>
            <input
              value={phoneInput}
              onChange={e => setPhoneInput(e.target.value)}
            />

            <label>Опис:</label>
            <textarea
              value={descriptionInput}
              onChange={e => setDescriptionInput(e.target.value)}
            />

            <label>Улюблені види спорту:</label>
            <div className="favorite-sports-list">
              {Object.values(sportCategories).flat().map(s => (
                <label key={s} className="sport-checkbox">
                  <input
                    type="checkbox"
                    checked={favoriteSports.includes(s)}
                    onChange={() => toggleSport(s)}
                  />
                  {s}
                </label>
              ))}
            </div>

            {profileError && (
              <p className="error-message">{profileError}</p>
            )}

            <div className="profile-actions">
              <button
                className="btn-primary"
                onClick={handleSaveProfile}
              >
                Зберегти
              </button>
              <button
              className="btn-secondary"
              onClick={() => {
                // ховаємо режим редагування
                setEditProfileMode(false);
                // очищаємо помилку
                setProfileError("");
                // відкат полів до поточних значень профілю
                setNameInput(profile.name);
                setAgeInput(profile.age);
                setPhoneInput(profile.phone || "");
                setDescriptionInput(profile.description || "");
                setFavoriteSports(profile.favorite_sports || []);
              }}
            >
              Відмінити
            </button>
            </div>
          </>
        ) : (
          <>
            <h2>{profile.name}</h2>
            <p><strong>Вік:</strong> {profile.age}</p>
            <p><strong>Email:</strong> {profile.email}</p>
            <p>
              <strong>Телефон:</strong>{' '}
              {profile.phone || <em>—</em>}
            </p>
            <p>
              <strong>Опис:</strong>{' '}
              {profile.description || <em>—</em>}
            </p>
            <p>
              <strong>Улюблені види спорту:</strong>{' '}
              {favoriteSports.length
                ? favoriteSports.join(', ')
                : <em>—</em>}
            </p>

            <div className="profile-actions">
              <button
                className="btn-primary"
                onClick={() => setEditProfileMode(true)}
              >
                Редагувати
              </button>
              <button
                className="btn-secondary"
                onClick={() => setShowPassMode(true)}
              >
                Змінити пароль
              </button>
            </div>
          </>
        )}

        {/* Форма зміни паролю */}
        {showPassMode && (
          <div className="password-form">
            <label>Старий пароль:</label>
            <input
              type="password"
              value={oldPass}
              onChange={e => setOldPass(e.target.value)}
            />

            <label>Новий пароль:</label>
            <input
              type="password"
              value={newPass}
              onChange={e => setNewPass(e.target.value)}
            />

            <label>Підтвердіть новий пароль:</label>
            <input
              type="password"
              value={confirmPass}
              onChange={e => setConfirmPass(e.target.value)}
            />

            {passError && (
              <p className="error-message">{passError}</p>
            )}

            <div className="profile-actions">
              <button
                className="btn-primary"
                onClick={async () => {
                  try {
                    await changeAthletePassword({
                      user_id: profile.user_id,
                      old_password: oldPass,
                      new_password: newPass,
                      confirm_password: confirmPass
                    });
                    setPassSuccess(true);
                    setShowPassMode(false);
                    setOldPass("");
                    setNewPass("");
                    setConfirmPass("");
                    setProfileError("");
                  } catch (err) {
                    setPassError(err.message);
                  }
                }}
              >
                Змінити пароль
              </button>
              <button
              className="btn-secondary"
              onClick={() => {
                // ховаємо форму зміни пароля
                setShowPassMode(false);
                // очищаємо помилку і поля
                setPassError("");
                setOldPass("");
                setNewPass("");
                setConfirmPass("");
              }}
            >
              Відмінити
            </button>

            </div>
          </div>
        )}

        {/* Модалки */}
        {passSuccess && (
          <Modal
            message="Пароль успішно змінено!"
            onClose={() => setPassSuccess(false)}
          />
        )}
        {showProfileSuccess && (
          <Modal
            message="Профіль успішно оновлено!"
            onClose={() => setShowProfileSuccess(false)}
          />
        )}
      </div>
    </div>
  ) : (
    <p>Завантаження профілю…</p>
  )}
</section>



    </div>
  );
};

export default AthleteDashboard;
