// src/components/Dashboards/CoachDashboard.js
import React, { useState, useEffect } from 'react';
import {
  createTraining,
  getCoachTrainings,
  getTrainingParticipants,
  updateTraining
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/CoachDashboard.css';

// –õ–æ–≥–æ—Ç–∏–ø–∏ —Å–µ–∫—Ü—ñ–π
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// –ú–∞–ø–∞ —Å–µ–∫—Ü—ñ—è ‚Üí –ª–æ–≥–æ—Ç–∏–ø
const sportLogos = {
  "–§—É—Ç–±–æ–ª": footballLogo,
  "–ë–∞—Å–∫–µ—Ç–±–æ–ª": basketballLogo,
  "–ë–∞–¥–º—ñ–Ω—Ç–æ–Ω": badmintonLogo,
  "–í–æ–ª–µ–π–±–æ–ª": volleyballLogo,
  "–ë–µ–π—Å–±–æ–ª": baseballLogo,
  "–†–µ–≥–±—ñ": rugbyLogo,
  "–•–æ–∫–µ–π": hockeyLogo,
  "–ö–∞—Ä–∞—Ç–µ": karateLogo,
  "–ë–æ–∫—Å": boxingLogo,
  "–£—à—É": wushuLogo,
  "–î–∂–∏—É-–¥–∂–∏—Ç—Å—É": jiuJitsuLogo,
  "–ú–ú–ê": mmaLogo,
  "–ë—ñ–≥": runningLogo,
  "–°—Ç—Ä–∏–±–∫–∏": jumpingLogo,
  "–ú–µ—Ç–∞–Ω–Ω—è": throwingLogo,
  "–ü–ª–∞–≤–∞–Ω–Ω—è": swimmingLogo,
  "–í—ñ—Ç—Ä–∏–ª—å–Ω–∏–π —Å–ø–æ—Ä—Ç": sailingLogo,
  "–ö–∞–Ω–æ—ó–∑–º": canoeingLogo,
  "–°–∫–µ–π–±–æ—Ä–¥–∏–Ω–≥": skateboardingLogo,
  "–°–Ω–æ—É–±–æ—Ä–¥–∏–Ω–≥": snowboardingLogo,
  "–ê–ª—å–ø—ñ–Ω—ñ–∑–º": climbingLogo,
  "–§—ñ—Ç–Ω–µ—Å": fitnessLogo,
  "–°–∏–ª–æ–≤—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è": strengthTrainingLogo,
  "–ô–æ–≥–∞": yogaLogo
};

// –ö–∞—Ç–µ–≥–æ—Ä—ñ—ó —Ç–∞ —Å–µ–∫—Ü—ñ—ó
const sportCategories = {
  "–Ü–≥—Ä–∏ –∑ –º'—è—á–µ–º": ["–§—É—Ç–±–æ–ª","–ë–∞—Å–∫–µ—Ç–±–æ–ª","–ë–∞–¥–º—ñ–Ω—Ç–æ–Ω","–í–æ–ª–µ–π–±–æ–ª","–ë–µ–π—Å–±–æ–ª","–†–µ–≥–±—ñ","–•–æ–∫–µ–π"],
  "–ë–æ–π–æ–≤—ñ –º–∏—Å—Ç–µ—Ü—Ç–≤–∞": ["–ö–∞—Ä–∞—Ç–µ","–ë–æ–∫—Å","–£—à—É","–î–∂–∏—É-–¥–∂–∏—Ç—Å—É","–ú–ú–ê"],
  "–õ–µ–≥–∫–∞ –∞—Ç–ª–µ—Ç–∏–∫–∞": ["–ë—ñ–≥","–°—Ç—Ä–∏–±–∫–∏","–ú–µ—Ç–∞–Ω–Ω—è"],
  "–í–æ–¥–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É": ["–ü–ª–∞–≤–∞–Ω–Ω—è","–í—ñ—Ç—Ä–∏–ª—å–Ω–∏–π —Å–ø–æ—Ä—Ç","–ö–∞–Ω–æ—ó–∑–º"],
  "–ï–∫—Å—Ç—Ä–µ–º–∞–ª—å–Ω—ñ –≤–∏–¥–∏ —Å–ø–æ—Ä—Ç—É": ["–°–∫–µ–π–±–æ—Ä–¥–∏–Ω–≥","–°–Ω–æ—É–±–æ—Ä–¥–∏–Ω–≥","–ê–ª—å–ø—ñ–Ω—ñ–∑–º"],
  "–§—ñ—Ç–Ω–µ—Å —Ç–∞ —Ç—Ä–µ–Ω–∞–∂–µ—Ä–Ω–∏–π –∑–∞–ª": ["–§—ñ—Ç–Ω–µ—Å","–°–∏–ª–æ–≤—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è","–ô–æ–≥–∞"]
};

const CoachDashboard = () => {
  // –°—Ç–∞–Ω–∏ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è
  const [trainingData, setTrainingData] = useState({
    date: '', start_time: '', end_time: '',
    price: '', spots: '', currency: 'UAH',
    category: '', section: ''
  });
  const [errors, setErrors] = useState({});
  const [modalMessage, setModalMessage] = useState('');
  const [showModal, setShowModal] = useState(false);

  // –ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è
  const [trainings, setTrainings] = useState([]);

  // –°—Ç–∞–Ω–∏ –¥–ª—è –¥–µ—Ç–∞–ª—ñ–∑–∞—Ü—ñ—ó / —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
  const [selectedTraining, setSelectedTraining] = useState(null);
  const [participants, setParticipants] = useState([]);
  const [showParticipantsModal, setShowParticipantsModal] = useState(false);
  const [editMode, setEditMode] = useState(false);
  const [editData, setEditData] = useState({
    date:'', start_time:'', end_time:'',
    price:'', spots:'', currency:'',
    category:'', section:''
  });

  const today = new Date().toISOString().split('T')[0];

  // –§–æ—Ä–º–∞—Ç–µ—Ä–∏
  const formatDate = t => new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t => new Date(t).toLocaleTimeString([], {hour:'2-digit',minute:'2-digit'});

  // Fetch —Ç—Ä–µ–Ω—É–≤–∞–Ω—å —Ç—Ä–µ–Ω–µ—Ä–∞
  const fetchTrainings = async () => {
    const coachId = localStorage.getItem('userId');
    if (!coachId) return;
    try {
      const { trainings } = await getCoachTrainings(coachId);
      setTrainings(trainings);
    } catch (err) {
      console.error(err);
    }
  };
  useEffect(fetchTrainings, []);

  // –û–±—Ä–æ–±–Ω–∏–∫–∏ —Ñ–æ—Ä–º–∏
  const handleChange = e => setTrainingData({...trainingData, [e.target.name]: e.target.value});
  const handleCategoryChange = e => setTrainingData({
    ...trainingData,
    category: e.target.value,
    section: ''
  });
  const getMinTime = () => {
    if (trainingData.date === today) {
      return new Date().toTimeString().slice(0,5);
    }
    return '00:00';
  };

  const handleSubmit = async e => {
    e.preventDefault();
    setErrors({});
    const coachId = localStorage.getItem('userId');
    if (!coachId) {
      setErrors({ general: '–ë—É–¥—å –ª–∞—Å–∫–∞, —É–≤—ñ–π–¥—ñ—Ç—å –ø–æ–≤—Ç–æ—Ä–Ω–æ.' });
      return;
    }
    if (!trainingData.date || !trainingData.start_time || !trainingData.end_time) {
      setErrors({ general: '–û–±–µ—Ä—ñ—Ç—å –¥–∞—Ç—É —Ç–∞ —á–∞—Å.' });
      return;
    }
    if (!trainingData.category || !trainingData.section) {
      setErrors({ general: '–û–±–µ—Ä—ñ—Ç—å –∫–∞—Ç–µ–≥–æ—Ä—ñ—é/—Å–µ–∫—Ü—ñ—é.' });
      return;
    }
    if (trainingData.start_time >= trainingData.end_time) {
      setErrors({ end_time: '–ß–∞—Å –∑–∞–≤–µ—Ä—à–µ–Ω–Ω—è –º–∞—î –±—É—Ç–∏ –ø—ñ–∑–Ω—ñ—à–µ.' });
      return;
    }
    const startDT = new Date(`${trainingData.date}T${trainingData.start_time}`);
    if (startDT < new Date()) {
      setErrors({ general: '–î–∞—Ç–∞/—á–∞—Å —É –º–∏–Ω—É–ª–æ–º—É.' });
      return;
    }
    const payload = {
      coach_id: coachId,
      start_time: `${trainingData.date}T${trainingData.start_time}`,
      end_time:   `${trainingData.date}T${trainingData.end_time}`,
      price:      parseFloat(trainingData.price),
      spots:      parseInt(trainingData.spots,10),
      currency:   trainingData.currency,
      category:   trainingData.category,
      section:    trainingData.section
    };
    try {
      await createTraining(payload);
      setModalMessage('–¢—Ä–µ–Ω—É–≤–∞–Ω–Ω—è —Å—Ç–≤–æ—Ä–µ–Ω–æ üëç');
      setShowModal(true);
      setTrainingData({
        date:'', start_time:'', end_time:'',
        price:'', spots:'', currency:'UAH',
        category:'', section:''
      });
      fetchTrainings();
    } catch (err) {
      setErrors({ general: err.message });
    }
  };

  const handleModalClose = () => setShowModal(false);

  // –î–µ—Ç–∞–ª—ñ —Ç–∞ —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
  const handleViewParticipants = async t => {
    setSelectedTraining(t);
    try {
      const { participants } = await getTrainingParticipants(t._id);
      setParticipants(participants);
      setShowParticipantsModal(true);
      setEditMode(false);
    } catch (err) { console.error(err); }
  };
  const closeParticipantsModal = () => {
    setShowParticipantsModal(false);
    setSelectedTraining(null);
    setParticipants([]);
    setEditMode(false);
  };

  const handleEdit = () => {
    const t = selectedTraining;
    const s = new Date(t.start_time), e = new Date(t.end_time), signed = t.participants?.length||0;
    setEditData({
      date:       s.toISOString().split('T')[0],
      start_time: s.toTimeString().slice(0,5),
      end_time:   e.toTimeString().slice(0,5),
      price:      t.price,
      spots:      signed + parseInt(t.spots,10),
      currency:   t.currency,
      category:   t.category,
      section:    t.section
    });
    setEditMode(true);
  };
  const handleEditChange = e => setEditData({...editData, [e.target.name]: e.target.value});

  const handleEditSubmit = async e => {
    e.preventDefault();
    setErrors({});
    if (!editData.date || !editData.start_time || !editData.end_time) {
      setErrors({ edit: '–û–±–µ—Ä—ñ—Ç—å –¥–∞—Ç—É —Ç–∞ —á–∞—Å.' });
      return;
    }
    if (editData.start_time >= editData.end_time) {
      setErrors({ edit: '–ß–∞—Å –∑–∞–≤–µ—Ä—à–µ–Ω–Ω—è –º–∞—î –±—É—Ç–∏ –ø—ñ–∑–Ω—ñ—à–µ.' });
      return;
    }
    const newStart = new Date(`${editData.date}T${editData.start_time}`);
    if (newStart < new Date()) {
      setErrors({ edit: '–ù–µ –º–æ–∂–Ω–∞ —É –º–∏–Ω—É–ª–æ–º—É.' });
      return;
    }
    const signed = selectedTraining.participants?.length||0;
    const total  = parseInt(editData.spots,10);
    const payload = {
      start_time: `${editData.date}T${editData.start_time}`,
      end_time:   `${editData.date}T${editData.end_time}`,
      price:      parseFloat(editData.price),
      currency:   editData.currency,
      category:   editData.category,
      section:    editData.section
    };
    if (total !== signed + parseInt(selectedTraining.spots,10)) {
      if (total < signed) {
        setErrors({ edit: '–ú—ñ—Å—Ü—å –º–µ–Ω—à–µ –∑–∞ –≤–∂–µ –∑–∞–ø–∏—Å–∞–Ω—ñ.' });
        return;
      }
      payload.spots = total - signed;
    }
    try {
      await updateTraining(selectedTraining._id, payload);
      setModalMessage('–¢—Ä–µ–Ω—É–≤–∞–Ω–Ω—è –æ–Ω–æ–≤–ª–µ–Ω–æ üëç');
      setShowModal(true);
      setEditMode(false);
      fetchTrainings();
      closeParticipantsModal();
    } catch (err) {
      setErrors({ edit: err.message });
    }
  };
  const cancelEdit = () => setEditMode(false);

  // –°–æ—Ä—Ç—É–≤–∞–Ω–Ω—è
  const sorted = [...trainings].sort(
    (a,b)=> new Date(a.start_time) - new Date(b.start_time)
  );

  return (
    <div className="dashboard-container">
      <h1>–ö–∞–±—ñ–Ω–µ—Ç –¢—Ä–µ–Ω–µ—Ä–∞</h1>

      {/* –§–û–†–ú–ê –°–¢–í–û–†–ï–ù–ù–Ø */}
      <div className="form-wrapper">
        <h2>–°—Ç–≤–æ—Ä–µ–Ω–Ω—è —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è</h2>
        <p>–ó–∞–ø–æ–≤–Ω—ñ—Ç—å —Ñ–æ—Ä–º—É, —â–æ–± –¥–æ–¥–∞—Ç–∏ –Ω–æ–≤–µ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è.</p>
        {errors.general && <p className="error-message">{errors.general}</p>}
        <form onSubmit={handleSubmit} className="create-training-form">
          <div className="form-row">
            <label htmlFor="date">–î–∞—Ç–∞ –ø—Ä–æ–≤–µ–¥–µ–Ω–Ω—è:</label>
            <input
              type="date"
              name="date"
              id="date"
              value={trainingData.date}
              onChange={handleChange}
              min={today}
              required
            />
          </div>
          <div className="form-row time-group">
            <div className="time-field">
              <label htmlFor="start_time">–ü–æ—á–∞—Ç–æ–∫:</label>
              <input
                type="time"
                name="start_time"
                id="start_time"
                value={trainingData.start_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
            </div>
            <div className="time-field">
              <label htmlFor="end_time">–ó–∞–≤–µ—Ä—à–µ–Ω–Ω—è:</label>
              <input
                type="time"
                name="end_time"
                id="end_time"
                value={trainingData.end_time}
                onChange={handleChange}
                min={getMinTime()}
                required
              />
              {errors.end_time && <p className="error-message">{errors.end_time}</p>}
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="category">–ö–∞—Ç–µ–≥–æ—Ä—ñ—è:</label>
            <select
              name="category"
              id="category"
              value={trainingData.category}
              onChange={handleCategoryChange}
              required
            >
              <option value="">-- –û–±–µ—Ä—ñ—Ç—å –∫–∞—Ç–µ–≥–æ—Ä—ñ—é --</option>
              {Object.keys(sportCategories).map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <div className="form-row">
            <label htmlFor="section">–°–µ–∫—Ü—ñ—è:</label>
            <select
              name="section"
              id="section"
              value={trainingData.section}
              onChange={handleChange}
              disabled={!trainingData.category}
              required
            >
              <option value="">-- –û–±–µ—Ä—ñ—Ç—å —Å–µ–∫—Ü—ñ—é --</option>
              {trainingData.category && sportCategories[trainingData.category].map(s => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
          </div>
          <div className="form-row price-currency-group">
            <div className="price-group">
              <label htmlFor="price">–¶—ñ–Ω–∞:</label>
              <input
                type="number"
                step="0.01"
                min="0"
                name="price"
                id="price"
                value={trainingData.price}
                onChange={handleChange}
                required
              />
              {errors.price && <p className="error-message">{errors.price}</p>}
            </div>
            <div className="currency-group">
              <label htmlFor="currency">–í–∞–ª—é—Ç–∞:</label>
              <select
                name="currency"
                id="currency"
                value={trainingData.currency}
                onChange={handleChange}
                required
              >
                <option value="UAH">UAH</option>
                <option value="EUR">EUR</option>
                <option value="USD">USD</option>
              </select>
            </div>
          </div>
          <div className="form-row">
            <label htmlFor="spots">–î–æ—Å—Ç—É–ø–Ω—ñ –º—ñ—Å—Ü—è:</label>
            <input
              type="number"
              min="0"
              name="spots"
              id="spots"
              value={trainingData.spots}
              onChange={handleChange}
              required
            />
            {errors.spots && <p className="error-message">{errors.spots}</p>}
          </div>
          <button type="submit" className="submit-btn">–°—Ç–≤–æ—Ä–∏—Ç–∏ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è</button>
        </form>
      </div>

      {/* –°–ï–ö–¶–Ü–Ø ‚Äú–í–ê–®–Ü –¢–†–ï–ù–£–í–ê–ù–ù–Ø‚Äù –£ –ö–ê–†–¢–û–ß–ö–ê–• */}
      <section className="registered-trainings-section">
        <h2>–í–∞—à—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è</h2>
        {sorted.length === 0
          ? <p>–ù–µ–º–∞—î —Å—Ç–≤–æ—Ä–µ–Ω–∏—Ö —Ç—Ä–µ–Ω—É–≤–∞–Ω—å.</p>
          : (
            <div className="cards-grid">
              {sorted.map(t => {
                const date = formatDate(t.start_time);
                const time = `${formatTime(t.start_time)} ‚Äì ${formatTime(t.end_time)}`;
                const logo = sportLogos[t.section];
                const count = t.participants?.length || 0;
                return (
                  <div key={t._id} className="training-card">
                    <div className="card-header">
                      <span className="card-date">{date}</span>
                      <span className="card-time">{time}</span>
                    </div>
                    <div className="card-body">
                      {logo && <img src={logo} alt={t.section} className="card-logo" />}
                      <p className="section-name">{t.section}</p>
                      <p className="participants-count">–£—á–∞—Å–Ω–∏–∫—ñ–≤: {count}</p>
                    </div>
                    <div className="card-footer">
                      <button
                        className="view-btn"
                        onClick={() => handleViewParticipants(t)}
                      >
                        –ü–µ—Ä–µ–≥–ª—è–Ω—É—Ç–∏
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )
        }
      </section>

      {/* –ú–û–î–ê–õ–¨–ù–Ü –í–Ü–ö–ù–ê */}
      {showParticipantsModal && selectedTraining && (
        <Modal
          modalType="info"
          onClose={closeParticipantsModal}
          message={
            editMode ? (
              <form onSubmit={handleEditSubmit} className="edit-training-form">
                {/* –†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è (–∞–Ω–∞–ª–æ–≥—ñ—á–Ω–æ —Ñ–æ—Ä–º—ñ —Å—Ç–≤–æ—Ä–µ–Ω–Ω—è) */}
                <div className="form-row">
                  <label htmlFor="editDate">–î–∞—Ç–∞:</label>
                  <input
                    type="date"
                    name="date"
                    id="editDate"
                    value={editData.date}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-row time-group">
                  <div className="time-field">
                    <label htmlFor="editStart">–ü–æ—á–∞—Ç–æ–∫:</label>
                    <input
                      type="time"
                      name="start_time"
                      id="editStart"
                      value={editData.start_time}
                      onChange={handleEditChange}
                      required
                    />
                  </div>
                  <div className="time-field">
                    <label htmlFor="editEnd">–ó–∞–≤–µ—Ä—à–µ–Ω–Ω—è:</label>
                    <input
                      type="time"
                      name="end_time"
                      id="editEnd"
                      value={editData.end_time}
                      onChange={handleEditChange}
                      required
                    />
                  </div>
                </div>
                <div className="form-row">
                  <label htmlFor="editPrice">–¶—ñ–Ω–∞:</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    name="price"
                    id="editPrice"
                    value={editData.price}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-row">
                  <label htmlFor="editSpots">–ó–∞–≥–∞–ª—å–Ω–∞ –º—ñ—Å—Ç–∫—ñ—Å—Ç—å:</label>
                  <input
                    type="number"
                    min="0"
                    name="spots"
                    id="editSpots"
                    value={editData.spots}
                    onChange={handleEditChange}
                    required
                  />
                </div>
                <div className="form-row">
                  <label htmlFor="editCurrency">–í–∞–ª—é—Ç–∞:</label>
                  <select
                    name="currency"
                    id="editCurrency"
                    value={editData.currency}
                    onChange={handleEditChange}
                    required
                  >
                    <option value="UAH">UAH</option>
                    <option value="EUR">EUR</option>
                    <option value="USD">USD</option>
                  </select>
                </div>
                <div className="form-row">
                  <label htmlFor="editCategory">–ö–∞—Ç–µ–≥–æ—Ä—ñ—è:</label>
                  <select
                    name="category"
                    id="editCategory"
                    value={editData.category}
                    onChange={handleEditChange}
                    required
                  >
                    <option value="">-- –û–±–µ—Ä—ñ—Ç—å –∫–∞—Ç–µ–≥–æ—Ä—ñ—é --</option>
                    {Object.keys(sportCategories).map(cat => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                </div>
                <div className="form-row">
                  <label htmlFor="editSection">–°–µ–∫—Ü—ñ—è:</label>
                  <select
                    name="section"
                    id="editSection"
                    value={editData.section}
                    onChange={handleEditChange}
                    disabled={!editData.category}
                    required
                  >
                    <option value="">-- –û–±–µ—Ä—ñ—Ç—å —Å–µ–∫—Ü—ñ—é --</option>
                    {editData.category && sportCategories[editData.category].map(s => (
                      <option key={s} value={s}>{s}</option>
                    ))}
                  </select>
                </div>
                {errors.edit && <p className="error-message">{errors.edit}</p>}
                <button type="button" className="submit-btn cancel-btn" onClick={cancelEdit}>
                  –í—ñ–¥–º—ñ–Ω–∏—Ç–∏
                </button>
                <button type="submit" className="submit-btn save-btn">
                  –ó–±–µ—Ä–µ–≥—Ç–∏
                </button>
              </form>
            ) : (
              <div>
                <h2>–î–µ—Ç–∞–ª—ñ —Ç—Ä–µ–Ω—É–≤–∞–Ω–Ω—è</h2>
                <p><strong>–°–µ–∫—Ü—ñ—è:</strong> {selectedTraining.section}</p>
                <p><strong>–î–∞—Ç–∞:</strong> {formatDate(selectedTraining.start_time)}</p>
                <p><strong>–ß–∞—Å:</strong> {formatTime(selectedTraining.start_time)} ‚Äì {formatTime(selectedTraining.end_time)}</p>
                <p><strong>–£—á–∞—Å–Ω–∏–∫—ñ–≤:</strong> {selectedTraining.participants?.length||0}</p>
                <p><strong>–ó–∞—Ä–æ–±–ª–µ–Ω–æ:</strong> {(selectedTraining.participants?.length||0)*selectedTraining.price} {selectedTraining.currency}</p>
                <hr />
                <h3>–°–ø–∏—Å–æ–∫ —É—á–∞—Å–Ω–∏–∫—ñ–≤:</h3>
                {participants.length === 0 ? <p>–ù—ñ—Ö—Ç–æ —â–µ –Ω–µ –∑–∞–ø–∏—Å–∞–≤—Å—è.</p> : (
                  <ul>
                    {participants.map((p,i) => <li key={i}>{p.name}, –≤—ñ–∫ {p.age}</li>)}
                  </ul>
                )}
                <button className="view-btn" onClick={handleEdit}>–†–µ–¥–∞–≥—É–≤–∞—Ç–∏</button>
              </div>
            )
          }
        />
      )}

      {showModal && <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />}
    </div>
  );
};

export default CoachDashboard;
