// client/src/components/Dashboards/AthleteDashboard.js

import React, { useState, useEffect } from 'react';
import {
  API_BASE_URL,
  getTrainings,
  getAthleteTrainings,
  getCoachProfile,
  getCoachReviews,       // ★
  submitReview,          // ★
  signupForTraining,
  cancelEnrollment
} from '../../services/api';
import Modal from '../../components/Common/Modal';
import '../../styles/AthleteDashboard.css';

// Логотипи секцій
import footballLogo         from '../../assets/logos/football.jpg';
import basketballLogo       from '../../assets/logos/basketball.jpg';
import badmintonLogo        from '../../assets/logos/badminton.jpg';
import volleyballLogo       from '../../assets/logos/volleyball.jpg';
import baseballLogo         from '../../assets/logos/baseball.jpg';
import rugbyLogo            from '../../assets/logos/rugby.jpg';
import hockeyLogo           from '../../assets/logos/hockey.jpg';
import karateLogo           from '../../assets/logos/karate.jpg';
import boxingLogo           from '../../assets/logos/boxing.jpg';
import wushuLogo            from '../../assets/logos/wushu.jpg';
import jiuJitsuLogo         from '../../assets/logos/jiu_jitsu.jpg';
import mmaLogo              from '../../assets/logos/mma.jpg';
import runningLogo          from '../../assets/logos/running.jpg';
import jumpingLogo          from '../../assets/logos/jumping.jpg';
import throwingLogo         from '../../assets/logos/throwing.jpg';
import swimmingLogo         from '../../assets/logos/swimming.jpg';
import sailingLogo          from '../../assets/logos/sailing.jpg';
import canoeingLogo         from '../../assets/logos/canoeing.jpg';
import skateboardingLogo    from '../../assets/logos/skateboarding.jpg';
import snowboardingLogo     from '../../assets/logos/snowboarding.jpg';
import climbingLogo         from '../../assets/logos/climbing.jpg';
import fitnessLogo          from '../../assets/logos/fitness.jpg';
import strengthTrainingLogo from '../../assets/logos/strength_training.jpg';
import yogaLogo             from '../../assets/logos/yoga.jpg';

// Мапа секція → логотип
const sportLogos = {
  "Футбол": footballLogo,
  "Баскетбол": basketballLogo,
  "Бадмінтон": badmintonLogo,
  "Волейбол": volleyballLogo,
  "Бейсбол": baseballLogo,
  "Регбі": rugbyLogo,
  "Хокей": hockeyLogo,
  "Карате": karateLogo,
  "Бокс": boxingLogo,
  "Ушу": wushuLogo,
  "Джиу-джитсу": jiuJitsuLogo,
  "ММА": mmaLogo,
  "Біг": runningLogo,
  "Стрибки": jumpingLogo,
  "Метання": throwingLogo,
  "Плавання": swimmingLogo,
  "Вітрильний спорт": sailingLogo,
  "Каноїзм": canoeingLogo,
  "Скейбординг": skateboardingLogo,
  "Сноубординг": snowboardingLogo,
  "Альпінізм": climbingLogo,
  "Фітнес": fitnessLogo,
  "Силові тренування": strengthTrainingLogo,
  "Йога": yogaLogo
};

const DEFAULT_AVATAR = `${API_BASE_URL}/avatars/default.png`;

const AthleteDashboard = () => {
  const [allTrainings, setAllTrainings]               = useState([]);
  const [registeredTrainings, setRegisteredTrainings] = useState([]);
  const [error, setError]                             = useState('');
  const [showModal, setShowModal]                     = useState(false);
  const [modalMessage, setModalMessage]               = useState('');
  const [showCancelModal, setShowCancelModal]         = useState(false);
  const [selectedCancelTraining, setSelectedCancelTraining] = useState(null);
  const [selectedTraining, setSelectedTraining]       = useState(null);

  // Для профілю тренера
  const [coachProfile, setCoachProfile] = useState(null);
  const [showCoachModal, setShowCoachModal] = useState(false);
  const [coachError, setCoachError] = useState('');

  // ★ Для відгуків ★
  const [reviews, setReviews]             = useState([]);
  const [averageRating, setAverageRating] = useState(null);
  const [reviewCount, setReviewCount]     = useState(0);
  const [newRating, setNewRating]         = useState(0);
  const [newComment, setNewComment]       = useState('');
  const [reviewError, setReviewError]     = useState('');
  const [reviewSuccess, setReviewSuccess] = useState('');

  const [filterCategory, setFilterCategory] = useState('');
  const [filterSection, setFilterSection]   = useState('');
  const [sortOption, setSortOption]         = useState("date_asc");

  useEffect(() => {
    fetchAllTrainings();
    fetchRegisteredTrainings();
  }, []);

  const fetchAllTrainings = async () => {
    try {
      const { trainings } = await getTrainings();
      setAllTrainings(trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const fetchRegisteredTrainings = async () => {
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setError("Будь ласка, увійдіть як спортсмен");
      return;
    }
    try {
      const { trainings } = await getAthleteTrainings(athleteId);
      setRegisteredTrainings(trainings);
    } catch (err) {
      setError(err.message);
    }
  };

  const formatDate = t =>
    new Date(t).toLocaleDateString('uk-UA',{day:'2-digit',month:'2-digit',year:'numeric'});
  const formatTime = t =>
    new Date(t).toLocaleTimeString([],{hour:'2-digit',minute:'2-digit'});

  // Фільтрація доступних
  const registeredIds = new Set(registeredTrainings.map(t => t._id));
  let available = allTrainings.filter(t => !registeredIds.has(t._id));
  if (filterCategory) available = available.filter(t => t.category === filterCategory);
  if (filterSection)  available = available.filter(t => t.section  === filterSection);
  available.sort((a,b)=>{
    switch(sortOption){
      case "date_asc":  return new Date(a.start_time) - new Date(b.start_time);
      case "date_desc": return new Date(b.start_time) - new Date(a.start_time);
      case "price_asc": return a.price - b.price;
      case "price_desc":return b.price - a.price;
      default: return 0;
    }
  });

  // Показати профіль тренера + відгуки
  const handleCoachClick = async coachId => {
    setCoachError('');
    try {
      const [{ profile }, reviewsData] = await Promise.all([
        getCoachProfile(coachId),
        getCoachReviews(coachId)
      ]);
      setCoachProfile(profile);
      setReviews(reviewsData.reviews);
      setAverageRating(reviewsData.average_rating);
      setReviewCount(reviewsData.count);
      setShowCoachModal(true);
      setNewRating(0);
      setNewComment('');
      setReviewError('');
      setReviewSuccess('');
    } catch (err) {
      setCoachError(err.message);
    }
  };

  const handleSignup = async id => {
    setError('');
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) { setError("Увійдіть як спортсмен"); return; }
    try {
      const res = await signupForTraining(id, athleteId);
      setModalMessage(res.message);
      setShowModal(true);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
    }
  };

  const openCancelModal = t => {
    setSelectedCancelTraining(t);
    setShowCancelModal(true);
  };
  const handleCancelEnrollment = async () => {
    const athleteId = localStorage.getItem('userId');
    try {
      await cancelEnrollment(selectedCancelTraining._id, athleteId);
      setModalMessage("Запис успішно скасовано.");
      setShowModal(true);
      setShowCancelModal(false);
      fetchAllTrainings();
      fetchRegisteredTrainings();
    } catch (err) {
      setError(err.message);
      setShowCancelModal(false);
    }
  };

  const handleViewDetails      = t => { setSelectedTraining(t); setShowModal(true); };
  const handleModalClose       = () => { setShowModal(false); setSelectedTraining(null); };
  const handleCancelModalClose = () => { setShowCancelModal(false); };

  const sortedRegistered = [...registeredTrainings].sort(
    (a,b)=> new Date(a.start_time) - new Date(b.start_time)
  );

  // ★ Відправка відгуку
  const handleReviewSubmit = async () => {
    setReviewError('');
    setReviewSuccess('');
    const athleteId = localStorage.getItem('userId');
    if (!athleteId) {
      setReviewError('Увійдіть, щоб залишити відгук');
      return;
    }
    if (newRating < 1) {
      setReviewError('Будь ласка, оберіть рейтинг');
      return;
    }
    try {
      await submitReview(coachProfile.user_id, athleteId, newRating, newComment);
      setReviewSuccess('Дякуємо за відгук!');
      const { reviews: revs, average_rating, count } =
        await getCoachReviews(coachProfile.user_id);
      setReviews(revs);
      setAverageRating(average_rating);
      setReviewCount(count);
      setNewRating(0);
      setNewComment('');
    } catch (err) {
      setReviewError(err.message);
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Кабінет Спортсмена</h1>
      {error && <p className="error-message">{error}</p>}

      {/* — Доступні тренування — */}
      <section className="available-trainings-section">
        <div className="filter-form">
          <div className="filter-row">
            <label>Категорія:</label>
            <select
              value={filterCategory}
              onChange={e => { setFilterCategory(e.target.value); setFilterSection(''); }}
            >
              <option value="">— Всі —</option>
              {Object.keys(sportCategories).map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
          <div className="filter-row">
            <label>Секція:</label>
            <select
              value={filterSection}
              disabled={!filterCategory}
              onChange={e => setFilterSection(e.target.value)}
            >
              <option value="">— Всі —</option>
              {filterCategory && sportCategories[filterCategory].map(s => (
                <option key={s} value={s}>{s}</option>
              ))}
            </select>
          </div>
          <div className="filter-row">
            <label>Сортування:</label>
            <select
              value={sortOption}
              onChange={e => setSortOption(e.target.value)}
            >
              <option value="date_asc">Дата ↑</option>
              <option value="date_desc">Дата ↓</option>
              <option value="price_asc">Ціна ↑</option>
              <option value="price_desc">Ціна ↓</option>
            </select>
          </div>
          <button
            className="reset-filter-btn"
            onClick={() => { setFilterCategory(''); setFilterSection(''); setSortOption("date_asc"); }}
          >
            Скинути
          </button>
        </div>

        <h2>Доступні тренування</h2>
        {available.length === 0 ? (
          <p>Немає доступних тренувань.</p>
        ) : (
          <div className="cards-grid">
            {available.map(t => {
              const date = formatDate(t.start_time);
              const time = `${formatTime(t.start_time)} – ${formatTime(t.end_time)}`;
              const logo = sportLogos[t.section];
              let avatarSrc = DEFAULT_AVATAR;
              if (t.avatar_url) {
                avatarSrc = t.avatar_url.startsWith('http')
                  ? t.avatar_url
                  : `${API_BASE_URL}${t.avatar_url}`;
              }
              return (
                <div key={t._id} className="training-card">
                  <div className="card-header">
                    <span className="card-date">{date}</span>
                    <span className="card-time">{time}</span>
                  </div>
                  <div className="card-body">
                    {logo && <img src={logo} alt={t.section} className="card-logo" />}
                    <p><strong>Секція:</strong> {t.section}</p>
                    <p className="trainer-line">
                      <strong>Тренер:</strong>{' '}
                      <img
                        src={avatarSrc}
                        alt="Avatar"
                        className="coach-avatar-small"
                        onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = DEFAULT_AVATAR; }}
                      />{' '}
                      <span
                        className="coach-name-link"
                        onClick={() => handleCoachClick(t.coach_id)}
                      >
                        {t.coach_name}
                      </span>
                    </p>
                    <p><strong>Вартість:</strong> {t.price} {t.currency}</p>
                    <p><strong>Місць:</strong> {t.spots}</p>
                  </div>
                  <div className="card-footer">
                    <button
                      className="signup-btn"
                      onClick={() => handleSignup(t._id)}
                      disabled={t.spots === 0}
                    >
                      Записатись
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* — Ваші тренування — */}
      <section className="registered-trainings-section">
        <h2>Ваші тренування</h2>
        {sortedRegistered.length === 0 ? (
          <p>Ви ще не записалися на тренування.</p>
        ) : (
          <div className="registered-cards-grid">
            {sortedRegistered.map(t => {
              const date = formatDate(t.start_time);
              const time = `${formatTime(t.start_time)} – ${formatTime(t.end_time)}`;
              const logo = sportLogos[t.section];
              let avatarSrc = DEFAULT_AVATAR;
              if (t.avatar_url) {
                avatarSrc = t.avatar_url.startsWith('http')
                  ? t.avatar_url
                  : `${API_BASE_URL}${t.avatar_url}`;
              }
              return (
                <div key={t._id} className="training-card registered-card new-design">
                  <div className="card-header">
                    <span className="card-date">{date}</span>
                    <span className="card-time">{time}</span>
                  </div>
                  <div className="card-body">
                    {logo && <img src={logo} alt={t.section} className="registered-card-logo" />}
                    <p className="registered-section-name">{t.section}</p>
                    <p className="registered-trainer-line">
                      із{' '}
                      <img
                        src={avatarSrc}
                        alt="Avatar"
                        className="coach-avatar-small"
                        onError={e => { e.currentTarget.onerror = null; e.currentTarget.src = DEFAULT_AVATAR; }}
                      />{' '}
                      <span
                        className="coach-name-link"
                        onClick={() => handleCoachClick(t.coach_id)}
                      >
                        {t.coach_name}
                      </span>
                    </p>
                  </div>
                  <div className="card-footer">
                    <button
                      className="action-btn view-btn"
                      onClick={() => handleViewDetails(t)}
                    >
                      Переглянути
                    </button>
                    <button
                      className="action-btn cancel-btn"
                      onClick={() => openCancelModal(t)}
                    >
                      Відмінити запис
                    </button>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </section>

      {/* Підтвердження скасування */}
      {showCancelModal && (
        <Modal
          modalType="confirm"
          onClose={handleCancelModalClose}
          message={
            <div className="cancel-modal">
              <p>Ви впевнені, що хочете відмінити запис?</p>
              <div className="cancel-modal-buttons">
                <button className="confirm-btn" onClick={handleCancelEnrollment}>Так</button>
                <button className="cancel-btn" onClick={handleCancelModalClose}>Ні</button>
              </div>
            </div>
          }
        />
      )}

      {/* Деталі тренування */}
      {showModal && selectedTraining && (
        <Modal
          modalType="info"
          onClose={handleModalClose}
          message={
            <div className="details-modal">
              <h3>Детальна інформація</h3>
              <p><strong>Дата:</strong> {formatDate(selectedTraining.start_time)}</p>
              <p><strong>Час:</strong> {`${formatTime(selectedTraining.start_time)} – ${formatTime(selectedTraining.end_time)}`}</p>
              <p><strong>Секція:</strong> {selectedTraining.section}</p>
              <p><strong>Вартість:</strong> {selectedTraining.price} {selectedTraining.currency}</p>
              <p><strong>Учасників:</strong> {selectedTraining.participants?.length || 0}</p>
              <p><strong>Тренер:</strong> {selectedTraining.coach_name}</p>
              <button onClick={handleModalClose}>Ок</button>
            </div>
          }
        />
      )}
      {showModal && !selectedTraining && (
        <Modal modalType="success" message={modalMessage} onClose={handleModalClose} />
      )}

      {/* Модалка профілю тренера з відгуками */}
      {showCoachModal && coachProfile && (
        <Modal
          modalType="info"
          onClose={() => setShowCoachModal(false)}
          message={
            <div className="coach-profile-modal">
              {/* Coach info */}
              <img
                src={coachProfile.avatar_url || DEFAULT_AVATAR}
                alt="Аватар тренера"
                onError={e=>{ e.currentTarget.src = DEFAULT_AVATAR; }}
                className="coach-avatar-large"
              />
              <p><strong>Ім'я:</strong> {coachProfile.name}</p>
              <p><strong>Вік:</strong> {coachProfile.age}</p>
              <p><strong>Email:</strong> {coachProfile.email}</p>
              <p><strong>Телефон:</strong> {coachProfile.phone || <em>Немає</em>}</p>
              <p><strong>Опис:</strong> {coachProfile.description || <em>Немає опису</em>}</p>
              <p>
                <strong>Улюблені види спорту:</strong>{' '}
                {coachProfile.favorite_sports.length
                  ? coachProfile.favorite_sports.join(', ')
                  : <em>Немає</em>}
              </p>

              <hr/>

              {/* Reviews summary */}
              <div className="reviews-summary">
                <strong>Середній рейтинг:</strong>{' '}
                {averageRating !== null ? `${averageRating} / 5` : '—'}{' '}
                ({reviewCount} відгук{reviewCount === 1 ? '' : 'ів'})
              </div>

              {/* List of existing reviews */}
              <div className="reviews-list">
                {reviews.length === 0
                  ? <p>Поки немає відгуків.</p>
                  : reviews.map((r,i) => (
                      <div key={i} className="review-item">
                        <div className="review-header">
                          <span className="review-rating">
                            {'★'.repeat(r.rating)}{'☆'.repeat(5 - r.rating)}
                          </span>
                          <span className="review-date">
                            {new Date(r.created_at).toLocaleDateString('uk-UA')}
                          </span>
                        </div>
                        {r.comment && <p className="review-comment">{r.comment}</p>}
                      </div>
                    ))
                }
              </div>

              <hr/>

              {/* Submit your own review */}
              <div className="submit-review">
                <strong>Ваш відгук:</strong>
                <div className="star-input">
                  {[1,2,3,4,5].map(n => (
                    <span
                      key={n}
                      className={`star ${newRating >= n ? 'filled' : ''}`}
                      onClick={() => setNewRating(n)}
                    >
                      ★
                    </span>
                  ))}
                </div>
                <textarea
                  className="review-textarea"
                  value={newComment}
                  onChange={e => setNewComment(e.target.value)}
                  placeholder="Ваш коментар..."
                />
                {reviewError && <p className="error-message">{reviewError}</p>}
                {reviewSuccess && <p className="success-message">{reviewSuccess}</p>}
                <button className="submit-review-btn" onClick={handleReviewSubmit}>
                  Відправити
                </button>
              </div>
            </div>
          }
        />
      )}
      {coachError && <p className="error-message">{coachError}</p>}
    </div>
  );
};

export default AthleteDashboard;
